import React, { useState, useEffect } from 'react';
import { adminService } from '../../services/api';
import './AdminPanel.css';

const ManageInstitutes = () => {
  const [institutes, setInstitutes] = useState([]);
  const [loading, setLoading] = useState(true);
  const [message, setMessage] = useState('');

  useEffect(() => {
    fetchInstitutes();
  }, []);

  const fetchInstitutes = async () => {
    try {
      const response = await adminService.getInstitutes();
      setInstitutes(response.data);
      setLoading(false);
    } catch (error) {
      console.error('Error fetching institutes:', error);
      setLoading(false);
    }
  };

  const handleVerification = async (instituteId, verified) => {
    try {
      await adminService.updateInstituteVerification(instituteId, verified);
      setMessage(`Institute ${verified ? 'approved' : 'rejected'} successfully!`);
      fetchInstitutes();
    } catch (error) {
      setMessage('Error updating institute status.');
    }
  };

  const handleActivation = async (instituteId, isActive) => {
    try {
      await adminService.updateInstituteStatus(instituteId, isActive);
      setMessage(`Institute ${isActive ? 'activated' : 'deactivated'} successfully!`);
      fetchInstitutes();
    } catch (error) {
      setMessage('Error updating institute status.');
    }
  };

  if (loading) {
    return <div className="loading">Loading institutes...</div>;
  }

  return (
    <div className="manage-institutes">
      <div className="admin-section">
        <h2>Manage Institutes</h2>
        
        {message && (
          <div className={`message ${message.includes('Error') ? 'error' : 'success'}`}>
            {message}
          </div>
        )}

        <div className="institutes-list">
          {institutes.map(institute => (
            <div key={institute._id} className="institute-card">
              <div className="institute-header">
                <div className="institute-basic">
                  {institute.logo && (
                    <img 
                      src={`http://localhost:5000/${institute.logo}`} 
                      alt={institute.name}
                      className="institute-logo"
                    />
                  )}
                  <div>
                    <h3>{institute.name}</h3>
                    <p className="institute-category">{institute.category} ‚Ä¢ {institute.affiliation}</p>
                    <p className="institute-location">{institute.address?.city}, {institute.address?.state}</p>
                  </div>
                </div>
                
                <div className="status-badges">
                  <span className={`status ${institute.verified ? 'verified' : 'pending'}`}>
                    {institute.verified ? 'Verified' : 'Pending'}
                  </span>
                  <span className={`status ${institute.isActive ? 'active' : 'inactive'}`}>
                    {institute.isActive ? 'Active' : 'Inactive'}
                  </span>
                </div>
              </div>

              <div className="institute-details">
                <p className="description">{institute.description}</p>
                
                <div className="contact-info">
                  <span>üìß {institute.contact?.email}</span>
                  <span>üìû {institute.contact?.phone}</span>
                  {institute.contact?.website && (
                    <span>üåê {institute.contact.website}</span>
                  )}
                </div>

                <div className="facilities">
                  <strong>Facilities:</strong>
                  <div className="facilities-list">
                    {institute.facilities?.map((facility, index) => (
                      <span key={index} className="facility-tag">{facility}</span>
                    ))}
                  </div>
                </div>

                <div className="stats">
                  <span>Courses: {institute.courses?.length || 0}</span>
                  <span>Rating: ‚≠ê {institute.rating?.average || 0}/5 ({institute.rating?.count || 0} reviews)</span>
                </div>
              </div>

              <div className="institute-actions">
                {!institute.verified && (
                  <>
                    <button 
                      onClick={() => handleVerification(institute._id, true)}
                      className="btn-approve"
                    >
                      Approve
                    </button>
                    <button 
                      onClick={() => handleVerification(institute._id, false)}
                      className="btn-reject"
                    >
                      Reject
                    </button>
                  </>
                )}
                
                <button 
                  onClick={() => handleActivation(institute._id, !institute.isActive)}
                  className={institute.isActive ? 'btn-deactivate' : 'btn-activate'}
                >
                  {institute.isActive ? 'Deactivate' : 'Activate'}
                </button>
              </div>
            </div>
          ))}
        </div>

        {institutes.length === 0 && (
          <div className="no-data">
            <p>No institutes found.</p>
          </div>
        )}
      </div>
    </div>
  );
};

export default ManageInstitutes;
