import React, { useState, useEffect } from 'react';
import { facilityService } from '../../services/facility';

const FacilitiesManagement = () => {
  const [facilities, setFacilities] = useState([]);
  const [showForm, setShowForm] = useState(false);
  const [editingFacility, setEditingFacility] = useState(null);
  const [formData, setFormData] = useState({
    name: '',
    description: ''
  });
  const [loading, setLoading] = useState(false);
  const [message, setMessage] = useState('');

  useEffect(() => {
    fetchFacilities();
  }, []);

  const fetchFacilities = async () => {
    try {
      const response = await facilityService.getFacilities();
      setFacilities(response.facilities || []);
    } catch (error) {
      console.error('Error fetching facilities:', error);
      setMessage('Error loading facilities');
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!formData.name.trim()) {
      setMessage('Facility name is required');
      return;
    }

    setLoading(true);
    setMessage('');

    try {
      if (editingFacility) {
        // Update existing facility
        await facilityService.updateFacility(editingFacility._id, formData);
        setMessage('Facility updated successfully!');
      } else {
        // Add new facility
        await facilityService.addFacility(formData);
        setMessage('Facility added successfully!');
      }

      setFormData({ name: '', description: '' });
      setShowForm(false);
      setEditingFacility(null);
      fetchFacilities(); // Refresh the list
      
      setTimeout(() => setMessage(''), 3000);
    } catch (error) {
      console.error('Error saving facility:', error);
      setMessage(error.response?.data?.message || 'Error saving facility');
    } finally {
      setLoading(false);
    }
  };

  const handleEdit = (facility) => {
    setEditingFacility(facility);
    setFormData({
      name: facility.name,
      description: facility.description || ''
    });
    setShowForm(true);
  };

  const handleRemove = async (facilityId) => {
    if (window.confirm('Are you sure you want to remove this facility?')) {
      try {
        await facilityService.removeFacility(facilityId);
        setMessage('Facility removed successfully!');
        fetchFacilities(); // Refresh the list
        setTimeout(() => setMessage(''), 3000);
      } catch (error) {
        console.error('Error removing facility:', error);
        setMessage(error.response?.data?.message || 'Error removing facility');
      }
    }
  };

  const resetForm = () => {
    setFormData({ name: '', description: '' });
    setShowForm(false);
    setEditingFacility(null);
    setMessage('');
  };

  return (
    <div className="facilities-management">
      <div className="page-header">
        <h2>Facilities Management</h2>
        <button 
          onClick={() => setShowForm(true)}
          className="btn btn-primary"
        >
          + Add New Facility
        </button>
      </div>

      {message && (
        <div className={`message ${message.includes('Error') ? 'error' : 'success'}`}>
          {message}
        </div>
      )}

      {/* Add/Edit Facility Form */}
      {showForm && (
        <div className="form-section">
          <h3>{editingFacility ? 'Edit Facility' : 'Add New Facility'}</h3>
          <form onSubmit={handleSubmit} className="facility-form">
            <div className="form-group">
              <label>Facility Name *</label>
              <input
                type="text"
                value={formData.name}
                onChange={(e) => setFormData(prev => ({ ...prev, name: e.target.value }))}
                placeholder="e.g., Library, Sports Complex, Laboratory"
                required
              />
            </div>

            <div className="form-group">
              <label>Description</label>
              <textarea
                value={formData.description}
                onChange={(e) => setFormData(prev => ({ ...prev, description: e.target.value }))}
                placeholder="Describe the facility and its features..."
                rows="3"
              />
            </div>

            <div className="form-actions">
              <button 
                type="button"
                onClick={resetForm}
                className="btn btn-outline"
              >
                Cancel
              </button>
              <button 
                type="submit" 
                className="btn btn-primary"
                disabled={loading || !formData.name.trim()}
              >
                {loading ? 'Saving...' : (editingFacility ? 'Update Facility' : 'Add Facility')}
              </button>
            </div>
          </form>
        </div>
      )}

      {/* Facilities List */}
      <div className="facilities-list">
        <h3>Current Facilities ({facilities.length})</h3>
        
        {facilities.length === 0 ? (
          <div className="empty-state">
            <p>No facilities added yet. Add your first facility to showcase what your institute offers.</p>
          </div>
        ) : (
          <div className="facilities-grid">
            {facilities.map((facility) => (
              <div key={facility._id} className="facility-card">
                <div className="facility-content">
                  <h4>{facility.name}</h4>
                  {facility.description && (
                    <p>{facility.description}</p>
                  )}
                </div>
                <div className="facility-actions">
                  <button 
                    onClick={() => handleEdit(facility)}
                    className="btn btn-outline btn-sm"
                  >
                    Edit
                  </button>
                  <button 
                    onClick={() => handleRemove(facility._id)}
                    className="btn btn-danger btn-sm"
                  >
                    Remove
                  </button>
                </div>
              </div>
            ))}
          </div>
        )}
      </div>
    </div>
  );
};

export default FacilitiesManagement;
