import React from 'react';
import { Link, useLocation, useNavigate } from 'react-router-dom';
import { useAuth } from '../../context/AuthContext';

const AdminSidebar = () => {
  const location = useLocation();
  const navigate = useNavigate();
  const { logout } = useAuth();

  const menuItems = [
    { path: '/admin', icon: 'üìä', label: 'Dashboard' },
    { path: '/admin/institutes', icon: 'üè´', label: 'Manage Institutes' },
    { path: '/admin/users', icon: 'üë•', label: 'Manage Users' },
    { path: '/admin/analytics', icon: 'üìà', label: 'Analytics' },
  ];

  const handleLogout = () => {
    logout();
    navigate('/');
  };

  return (
    <div className="sidebar">
      <div className="sidebar-header">
        <div className="admin-logo">
          <div className="logo-placeholder">‚öôÔ∏è</div>
        </div>
        <h3>Admin Panel</h3>
        <p className="admin-role">Super Administrator</p>
      </div>

      <nav className="sidebar-nav">
        <ul className="sidebar-menu">
          {menuItems.map((item) => (
            <li key={item.path} className="sidebar-item">
              <Link
                to={item.path}
                className={`sidebar-link ${
                  location.pathname === item.path ? 'active' : ''
                }`}
              >
                <span className="sidebar-icon">{item.icon}</span>
                <span className="sidebar-label">{item.label}</span>
              </Link>
            </li>
          ))}
        </ul>
      </nav>

      <div className="sidebar-footer">
        <button onClick={handleLogout} className="btn btn-danger btn-block">
          üö™ Logout
        </button>
      </div>
    </div>
  );
};

export default AdminSidebar;
