import React, { useState, useEffect } from 'react';
import { useAuth } from '../../context/AuthContext';
import { useApp } from '../../context/AppContext';
import { enquiryAPI } from '../../services/api';
import InstituteSidebar from './InstituteSidebar';
import './InstituteDashboard.css';

const Enquiries = () => {
  const { user } = useAuth();
  const { addAlert } = useApp();
  const [enquiries, setEnquiries] = useState([]);
  const [filteredEnquiries, setFilteredEnquiries] = useState([]);
  const [loading, setLoading] = useState(true);
  const [statusFilter, setStatusFilter] = useState('all');
  const [selectedEnquiry, setSelectedEnquiry] = useState(null);
  const [showResponseModal, setShowResponseModal] = useState(false);
  const [responseText, setResponseText] = useState('');

  useEffect(() => {
    fetchEnquiries();
  }, [user]);

  useEffect(() => {
    filterEnquiries();
  }, [enquiries, statusFilter]);

  const fetchEnquiries = async () => {
    try {
      const response = await enquiryAPI.getByInstitute(user.instituteId);
      setEnquiries(response.data || []);
    } catch (error) {
      addAlert({
        type: 'error',
        message: 'Failed to load enquiries'
      });
    } finally {
      setLoading(false);
    }
  };

  const filterEnquiries = () => {
    let filtered = enquiries;
    
    if (statusFilter !== 'all') {
      filtered = filtered.filter(enquiry => enquiry.status === statusFilter);
    }
    
    setFilteredEnquiries(filtered);
  };

  const handleStatusUpdate = async (enquiryId, newStatus) => {
    try {
      await enquiryAPI.updateStatus(enquiryId, newStatus);
      
      addAlert({
        type: 'success',
        message: `Enquiry marked as ${newStatus}`
      });

      setEnquiries(prev => prev.map(enquiry =>
        enquiry._id === enquiryId ? { ...enquiry, status: newStatus } : enquiry
      ));

    } catch (error) {
      addAlert({
        type: 'error',
        message: 'Failed to update enquiry status'
      });
    }
  };

  const handleRespond = (enquiry) => {
    setSelectedEnquiry(enquiry);
    setShowResponseModal(true);
    setResponseText('');
  };

  const handleSendResponse = async () => {
    if (!responseText.trim()) {
      alert('Please enter a response message');
      return;
    }

    try {
      // This would typically be an API call to send response
      // For now, we'll simulate it
      addAlert({
        type: 'success',
        message: 'Response sent successfully'
      });

      // Update enquiry status to responded
      await handleStatusUpdate(selectedEnquiry._id, 'responded');
      
      setShowResponseModal(false);
      setSelectedEnquiry(null);
      setResponseText('');

    } catch (error) {
      addAlert({
        type: 'error',
        message: 'Failed to send response'
      });
    }
  };

  const getStatusBadge = (status) => {
    const statusConfig = {
      pending: { class: 'warning', label: 'Pending' },
      responded: { class: 'success', label: 'Responded' },
      closed: { class: 'secondary', label: 'Closed' }
    };

    const config = statusConfig[status] || { class: 'secondary', label: status };
    return <span className={`status-badge ${config.class}`}>{config.label}</span>;
  };

  if (loading) {
    return (
      <div className="dashboard-container">
        <InstituteSidebar />
        <div className="dashboard-content">
          <div className="loading">Loading enquiries...</div>
        </div>
      </div>
    );
  }

  return (
    <div className="dashboard-container">
      <InstituteSidebar />
      <div className="dashboard-content">
        <div className="page-header">
          <h1>Student Enquiries</h1>
          <div className="header-stats">
            <span className="stat-item">
              Total: <strong>{enquiries.length}</strong>
            </span>
            <span className="stat-item">
              Pending: <strong>{enquiries.filter(e => e.status === 'pending').length}</strong>
            </span>
            <span className="stat-item">
              Responded: <strong>{enquiries.filter(e => e.status === 'responded').length}</strong>
            </span>
          </div>
        </div>

        {/* Filters */}
        <div className="filters-card">
          <div className="filter-group">
            <select
              value={statusFilter}
              onChange={(e) => setStatusFilter(e.target.value)}
              className="filter-select"
            >
              <option value="all">All Status</option>
              <option value="pending">Pending</option>
              <option value="responded">Responded</option>
              <option value="closed">Closed</option>
            </select>
          </div>
        </div>

        {/* Enquiries List */}
        <div className="enquiries-list">
          {filteredEnquiries.length > 0 ? (
            filteredEnquiries.map(enquiry => (
              <div key={enquiry._id} className="enquiry-card">
                <div className="enquiry-header">
                  <div className="enquiry-info">
                    <h3>{enquiry.name}</h3>
                    <p className="enquiry-email">{enquiry.email}</p>
                    <p className="enquiry-phone">{enquiry.phone || 'No phone provided'}</p>
                    {enquiry.courseInterest && (
                      <p className="enquiry-course">
                        Interested in: <strong>{enquiry.courseInterest}</strong>
                      </p>
                    )}
                  </div>
                  <div className="enquiry-meta">
                    {getStatusBadge(enquiry.status)}
                    <span className="enquiry-date">
                      {new Date(enquiry.createdAt).toLocaleDateString()} at{' '}
                      {new Date(enquiry.createdAt).toLocaleTimeString()}
                    </span>
                  </div>
                </div>

                <div className="enquiry-content">
                  <p>{enquiry.message}</p>
                </div>

                <div className="enquiry-actions">
                  {enquiry.status === 'pending' && (
                    <button
                      onClick={() => handleRespond(enquiry)}
                      className="btn btn-primary btn-sm"
                    >
                      <i className="fas fa-reply"></i>
                      Respond
                    </button>
                  )}
                  
                  {enquiry.status === 'pending' && (
                    <button
                      onClick={() => handleStatusUpdate(enquiry._id, 'closed')}
                      className="btn btn-outline btn-sm"
                    >
                      <i className="fas fa-times"></i>
                      Close
                    </button>
                  )}

                  {enquiry.status === 'responded' && (
                    <button
                      onClick={() => handleStatusUpdate(enquiry._id, 'closed')}
                      className="btn btn-outline btn-sm"
                    >
                      <i className="fas fa-check"></i>
                      Mark Closed
                    </button>
                  )}

                  {enquiry.status === 'closed' && (
                    <button
                      onClick={() => handleStatusUpdate(enquiry._id, 'pending')}
                      className="btn btn-outline btn-sm"
                    >
                      <i className="fas fa-redo"></i>
                      Reopen
                    </button>
                  )}
                </div>
              </div>
            ))
          ) : (
            <div className="no-data-card">
              <i className="fas fa-envelope"></i>
              <h3>No Enquiries Found</h3>
              <p>
                {statusFilter !== 'all' 
                  ? `No ${statusFilter} enquiries found.`
                  : 'You haven\'t received any enquiries yet.'
                }
              </p>
            </div>
          )}
        </div>

        {/* Response Modal */}
        {showResponseModal && selectedEnquiry && (
          <div className="modal-overlay">
            <div className="modal-content">
              <div className="modal-header">
                <h3>Respond to Enquiry</h3>
                <button 
                  onClick={() => setShowResponseModal(false)}
                  className="btn btn-close"
                >
                  <i className="fas fa-times"></i>
                </button>
              </div>
              
              <div className="modal-body">
                <div className="enquiry-details">
                  <h4>From: {selectedEnquiry.name}</h4>
                  <p><strong>Email:</strong> {selectedEnquiry.email}</p>
                  {selectedEnquiry.phone && (
                    <p><strong>Phone:</strong> {selectedEnquiry.phone}</p>
                  )}
                  {selectedEnquiry.courseInterest && (
                    <p><strong>Course Interest:</strong> {selectedEnquiry.courseInterest}</p>
                  )}
                  <div className="enquiry-message">
                    <strong>Original Message:</strong>
                    <p>{selectedEnquiry.message}</p>
                  </div>
                </div>

                <div className="form-group">
                  <label className="form-label">Your Response</label>
                  <textarea
                    value={responseText}
                    onChange={(e) => setResponseText(e.target.value)}
                    className="form-control"
                    rows="6"
                    placeholder="Type your response to the student/parent..."
                  />
                </div>
              </div>

              <div className="modal-footer">
                <div className="action-buttons">
                  <button
                    onClick={() => setShowResponseModal(false)}
                    className="btn btn-outline"
                  >
                    Cancel
                  </button>
                  <button
                    onClick={handleSendResponse}
                    className="btn btn-primary"
                  >
                    <i className="fas fa-paper-plane"></i>
                    Send Response
                  </button>
                </div>
              </div>
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default Enquiries;
