import React, { useState, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { useAuth } from '../../context/AuthContext';
import { authAPI, healthCheck } from '../../services/api';
import './Auth.css';

const Login = () => {
  const [formData, setFormData] = useState({
    email: '',
    password: ''
  });
  const [errors, setErrors] = useState({});
  const [loading, setLoading] = useState(false);
  const [apiStatus, setApiStatus] = useState('checking');
  const { login } = useAuth();
  const navigate = useNavigate();

  useEffect(() => {
    const checkApiHealth = async () => {
      try {
        await healthCheck();
        setApiStatus('connected');
      } catch (error) {
        setApiStatus('disconnected');
        console.error('API health check failed:', error);
      }
    };
    
    checkApiHealth();
  }, []);

  const validateForm = () => {
    const newErrors = {};
    
    if (!formData.email.trim()) {
      newErrors.email = 'Email is required';
    } else if (!/\S+@\S+\.\S+/.test(formData.email)) {
      newErrors.email = 'Please enter a valid email address';
    }
    
    if (!formData.password) {
      newErrors.password = 'Password is required';
    }
    
    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
    
    if (errors[name]) {
      setErrors(prev => ({
        ...prev,
        [name]: ''
      }));
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    
    if (!validateForm()) return;
    
    setLoading(true);
    setErrors({});
    
    try {
      console.log('üîê Attempting login with:', formData.email);
      const response = await authAPI.login(formData);
      const { token, user } = response.data;
      
      console.log('‚úÖ Login successful for:', user.email);
      login(token, user);
      
      // Redirect based on role
      if (user.role === 'admin') {
        navigate('/admin/dashboard');
      } else if (user.role === 'institute') {
        navigate('/institute/dashboard');
      } else {
        navigate('/user/dashboard');
      }
    } catch (error) {
      console.error('‚ùå Login failed:', error);
      
      let errorMessage = error.message || 'Login failed';
      
      // Provide more specific error messages
      if (errorMessage.includes('Invalid email or password')) {
        errorMessage = 'Invalid email or password. Please check your credentials.';
      } else if (errorMessage.includes('pending admin approval')) {
        errorMessage = 'Your institute is pending admin approval. Please wait for approval.';
      }
      
      setErrors({ submit: errorMessage });
    } finally {
      setLoading(false);
    }
  };

  const fillTestCredentials = (type) => {
    const credentials = {
      admin: { email: 'admin@edulist.com', password: 'admin123' },
      institute: { email: 'rlsclg@gmail.com', password: 'password123' },
      user: { email: 'rahul@example.com', password: 'password123' }
    };
    
    setFormData(credentials[type]);
    setErrors({}); // Clear errors when filling test credentials
  };

  return (
    <div className="auth-container">
      <div className="auth-card">
        <h2>Login to EduList</h2>
        
        {/* API Status Indicator */}
        <div className={`api-status ${apiStatus}`}>
          {apiStatus === 'checking' && 'Checking server connection...'}
          {apiStatus === 'connected' && '‚úÖ Connected to server'}
          {apiStatus === 'disconnected' && '‚ùå Cannot connect to server'}
        </div>
        
        {errors.submit && (
          <div className="error-message">
            {errors.submit}
          </div>
        )}
        
        <form onSubmit={handleSubmit} className="auth-form">
          <div className="form-group">
            <label htmlFor="email">Email Address *</label>
            <input
              type="email"
              id="email"
              name="email"
              value={formData.email}
              onChange={handleChange}
              className={errors.email ? 'error' : ''}
              placeholder="Enter your email"
              disabled={loading}
            />
            {errors.email && <span className="error-text">{errors.email}</span>}
          </div>
          
          <div className="form-group">
            <label htmlFor="password">Password *</label>
            <input
              type="password"
              id="password"
              name="password"
              value={formData.password}
              onChange={handleChange}
              className={errors.password ? 'error' : ''}
              placeholder="Enter your password"
              disabled={loading}
            />
            {errors.password && <span className="error-text">{errors.password}</span>}
          </div>
          
          <button 
            type="submit" 
            className="auth-button"
            disabled={loading || apiStatus === 'disconnected'}
          >
            {loading ? (
              <>
                <span className="loading-spinner"></span>
                Logging in...
              </>
            ) : (
              'Login'
            )}
          </button>
        </form>

        {/* Test Credentials */}
        <div className="test-credentials">
          <h4>Quick Test Login:</h4>
          <div className="test-buttons">
            <button 
              type="button" 
              className="btn-test-admin"
              onClick={() => fillTestCredentials('admin')}
              disabled={loading}
            >
              Admin Account
            </button>
            <button 
              type="button" 
              className="btn-test-institute"
              onClick={() => fillTestCredentials('institute')}
              disabled={loading}
            >
              Institute Account
            </button>
            <button 
              type="button" 
              className="btn-test-user"
              onClick={() => fillTestCredentials('user')}
              disabled={loading}
            >
              User Account
            </button>
          </div>
          <div className="test-info">
            <p><strong>Admin:</strong> admin@edulist.com / admin123</p>
            <p><strong>Institute:</strong> rlsclg@gmail.com / password123</p>
            <p><strong>User:</strong> rahul@example.com / password123</p>
          </div>
        </div>
        
        <p className="auth-link">
          Don't have an account? <Link to="/register">Register here</Link>
        </p>
      </div>
    </div>
  );
};

export default Login;
