import React, { useState, useEffect } from 'react';
import { Routes, Route, useNavigate, useLocation } from 'react-router-dom';
import { useAuth } from '../../context/AuthContext';
import InstituteSidebar from './InstituteSidebar';
import ProfileManagement from './ProfileManagement';
import CourseManagement from './CourseManagement';
import FacilitiesManagement from './FacilitiesManagement';
import Enquiries from './Enquiries';
import Reviews from './Reviews';
import DashboardHome from './DashboardHome';
import './InstituteDashboard.css';

const InstituteDashboard = () => {
  const { user, logout } = useAuth();
  const navigate = useNavigate();
  const location = useLocation();
  const [sidebarOpen, setSidebarOpen] = useState(false);
  const [redirected, setRedirected] = useState(false);

  useEffect(() => {
    // Only redirect once when component mounts or path changes
    if (!redirected && (location.pathname === '/institute/dashboard' || location.pathname === '/institute/dashboard/')) {
      setRedirected(true);
      navigate('/institute/dashboard/home', { replace: true });
    }
  }, [location, navigate, redirected]);

  const handleLogout = () => {
    logout();
    navigate('/');
  };

  return (
    <div className="dashboard-container">
      <InstituteSidebar 
        isOpen={sidebarOpen} 
        onClose={() => setSidebarOpen(false)}
        onLogout={handleLogout}
      />
      
      <div className="dashboard-main">
        <div className="dashboard-header">
          <button 
            className="menu-toggle"
            onClick={() => setSidebarOpen(true)}
          >
            â˜°
          </button>
          <h1>Institute Dashboard</h1>
          <div className="user-info">
            <span>Welcome, {user?.name}</span>
            <button onClick={handleLogout} className="logout-btn">
              Logout
            </button>
          </div>
        </div>

        <div className="dashboard-content">
          <Routes>
            <Route path="home/*" element={<DashboardHome />} />
            <Route path="profile/*" element={<ProfileManagement />} />
            <Route path="courses/*" element={<CourseManagement />} />
            <Route path="facilities/*" element={<FacilitiesManagement />} />
            <Route path="enquiries/*" element={<Enquiries />} />
            <Route path="reviews/*" element={<Reviews />} />
            <Route path="*" element={<Navigate to="home" replace />} />
          </Routes>
        </div>
      </div>
    </div>
  );
};

export default InstituteDashboard;
