import React from 'react';
import { Link, useLocation } from 'react-router-dom';

const InstituteSidebar = ({ isOpen, onClose, onLogout }) => {
  const location = useLocation();

  const menuItems = [
    { path: '/institute/dashboard/profile', icon: 'üè´', label: 'Profile' },
    { path: '/institute/dashboard/courses', icon: 'üìö', label: 'Courses' },
    { path: '/institute/dashboard/facilities', icon: 'üè¢', label: 'Facilities' },
    { path: '/institute/dashboard/enquiries', icon: 'üìß', label: 'Enquiries' },
    { path: '/institute/dashboard/reviews', icon: '‚≠ê', label: 'Reviews' },
  ];

  return (
    <>
      {/* Overlay for mobile */}
      {isOpen && <div className="sidebar-overlay" onClick={onClose}></div>}
      
      <div className={`institute-sidebar ${isOpen ? 'open' : ''}`}>
        <div className="sidebar-header">
          <h3>Institute Panel</h3>
          <button className="close-sidebar" onClick={onClose}>√ó</button>
        </div>
        
        <nav className="sidebar-nav">
          {menuItems.map(item => (
            <Link
              key={item.path}
              to={item.path}
              className={`nav-item ${location.pathname === item.path ? 'active' : ''}`}
              onClick={onClose}
            >
              <span className="nav-icon">{item.icon}</span>
              <span className="nav-label">{item.label}</span>
            </Link>
          ))}
        </nav>
        
        <div className="sidebar-footer">
          <button onClick={onLogout} className="logout-btn">
            <span>üö™</span>
            <span>Logout</span>
          </button>
        </div>
      </div>
    </>
  );
};

export default InstituteSidebar;
