import React, { useState, useEffect } from 'react';
import './AdminPanel.css';

const ManageUsers = () => {
  const [users, setUsers] = useState([]);
  const [loading, setLoading] = useState(true);
  const [searchTerm, setSearchTerm] = useState('');

  useEffect(() => {
    fetchUsers();
  }, []);

  const fetchUsers = async () => {
    try {
      // Mock data - in real app, fetch from API
      const mockUsers = [
        {
          _id: '1',
          name: 'Rahul Sharma',
          email: 'rahul@example.com',
          phone: '9876543210',
          role: 'user',
          isActive: true,
          createdAt: new Date('2024-01-01')
        },
        {
          _id: '2',
          name: 'Priya Patel',
          email: 'priya@example.com',
          phone: '9876543211',
          role: 'user',
          isActive: true,
          createdAt: new Date('2024-01-02')
        },
        {
          _id: '3',
          name: 'Delhi Public School',
          email: 'dps@example.com',
          phone: '9876543212',
          role: 'institute',
          isActive: true,
          createdAt: new Date('2024-01-03')
        },
        {
          _id: '4',
          name: 'Amit Kumar',
          email: 'amit@example.com',
          phone: '9876543213',
          role: 'user',
          isActive: false,
          createdAt: new Date('2024-01-04')
        }
      ];
      setUsers(mockUsers);
    } catch (error) {
      console.error('Error fetching users:', error);
    } finally {
      setLoading(false);
    }
  };

  const handleToggleStatus = async (userId, currentStatus) => {
    try {
      const updatedUsers = users.map(user =>
        user._id === userId 
          ? { ...user, isActive: !currentStatus }
          : user
      );
      setUsers(updatedUsers);
      // In real app, call API to update user status
    } catch (error) {
      console.error('Error updating user status:', error);
    }
  };

  const filteredUsers = users.filter(user =>
    user.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
    user.email.toLowerCase().includes(searchTerm.toLowerCase()) ||
    user.role.toLowerCase().includes(searchTerm.toLowerCase())
  );

  const formatDate = (date) => {
    return new Date(date).toLocaleDateString('en-IN');
  };

  const getRoleBadge = (role) => {
    const roleConfig = {
      admin: { class: 'role-admin', text: 'Admin' },
      institute: { class: 'role-institute', text: 'Institute' },
      user: { class: 'role-user', text: 'User' }
    };
    
    const config = roleConfig[role] || roleConfig.user;
    return <span className={`role-badge ${config.class}`}>{config.text}</span>;
  };

  const getStatusBadge = (isActive) => {
    return (
      <span className={`status-badge ${isActive ? 'status-active' : 'status-inactive'}`}>
        {isActive ? 'Active' : 'Inactive'}
      </span>
    );
  };

  if (loading) {
    return <div className="loading">Loading users...</div>;
  }

  return (
    <div className="manage-users">
      <div className="section-header">
        <h2>Manage Users</h2>
        <div className="search-box">
          <input
            type="text"
            placeholder="Search users by name, email, or role..."
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
          />
        </div>
      </div>

      <div className="users-table-container">
        <table className="users-table">
          <thead>
            <tr>
              <th>User Info</th>
              <th>Role</th>
              <th>Status</th>
              <th>Joined Date</th>
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>
            {filteredUsers.length === 0 ? (
              <tr>
                <td colSpan="5" className="no-data">
                  No users found
                </td>
              </tr>
            ) : (
              filteredUsers.map(user => (
                <tr key={user._id}>
                  <td className="user-info">
                    <div className="user-avatar">
                      {user.name.charAt(0).toUpperCase()}
                    </div>
                    <div className="user-details">
                      <h4>{user.name}</h4>
                      <p>{user.email}</p>
                      <p>{user.phone}</p>
                    </div>
                  </td>
                  <td>
                    {getRoleBadge(user.role)}
                  </td>
                  <td>
                    {getStatusBadge(user.isActive)}
                  </td>
                  <td>
                    {formatDate(user.createdAt)}
                  </td>
                  <td>
                    <div className="user-actions">
                      <button
                        onClick={() => handleToggleStatus(user._id, user.isActive)}
                        className={user.isActive ? 'btn-deactivate' : 'btn-activate'}
                      >
                        {user.isActive ? 'Deactivate' : 'Activate'}
                      </button>
                      <button className="btn-view">
                        View Details
                      </button>
                    </div>
                  </td>
                </tr>
              ))
            )}
          </tbody>
        </table>
      </div>

      {/* User Statistics */}
      <div className="user-stats">
        <div className="stat-card">
          <h3>Total Users</h3>
          <p className="stat-number">{users.length}</p>
        </div>
        <div className="stat-card">
          <h3>Active Users</h3>
          <p className="stat-number">{users.filter(u => u.isActive).length}</p>
        </div>
        <div className="stat-card">
          <h3>Institutes</h3>
          <p className="stat-number">{users.filter(u => u.role === 'institute').length}</p>
        </div>
        <div className="stat-card">
          <h3>Regular Users</h3>
          <p className="stat-number">{users.filter(u => u.role === 'user').length}</p>
        </div>
      </div>
    </div>
  );
};

export default ManageUsers;
