import React, { useState, useEffect } from 'react';
import { useParams, Link } from 'react-router-dom';
import { instituteAPI, reviewAPI, enquiryAPI } from '../../services/api';
import { useAuth } from '../../context/AuthContext';
import ReviewForm from './ReviewForm';
import EnquiryForm from './EnquiryForm';

const InstituteDetail = () => {
  const { id } = useParams();
  const [institute, setInstitute] = useState(null);
  const [reviews, setReviews] = useState([]);
  const [courses, setCourses] = useState([]);
  const [facilities, setFacilities] = useState([]);
  const [activeTab, setActiveTab] = useState('overview');
  const [showReviewForm, setShowReviewForm] = useState(false);
  const [showEnquiryForm, setShowEnquiryForm] = useState(false);
  const [loading, setLoading] = useState(true);
  const { isAuthenticated } = useAuth();

  useEffect(() => {
    fetchInstituteDetails();
  }, [id]);

  const fetchInstituteDetails = async () => {
    try {
      setLoading(true);
      const [instituteRes, reviewsRes] = await Promise.all([
        instituteAPI.getById(id),
        reviewAPI.getByInstitute(id)
      ]);
      
      setInstitute(instituteRes.data);
      setReviews(reviewsRes.data);
      
      // In a real app, you would fetch courses and facilities here
      // For now, we'll use mock data
      setCourses(getMockCourses());
      setFacilities(getMockFacilities());
    } catch (error) {
      console.error('Error fetching institute details:', error);
    } finally {
      setLoading(false);
    }
  };

  const getMockCourses = () => [
    { title: 'Science Stream', duration: '2 Years', fees: 120000 },
    { title: 'Commerce Stream', duration: '2 Years', fees: 100000 },
    { title: 'Arts Stream', duration: '2 Years', fees: 80000 }
  ];

  const getMockFacilities = () => [
    { name: 'Library', icon: 'üìö', isAvailable: true },
    { name: 'Science Lab', icon: 'üî¨', isAvailable: true },
    { name: 'Computer Lab', icon: 'üíª', isAvailable: true },
    { name: 'Sports Ground', icon: '‚öΩ', isAvailable: true },
    { name: 'Auditorium', icon: 'üé≠', isAvailable: true },
    { name: 'Cafeteria', icon: 'üçΩÔ∏è', isAvailable: true }
  ];

  const renderStars = (rating) => {
    return Array.from({ length: 5 }, (_, index) => (
      <span key={index} className={index < rating ? 'star filled' : 'star'}>
        {index < rating ? '‚≠ê' : '‚òÜ'}
      </span>
    ));
  };

  if (loading) {
    return <div className="loading">Loading institute details...</div>;
  }

  if (!institute) {
    return <div className="error">Institute not found</div>;
  }

  return (
    <div className="institute-detail">
      <div className="container">
        {/* Institute Header */}
        <div className="institute-header">
          <div className="institute-hero">
            <div className="hero-image">
              {institute.images && institute.images.length > 0 ? (
                <img src={institute.images[0]} alt={institute.name} />
              ) : (
                <div className="image-placeholder">üè´</div>
              )}
            </div>
            
            <div className="hero-content">
              <h1>{institute.name}</h1>
              <p className="category">{institute.category}</p>
              <p className="location">üìç {institute.address.city}, {institute.address.state}</p>
              
              <div className="rating-overview">
                {institute.avgRating > 0 ? (
                  <>
                    <div className="rating">
                      <span className="rating-number">{institute.avgRating}</span>
                      <div className="stars">
                        {renderStars(Math.round(institute.avgRating))}
                      </div>
                      <span className="review-count">({institute.totalReviews} reviews)</span>
                    </div>
                  </>
                ) : (
                  <span className="no-rating">No ratings yet</span>
                )}
              </div>

              <div className="action-buttons">
                <button 
                  className="btn btn-primary"
                  onClick={() => setShowEnquiryForm(true)}
                >
                  üìß Send Enquiry
                </button>
                {isAuthenticated && (
                  <button 
                    className="btn btn-outline"
                    onClick={() => setShowReviewForm(true)}
                  >
                    ‚≠ê Write Review
                  </button>
                )}
              </div>
            </div>
          </div>
        </div>

        {/* Navigation Tabs */}
        <div className="detail-tabs">
          <button 
            className={`tab ${activeTab === 'overview' ? 'active' : ''}`}
            onClick={() => setActiveTab('overview')}
          >
            Overview
          </button>
          <button 
            className={`tab ${activeTab === 'courses' ? 'active' : ''}`}
            onClick={() => setActiveTab('courses')}
          >
            Courses
          </button>
          <button 
            className={`tab ${activeTab === 'facilities' ? 'active' : ''}`}
            onClick={() => setActiveTab('facilities')}
          >
            Facilities
          </button>
          <button 
            className={`tab ${activeTab === 'reviews' ? 'active' : ''}`}
            onClick={() => setActiveTab('reviews')}
          >
            Reviews ({reviews.length})
          </button>
        </div>

        {/* Tab Content */}
        <div className="tab-content">
          {activeTab === 'overview' && (
            <div className="overview-tab">
              <div className="overview-grid">
                <div className="info-card">
                  <h3>About</h3>
                  <p>{institute.description}</p>
                  
                  <div className="details-list">
                    <div className="detail-item">
                      <strong>Affiliation:</strong> {institute.affiliation}
                    </div>
                    {institute.establishedYear && (
                      <div className="detail-item">
                        <strong>Established:</strong> {institute.establishedYear}
                      </div>
                    )}
                    {institute.contact.phone && (
                      <div className="detail-item">
                        <strong>Phone:</strong> {institute.contact.phone}
                      </div>
                    )}
                    {institute.contact.email && (
                      <div className="detail-item">
                        <strong>Email:</strong> {institute.contact.email}
                      </div>
                    )}
                    {institute.contact.website && (
                      <div className="detail-item">
                        <strong>Website:</strong> 
                        <a href={institute.contact.website} target="_blank" rel="noopener noreferrer">
                          {institute.contact.website}
                        </a>
                      </div>
                    )}
                  </div>
                </div>

                <div className="info-card">
                  <h3>Quick Facts</h3>
                  <div className="facts-grid">
                    <div className="fact">
                      <span className="fact-label">Category</span>
                      <span className="fact-value">{institute.category}</span>
                    </div>
                    <div className="fact">
                      <span className="fact-label">Location</span>
                      <span className="fact-value">{institute.address.city}</span>
                    </div>
                    {institute.feeRange && (
                      <div className="fact">
                        <span className="fact-label">Annual Fees</span>
                        <span className="fact-value">
                          ‚Çπ{institute.feeRange.min?.toLocaleString()} - ‚Çπ{institute.feeRange.max?.toLocaleString()}
                        </span>
                      </div>
                    )}
                    <div className="fact">
                      <span className="fact-label">Verified</span>
                      <span className="fact-value verified">‚úì Yes</span>
                    </div>
                  </div>
                </div>
              </div>
            </div>
          )}

          {activeTab === 'courses' && (
            <div className="courses-tab">
              <h3>Available Courses</h3>
              <div className="courses-grid">
                {courses.map((course, index) => (
                  <div key={index} className="course-card">
                    <h4>{course.title}</h4>
                    <p><strong>Duration:</strong> {course.duration}</p>
                    <p><strong>Fees:</strong> ‚Çπ{course.fees?.toLocaleString()}</p>
                  </div>
                ))}
              </div>
            </div>
          )}

          {activeTab === 'facilities' && (
            <div className="facilities-tab">
              <h3>Institute Facilities</h3>
              <div className="facilities-grid">
                {facilities.map((facility, index) => (
                  <div key={index} className="facility-item">
                    <span className="facility-icon">{facility.icon}</span>
                    <span className="facility-name">{facility.name}</span>
                  </div>
                ))}
              </div>
            </div>
          )}

          {activeTab === 'reviews' && (
            <div className="reviews-tab">
              <div className="reviews-header">
                <h3>Student Reviews</h3>
                {isAuthenticated && (
                  <button 
                    className="btn btn-primary"
                    onClick={() => setShowReviewForm(true)}
                  >
                    Write a Review
                  </button>
                )}
              </div>

              {reviews.length === 0 ? (
                <div className="empty-reviews">
                  <p>No reviews yet. Be the first to review this institute!</p>
                  {!isAuthenticated && (
                    <p>
                      <Link to="/login">Login</Link> to write a review.
                    </p>
                  )}
                </div>
              ) : (
                <div className="reviews-list">
                  {reviews.map(review => (
                    <div key={review._id} className="review-item">
                      <div className="review-header">
                        <h4>{review.user.name}</h4>
                        <div className="review-rating">
                          {renderStars(review.rating)}
                        </div>
                      </div>
                      <p className="review-text">{review.reviewText}</p>
                      <span className="review-date">
                        {new Date(review.createdAt).toLocaleDateString()}
                      </span>
                    </div>
                  ))}
                </div>
              )}
            </div>
          )}
        </div>
      </div>

      {/* Modals */}
      {showReviewForm && (
        <ReviewForm 
          institute={institute}
          onClose={() => setShowReviewForm(false)}
          onSuccess={() => {
            setShowReviewForm(false);
            fetchInstituteDetails();
          }}
        />
      )}

      {showEnquiryForm && (
        <EnquiryForm 
          institute={institute}
          onClose={() => setShowEnquiryForm(false)}
          onSuccess={() => {
            setShowEnquiryForm(false);
            alert('Enquiry sent successfully!');
          }}
        />
      )}
    </div>
  );
};

export default InstituteDetail;
