import './Auth.css';

const Register = () => {
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    phone: '',
    password: '',
    confirmPassword: '',
    role: 'user'
  });
  
  const [instituteData, setInstituteData] = useState({
    name: '',
    category: '',
    affiliation: '',
    address: '',
    city: '',
    state: '',
    contactEmail: '',
    contactPhone: '',
    website: '',
    description: ''
  });
  
  const [errors, setErrors] = useState({});
  const [loading, setLoading] = useState(false);
  const { login } = useAuth();
  const navigate = useNavigate();

  const validateForm = () => {
    const newErrors = {};
    
    // Basic validation
    if (!formData.name.trim()) newErrors.name = 'Name is required';
    if (!formData.email) newErrors.email = 'Email is required';
    else if (!/\S+@\S+\.\S+/.test(formData.email)) newErrors.email = 'Email is invalid';
    if (!formData.phone.trim()) newErrors.phone = 'Phone is required';
    if (!formData.password) newErrors.password = 'Password is required';
    else if (formData.password.length < 6) newErrors.password = 'Password must be at least 6 characters';
    if (formData.password !== formData.confirmPassword) newErrors.confirmPassword = 'Passwords do not match';
    
    // Institute specific validation
    if (formData.role === 'institute') {
      if (!instituteData.name.trim()) newErrors.instituteName = 'Institute name is required';
      if (!instituteData.category) newErrors.category = 'Category is required';
      if (!instituteData.affiliation.trim()) newErrors.affiliation = 'Affiliation is required';
      if (!instituteData.address.trim()) newErrors.address = 'Address is required';
      if (!instituteData.city.trim()) newErrors.city = 'City is required';
      if (!instituteData.state.trim()) newErrors.state = 'State is required';
      if (!instituteData.contactEmail) newErrors.contactEmail = 'Contact email is required';
      if (!instituteData.contactPhone.trim()) newErrors.contactPhone = 'Contact phone is required';
    }
    
    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleUserChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
    
    if (errors[e.target.name]) {
      setErrors({
        ...errors,
        [e.target.name]: ''
      });
    }
  };

  const handleInstituteChange = (e) => {
    setInstituteData({
      ...instituteData,
      [e.target.name]: e.target.value
    });
    
    if (errors[e.target.name]) {
      setErrors({
        ...errors,
        [e.target.name]: ''
      });
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    
    if (!validateForm()) return;
    
    setLoading(true);
    
    try {
      const registrationData = {
        ...formData,
        instituteData: formData.role === 'institute' ? instituteData : undefined
      };
      
      const response = await authAPI.register(registrationData);
      const { token, user } = response.data;
      
      login(token, user);
      
      if (user.role === 'institute') {
        navigate('/institute/dashboard?pending=true');
      } else {
        navigate('/user/dashboard');
      }
    } catch (error) {
      setErrors({ submit: error.response?.data?.message || 'Registration failed' });
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="auth-container">
      <div className="auth-card">
        <h2>Join EduList</h2>
        
        {errors.submit && (
          <div className="error-message">{errors.submit}</div>
        )}
        
        <form onSubmit={handleSubmit} className="auth-form">
          <div className="form-group">
            <label htmlFor="name">Full Name *</label>
            <input
              type="text"
              id="name"
              name="name"
              value={formData.name}
              onChange={handleUserChange}
              className={errors.name ? 'error' : ''}
            />
            {errors.name && <span className="error-text">{errors.name}</span>}
          </div>
          
          <div className="form-group">
            <label htmlFor="email">Email *</label>
            <input
              type="email"
              id="email"
              name="email"
              value={formData.email}
              onChange={handleUserChange}
              className={errors.email ? 'error' : ''}
            />
            {errors.email && <span className="error-text">{errors.email}</span>}
          </div>
          
          <div className="form-group">
            <label htmlFor="phone">Phone *</label>
            <input
              type="tel"
              id="phone"
              name="phone"
              value={formData.phone}
              onChange={handleUserChange}
              className={errors.phone ? 'error' : ''}
            />
            {errors.phone && <span className="error-text">{errors.phone}</span>}
          </div>
          
          <div className="form-group">
            <label htmlFor="role">I am a *</label>
            <select
              id="role"
              name="role"
              value={formData.role}
              onChange={handleUserChange}
            >
              <option value="user">Student/Parent</option>
              <option value="institute">Educational Institute</option>
            </select>
          </div>
          
          <div className="form-group">
            <label htmlFor="password">Password *</label>
            <input
              type="password"
              id="password"
              name="password"
              value={formData.password}
              onChange={handleUserChange}
              className={errors.password ? 'error' : ''}
            />
            {errors.password && <span className="error-text">{errors.password}</span>}
          </div>
          
          <div className="form-group">
            <label htmlFor="confirmPassword">Confirm Password *</label>
            <input
              type="password"
              id="confirmPassword"
              name="confirmPassword"
              value={formData.confirmPassword}
              onChange={handleUserChange}
              className={errors.confirmPassword ? 'error' : ''}
            />
            {errors.confirmPassword && <span className="error-text">{errors.confirmPassword}</span>}
          </div>
          
          {/* Institute Specific Fields */}
          {formData.role === 'institute' && (
            <div className="institute-fields">
              <h3>Institute Information</h3>
              
              <div className="form-group">
                <label htmlFor="instituteName">Institute Name *</label>
                <input
                  type="text"
                  id="instituteName"
                  name="name"
                  value={instituteData.name}
                  onChange={handleInstituteChange}
                  className={errors.instituteName ? 'error' : ''}
                />
                {errors.instituteName && <span className="error-text">{errors.instituteName}</span>}
              </div>
              
              <div className="form-group">
                <label htmlFor="category">Category *</label>
                <select
                  id="category"
                  name="category"
                  value={instituteData.category}
                  onChange={handleInstituteChange}
                  className={errors.category ? 'error' : ''}
                >
                  <option value="">Select Category</option>
                  <option value="school">School</option>
                  <option value="college">College</option>
                  <option value="coaching">Coaching Center</option>
                  <option value="preschool">Preschool</option>
                  <option value="university">University</option>
                </select>
                {errors.category && <span className="error-text">{errors.category}</span>}
              </div>
              
              <div className="form-group">
                <label htmlFor="affiliation">Affiliation/Board *</label>
                <input
                  type="text"
                  id="affiliation"
                  name="affiliation"
                  value={instituteData.affiliation}
                  onChange={handleInstituteChange}
                  className={errors.affiliation ? 'error' : ''}
                  placeholder="e.g., CBSE, State Board, UGC"
                />
                {errors.affiliation && <span className="error-text">{errors.affiliation}</span>}
              </div>
              
              <div className="form-group">
                <label htmlFor="address">Address *</label>
                <textarea
                  id="address"
                  name="address"
                  value={instituteData.address}
                  onChange={handleInstituteChange}
                  className={errors.address ? 'error' : ''}
                  rows="3"
                />
                {errors.address && <span className="error-text">{errors.address}</span>}
              </div>
              
              <div className="form-row">
                <div className="form-group">
                  <label htmlFor="city">City *</label>
                  <input
                    type="text"
                    id="city"
                    name="city"
                    value={instituteData.city}
                    onChange={handleInstituteChange}
                    className={errors.city ? 'error' : ''}
                  />
                  {errors.city && <span className="error-text">{errors.city}</span>}
                </div>
                
                <div className="form-group">
                  <label htmlFor="state">State *</label>
                  <input
                    type="text"
                    id="state"
                    name="state"
                    value={instituteData.state}
                    onChange={handleInstituteChange}
                    className={errors.state ? 'error' : ''}
                  />
                  {errors.state && <span className="error-text">{errors.state}</span>}
                </div>
              </div>
              
              <div className="form-group">
                <label htmlFor="contactEmail">Contact Email *</label>
                <input
                  type="email"
                  id="contactEmail"
                  name="contactEmail"
                  value={instituteData.contactEmail}
                  onChange={handleInstituteChange}
                  className={errors.contactEmail ? 'error' : ''}
                />
                {errors.contactEmail && <span className="error-text">{errors.contactEmail}</span>}
              </div>
              
              <div className="form-group">
                <label htmlFor="contactPhone">Contact Phone *</label>
                <input
                  type="tel"
                  id="contactPhone"
                  name="contactPhone"
                  value={instituteData.contactPhone}
                  onChange={handleInstituteChange}
                  className={errors.contactPhone ? 'error' : ''}
                />
                {errors.contactPhone && <span className="error-text">{errors.contactPhone}</span>}
              </div>
              
              <div className="form-group">
                <label htmlFor="website">Website</label>
                <input
                  type="url"
                  id="website"
                  name="website"
                  value={instituteData.website}
                  onChange={handleInstituteChange}
                  placeholder="https://example.com"
                />
              </div>
              
              <div className="form-group">
                <label htmlFor="description">Description</label>
                <textarea
                  id="description"
                  name="description"
                  value={instituteData.description}
                  onChange={handleInstituteChange}
                  rows="4"
                  placeholder="Tell us about your institute..."
                />
              </div>
            </div>
          )}
          
          <button 
            type="submit" 
            className="auth-button"
            disabled={loading}
          >
            {loading ? 'Creating Account...' : 'Register'}
          </button>
        </form>
        
        <p className="auth-link">
          Already have an account? <Link to="/login">Login here</Link>
        </p>
      </div>
    </div>
  );
};

export default Register;
