import React, { useState, useEffect } from 'react';
import { facilityAPI } from '../../services/api';
import { useAuth } from '../../context/AuthContext';

const FacilitiesManagement = () => {
  const [facilities, setFacilities] = useState([]);
  const [showForm, setShowForm] = useState(false);
  const [editingFacility, setEditingFacility] = useState(null);
  const [loading, setLoading] = useState(false);
  const { user } = useAuth();

  const [formData, setFormData] = useState({
    name: '',
    description: '',
    icon: '‚úÖ'
  });

  useEffect(() => {
    fetchFacilities();
  }, []);

  const fetchFacilities = async () => {
    try {
      const response = await facilityAPI.getByInstitute(user.institute._id);
      setFacilities(response.data);
    } catch (error) {
      console.error('Error fetching facilities:', error);
    }
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);

    try {
      if (editingFacility) {
        await facilityAPI.update(editingFacility._id, formData);
      } else {
        await facilityAPI.create(user.institute._id, formData);
      }
      
      setShowForm(false);
      setEditingFacility(null);
      setFormData({ name: '', description: '', icon: '‚úÖ' });
      fetchFacilities();
    } catch (error) {
      alert('Error saving facility: ' + error.message);
    } finally {
      setLoading(false);
    }
  };

  const handleEdit = (facility) => {
    setEditingFacility(facility);
    setFormData({
      name: facility.name,
      description: facility.description || '',
      icon: facility.icon || '‚úÖ'
    });
    setShowForm(true);
  };

  const handleDelete = async (facilityId) => {
    if (window.confirm('Are you sure you want to delete this facility?')) {
      try {
        await facilityAPI.delete(facilityId);
        fetchFacilities();
      } catch (error) {
        alert('Error deleting facility: ' + error.message);
      }
    }
  };

  const handleToggle = async (facilityId) => {
    try {
      await facilityAPI.toggle(facilityId);
      fetchFacilities();
    } catch (error) {
      alert('Error updating facility status: ' + error.message);
    }
  };

  const commonFacilities = [
    { name: 'Library', icon: 'üìö', description: 'Well-stocked library with books and digital resources' },
    { name: 'Computer Lab', icon: 'üíª', description: 'Modern computer lab with internet access' },
    { name: 'Science Lab', icon: 'üî¨', description: 'Fully equipped science laboratories' },
    { name: 'Sports Ground', icon: '‚öΩ', description: 'Spacious playground for various sports' },
    { name: 'Auditorium', icon: 'üé≠', description: 'Large auditorium for events and functions' },
    { name: 'Cafeteria', icon: 'üçΩÔ∏è', description: 'Clean and hygienic cafeteria' },
    { name: 'Hostel', icon: 'üè†', description: 'Safe and comfortable hostel accommodation' },
    { name: 'Transport', icon: 'üöå', description: 'School bus transportation service' },
    { name: 'Medical Room', icon: 'üè•', description: 'Medical facility with trained staff' },
    { name: 'Art Room', icon: 'üé®', description: 'Dedicated space for arts and crafts' }
  ];

  const addCommonFacility = async (facility) => {
    try {
      await facilityAPI.create(user.institute._id, facility);
      fetchFacilities();
    } catch (error) {
      alert('Error adding facility: ' + error.message);
    }
  };

  return (
    <div className="facilities-management">
      <div className="section-header">
        <h2>Facilities Management</h2>
        <button 
          className="btn btn-primary"
          onClick={() => {
            setShowForm(true);
            setEditingFacility(null);
            setFormData({ name: '', description: '', icon: '‚úÖ' });
          }}
        >
          + Add Custom Facility
        </button>
      </div>

      {showForm && (
        <div className="modal-overlay">
          <div className="modal-content">
            <div className="modal-header">
              <h3>{editingFacility ? 'Edit Facility' : 'Add New Facility'}</h3>
              <button 
                className="close-btn"
                onClick={() => setShowForm(false)}
              >
                √ó
              </button>
            </div>
            
            <form onSubmit={handleSubmit} className="facility-form">
              <div className="form-group">
                <label>Facility Name *</label>
                <input
                  type="text"
                  name="name"
                  value={formData.name}
                  onChange={handleChange}
                  required
                  placeholder="e.g., Swimming Pool, Library, etc."
                />
              </div>

              <div className="form-group">
                <label>Icon</label>
                <select
                  name="icon"
                  value={formData.icon}
                  onChange={handleChange}
                >
                  <option value="‚úÖ">‚úÖ Checkmark</option>
                  <option value="üìö">üìö Library</option>
                  <option value="üíª">üíª Computer</option>
                  <option value="üî¨">üî¨ Science Lab</option>
                  <option value="‚öΩ">‚öΩ Sports</option>
                  <option value="üé≠">üé≠ Auditorium</option>
                  <option value="üçΩÔ∏è">üçΩÔ∏è Cafeteria</option>
                  <option value="üè†">üè† Hostel</option>
                  <option value="üöå">üöå Transport</option>
                  <option value="üè•">üè• Medical</option>
                  <option value="üé®">üé® Art Room</option>
                  <option value="üìù">üìù Test Series</option>
                  <option value="‚ùì">‚ùì Doubt Sessions</option>
                </select>
              </div>

              <div className="form-group">
                <label>Description</label>
                <textarea
                  name="description"
                  value={formData.description}
                  onChange={handleChange}
                  rows="3"
                  placeholder="Describe the facility..."
                />
              </div>

              <div className="form-actions">
                <button
                  type="button"
                  className="btn btn-outline"
                  onClick={() => setShowForm(false)}
                >
                  Cancel
                </button>
                <button
                  type="submit"
                  className="btn btn-primary"
                  disabled={loading}
                >
                  {loading ? 'Saving...' : (editingFacility ? 'Update' : 'Create')}
                </button>
              </div>
            </form>
          </div>
        </div>
      )}

      {/* Common Facilities Quick Add */}
      <div className="common-facilities">
        <h3>Quick Add Common Facilities</h3>
        <div className="common-facilities-grid">
          {commonFacilities.map((facility, index) => (
            <div key={index} className="common-facility-card">
              <div className="facility-icon">{facility.icon}</div>
              <div className="facility-info">
                <h4>{facility.name}</h4>
                <p>{facility.description}</p>
              </div>
              <button
                className="btn btn-outline btn-sm"
                onClick={() => addCommonFacility(facility)}
              >
                Add
              </button>
            </div>
          ))}
        </div>
      </div>

      {/* Existing Facilities */}
      <div className="facilities-list">
        <h3>Current Facilities ({facilities.length})</h3>
        
        {facilities.length === 0 ? (
          <div className="empty-state">
            <p>No facilities added yet. Add some facilities to showcase your institute.</p>
          </div>
        ) : (
          <div className="facilities-grid">
            {facilities.map(facility => (
              <div key={facility._id} className="facility-card">
                <div className="facility-header">
                  <div className="facility-icon">{facility.icon}</div>
                  <div className="facility-status">
                    <label className="toggle-switch">
                      <input
                        type="checkbox"
                        checked={facility.isAvailable}
                        onChange={() => handleToggle(facility._id)}
                      />
                      <span className="slider"></span>
                    </label>
                    <span className={`status ${facility.isAvailable ? 'active' : 'inactive'}`}>
                      {facility.isAvailable ? 'Available' : 'Unavailable'}
                    </span>
                  </div>
                </div>
                
                <div className="facility-content">
                  <h4>{facility.name}</h4>
                  {facility.description && (
                    <p className="facility-description">{facility.description}</p>
                  )}
                </div>

                <div className="facility-actions">
                  <button
                    className="btn btn-outline btn-sm"
                    onClick={() => handleEdit(facility)}
                  >
                    Edit
                  </button>
                  <button
                    className="btn btn-danger btn-sm"
                    onClick={() => handleDelete(facility._id)}
                  >
                    Delete
                  </button>
                </div>
              </div>
            ))}
          </div>
        )}
      </div>
    </div>
  );
};

export default FacilitiesManagement;
