import React, { useState, useEffect } from 'react';
import { useAuth } from '../../context/AuthContext';
import { reviewService, enquiryService } from '../../services/api';
import './UserPanel.css';

const UserDashboard = () => {
  const { user } = useAuth();
  const [userReviews, setUserReviews] = useState([]);
  const [userEnquiries, setUserEnquiries] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  useEffect(() => {
    fetchUserData();
  }, []);

  const fetchUserData = async () => {
    try {
      setLoading(true);
      setError('');

      // Fetch user's reviews and enquiries
      const [reviewsRes, enquiriesRes] = await Promise.all([
        reviewService.getByUser(),
        enquiryService.getByUser()
      ]);
      
      setUserReviews(reviewsRes.data || []);
      setUserEnquiries(enquiriesRes.data || []);
      
    } catch (error) {
      console.error('Error fetching user data:', error);
      setError('Failed to load your data. Please try again.');
      setUserReviews([]);
      setUserEnquiries([]);
    } finally {
      setLoading(false);
    }
  };

  if (loading) {
    return (
      <div className="user-dashboard">
        <div className="loading">Loading your dashboard...</div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="user-dashboard">
        <div className="error-message">
          <p>{error}</p>
          <button onClick={fetchUserData} className="btn-primary">
            Try Again
          </button>
        </div>
      </div>
    );
  }

  return (
    <div className="user-dashboard">
      <div className="container">
        <div className="dashboard-header">
          <h1>Welcome back, {user?.name}!</h1>
          <p>Manage your reviews and enquiries</p>
        </div>

        <div className="dashboard-stats">
          <div className="stat-card">
            <div className="stat-icon">‚≠ê</div>
            <div className="stat-info">
              <h3>{userReviews.length}</h3>
              <p>Reviews Written</p>
            </div>
          </div>
          <div className="stat-card">
            <div className="stat-icon">üì•</div>
            <div className="stat-info">
              <h3>{userEnquiries.length}</h3>
              <p>Enquiries Sent</p>
            </div>
          </div>
        </div>

        <div className="dashboard-sections">
          <div className="dashboard-section">
            <h2>Your Recent Reviews</h2>
            {userReviews.length === 0 ? (
              <div className="no-data">
                <p>You haven't written any reviews yet.</p>
                <a href="/institutes" className="btn-primary">Browse Institutes</a>
              </div>
            ) : (
              <div className="reviews-list">
                {userReviews.slice(0, 5).map(review => (
                  <div key={review._id} className="review-item">
                    <div className="review-header">
                      <h4>{review.institute?.name}</h4>
                      <div className="rating">
                        {'‚≠ê'.repeat(review.rating)}
                        <span>({review.rating}/5)</span>
                      </div>
                    </div>
                    <p className="review-text">{review.reviewText}</p>
                    <span className="review-date">
                      {new Date(review.createdAt).toLocaleDateString()}
                    </span>
                  </div>
                ))}
              </div>
            )}
          </div>

          <div className="dashboard-section">
            <h2>Your Recent Enquiries</h2>
            {userEnquiries.length === 0 ? (
              <div className="no-data">
                <p>You haven't sent any enquiries yet.</p>
                <a href="/institutes" className="btn-primary">Find Institutes</a>
              </div>
            ) : (
              <div className="enquiries-list">
                {userEnquiries.slice(0, 5).map(enquiry => (
                  <div key={enquiry._id} className="enquiry-item">
                    <div className="enquiry-header">
                      <h4>{enquiry.institute?.name}</h4>
                      <span className={`status ${enquiry.status}`}>
                        {enquiry.status}
                      </span>
                    </div>
                    <p className="enquiry-message">{enquiry.message}</p>
                    {enquiry.response && (
                      <div className="enquiry-response">
                        <strong>Response:</strong>
                        <p>{enquiry.response.message}</p>
                      </div>
                    )}
                    <span className="enquiry-date">
                      {new Date(enquiry.createdAt).toLocaleDateString()}
                    </span>
                  </div>
                ))}
              </div>
            )}
          </div>
        </div>
      </div>
    </div>
  );
};

export default UserDashboard;
