import React, { useState, useEffect } from 'react';
import { Routes, Route, useNavigate } from 'react-router-dom';
import InstituteSidebar from './InstituteSidebar';
import ProfileManagement from './ProfileManagement';
import CourseManagement from './CourseManagement';
import FacilitiesManagement from './FacilitiesManagement';
import Enquiries from './Enquiries';
import Reviews from './Reviews';
import { useAuth } from '../../context/AuthContext';
import { instituteAPI } from '../../services/api';
import './InstituteDashboard.css';

const InstituteDashboard = () => {
  const [institute, setInstitute] = useState(null);
  const [stats, setStats] = useState({});
  const [loading, setLoading] = useState(true);
  const { user, logout } = useAuth();
  const navigate = useNavigate();
  const location = window.location;

  useEffect(() => {
    // Check if user is institute
    const userRole = user?.user?.role || user?.role;
    if (userRole !== 'institute') {
      navigate('/');
      return;
    }
    fetchInstituteData();
  }, [user, navigate]);

  const fetchInstituteData = async () => {
    try {
      // For now, use mock data since we need to fix the API
      const mockInstitute = {
        _id: '1',
        name: user?.user?.name || user?.name || 'Your Institute',
        category: 'school',
        avgRating: 4.5,
        totalReviews: 12
      };
      
      setInstitute(mockInstitute);
      setStats({
        reviews: 12,
        enquiries: 5,
        courses: 3,
        avgRating: 4.5
      });
    } catch (error) {
      console.error('Error fetching institute data:', error);
    } finally {
      setLoading(false);
    }
  };

  const handleLogout = () => {
    logout();
    navigate('/');
  };

  if (loading) {
    return (
      <div className="institute-dashboard">
        <div className="loading">Loading institute dashboard...</div>
      </div>
    );
  }

  return (
    <div className="institute-dashboard">
      <InstituteSidebar 
        institute={institute} 
        stats={stats}
        onLogout={handleLogout}
      />
      
      <div className="dashboard-content">
        <div className="content-header">
          <h1>Welcome, {institute?.name}</h1>
          <p>Manage your institute profile and interact with students</p>
        </div>

        <Routes>
          <Route 
            path="/" 
            element={<DashboardHome institute={institute} stats={stats} />} 
          />
          <Route 
            path="/profile" 
            element={
              <ProfileManagement 
                institute={institute} 
                onUpdate={fetchInstituteData} 
              />
            } 
          />
          <Route path="/courses" element={<CourseManagement />} />
          <Route path="/facilities" element={<FacilitiesManagement />} />
          <Route path="/enquiries" element={<Enquiries />} />
          <Route path="/reviews" element={<Reviews />} />
        </Routes>
      </div>
    </div>
  );
};

const DashboardHome = ({ institute, stats }) => {
  return (
    <div className="dashboard-home">
      <div className="stats-grid">
        <div className="stat-card">
          <h3>Total Reviews</h3>
          <div className="stat-number">{stats.reviews || 0}</div>
        </div>
        <div className="stat-card">
          <h3>Pending Enquiries</h3>
          <div className="stat-number">{stats.enquiries || 0}</div>
        </div>
        <div className="stat-card">
          <h3>Courses</h3>
          <div className="stat-number">{stats.courses || 0}</div>
        </div>
        <div className="stat-card">
          <h3>Rating</h3>
          <div className="stat-number">{institute?.avgRating || 'N/A'}</div>
        </div>
      </div>

      <div className="recent-activities">
        <h2>Recent Activities</h2>
        <div className="activity-list">
          <div className="activity-item">
            <div className="activity-icon">üìä</div>
            <div className="activity-content">
              <p>Your institute profile is live and visible to students</p>
              <span className="activity-time">Just now</span>
            </div>
          </div>
          <div className="activity-item">
            <div className="activity-icon">‚≠ê</div>
            <div className="activity-content">
              <p>Current rating: {institute?.avgRating || 'No ratings yet'}</p>
              <span className="activity-time">Updated recently</span>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default InstituteDashboard;
