import React, { useState } from 'react';
import { instituteAPI } from '../../services/api';

const CourseManagement = ({ institute, onUpdate }) => {
  const [showForm, setShowForm] = useState(false);
  const [editingCourse, setEditingCourse] = useState(null);
  const [formData, setFormData] = useState({
    title: '',
    description: '',
    duration: '',
    fees: '',
    images: []
  });
  const [isSubmitting, setIsSubmitting] = useState(false);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleImageChange = (e) => {
    const files = Array.from(e.target.files);
    setFormData(prev => ({
      ...prev,
      images: files
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setIsSubmitting(true);

    try {
      const submitData = new FormData();
      submitData.append('title', formData.title);
      submitData.append('description', formData.description);
      submitData.append('duration', formData.duration);
      submitData.append('fees', formData.fees);

      formData.images.forEach(image => {
        submitData.append('courseImages', image);
      });

      if (editingCourse) {
        // Update course logic here
        console.log('Update course:', editingCourse._id);
      } else {
        await instituteAPI.addCourse(submitData);
      }

      onUpdate();
      resetForm();
    } catch (error) {
      console.error('Error saving course:', error);
    } finally {
      setIsSubmitting(false);
    }
  };

  const resetForm = () => {
    setFormData({
      title: '',
      description: '',
      duration: '',
      fees: '',
      images: []
    });
    setEditingCourse(null);
    setShowForm(false);
  };

  const handleEdit = (course) => {
    setEditingCourse(course);
    setFormData({
      title: course.title,
      description: course.description,
      duration: course.duration,
      fees: course.fees,
      images: []
    });
    setShowForm(true);
  };

  const handleDelete = async (courseId) => {
    if (window.confirm('Are you sure you want to delete this course?')) {
      try {
        // Implement delete course API call
        console.log('Delete course:', courseId);
        onUpdate();
      } catch (error) {
        console.error('Error deleting course:', error);
      }
    }
  };

  return (
    <div className="course-management">
      <div className="section-header">
        <h2>Course Management</h2>
        <button 
          className="btn btn-primary"
          onClick={() => setShowForm(true)}
        >
          + Add New Course
        </button>
      </div>

      {showForm && (
        <div className="form-modal">
          <div className="modal-content">
            <div className="modal-header">
              <h3>{editingCourse ? 'Edit Course' : 'Add New Course'}</h3>
              <button onClick={resetForm} className="close-btn">√ó</button>
            </div>
            
            <form onSubmit={handleSubmit} className="modal-form">
              <div className="form-group">
                <label>Course Title *</label>
                <input
                  type="text"
                  name="title"
                  value={formData.title}
                  onChange={handleChange}
                  required
                />
              </div>

              <div className="form-group">
                <label>Description *</label>
                <textarea
                  name="description"
                  value={formData.description}
                  onChange={handleChange}
                  rows="4"
                  required
                />
              </div>

              <div className="form-row">
                <div className="form-group">
                  <label>Duration *</label>
                  <input
                    type="text"
                    name="duration"
                    value={formData.duration}
                    onChange={handleChange}
                    placeholder="e.g., 2 years, 6 months"
                    required
                  />
                </div>

                <div className="form-group">
                  <label>Fees (‚Çπ) *</label>
                  <input
                    type="number"
                    name="fees"
                    value={formData.fees}
                    onChange={handleChange}
                    required
                  />
                </div>
              </div>

              <div className="form-group">
                <label>Course Images</label>
                <input
                  type="file"
                  multiple
                  accept="image/*"
                  onChange={handleImageChange}
                />
                <small>You can upload multiple images</small>
              </div>

              <div className="form-actions">
                <button 
                  type="button" 
                  className="btn btn-secondary"
                  onClick={resetForm}
                >
                  Cancel
                </button>
                <button 
                  type="submit" 
                  className="btn btn-primary"
                  disabled={isSubmitting}
                >
                  {isSubmitting ? 'Saving...' : editingCourse ? 'Update Course' : 'Add Course'}
                </button>
              </div>
            </form>
          </div>
        </div>
      )}

      <div className="courses-list">
        <h3>Your Courses ({institute?.courses?.length || 0})</h3>
        
        {institute?.courses?.length > 0 ? (
          <div className="courses-grid">
            {institute.courses.map((course, index) => (
              <div key={index} className="course-card">
                <div className="course-image">
                  {course.images && course.images.length > 0 ? (
                    <img 
                      src={`http://localhost:5000/uploads/${course.images[0]}`} 
                      alt={course.title}
                    />
                  ) : (
                    <div className="image-placeholder">üìö</div>
                  )}
                </div>
                
                <div className="course-content">
                  <h4>{course.title}</h4>
                  <p className="course-description">{course.description}</p>
                  <div className="course-details">
                    <span className="duration">‚è±Ô∏è {course.duration}</span>
                    <span className="fees">üí∞ ‚Çπ{course.fees?.toLocaleString()}</span>
                  </div>
                </div>
                
                <div className="course-actions">
                  <button 
                    className="btn btn-outline btn-sm"
                    onClick={() => handleEdit(course)}
                  >
                    Edit
                  </button>
                  <button 
                    className="btn btn-danger btn-sm"
                    onClick={() => handleDelete(course._id)}
                  >
                    Delete
                  </button>
                </div>
              </div>
            ))}
          </div>
        ) : (
          <div className="empty-state">
            <div className="empty-icon">üìö</div>
            <h4>No Courses Added</h4>
            <p>Start by adding your first course to attract more students.</p>
          </div>
        )}
      </div>
    </div>
  );
};

export default CourseManagement;
