import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { instituteService, enquiryService, reviewService } from '../../services/api';
import './InstituteDashboard.css';

const DashboardHome = () => {
  const [stats, setStats] = useState({
    enquiries: 0,
    reviews: 0,
    courses: 0,
    rating: 0
  });
  const [recentEnquiries, setRecentEnquiries] = useState([]);
  const [recentReviews, setRecentReviews] = useState([]);
  const [institute, setInstitute] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetchDashboardData();
  }, []);

  const fetchDashboardData = async () => {
    try {
      const [instituteRes, enquiriesRes, reviewsRes] = await Promise.all([
        instituteService.getCurrentInstitute(),
        enquiryService.getByInstitute(),
        reviewService.getByInstitute()
      ]);

      const instituteData = instituteRes.data;
      const enquiriesData = enquiriesRes.data;
      const reviewsData = reviewsRes.data;
      
      setInstitute(instituteData);
      setStats({
        enquiries: enquiriesData.length,
        reviews: reviewsData.length,
        courses: instituteData.courses?.length || 0,
        rating: instituteData.rating?.average || 0
      });

      setRecentEnquiries(enquiriesData.slice(0, 5));
      setRecentReviews(reviewsData.slice(0, 5));
      setLoading(false);
    } catch (error) {
      console.error('Error fetching dashboard data:', error);
      setLoading(false);
    }
  };

  const getStatusBadge = (status) => {
    const statusConfig = {
      pending: { class: 'pending', label: 'Pending' },
      responded: { class: 'responded', label: 'Responded' },
      closed: { class: 'closed', label: 'Closed' }
    };
    
    const config = statusConfig[status] || statusConfig.pending;
    return <span className={`status-badge ${config.class}`}>{config.label}</span>;
  };

  if (loading) {
    return <div className="loading">Loading dashboard...</div>;
  }

  return (
    <div className="dashboard-home">
      <div className="welcome-section">
        <h1>Welcome to Your Dashboard</h1>
        <p>Manage your institute profile, courses, and interact with students</p>
