import React, { useState, useEffect } from 'react';
import { enquiryAPI } from '../../services/api';

const Enquiries = () => {
  const [enquiries, setEnquiries] = useState([]);
  const [loading, setLoading] = useState(true);
  const [respondingTo, setRespondingTo] = useState(null);
  const [responseMessage, setResponseMessage] = useState('');

  useEffect(() => {
    fetchEnquiries();
  }, []);

  const fetchEnquiries = async () => {
    try {
      const response = await enquiryAPI.getByInstitute();
      setEnquiries(response.data);
    } catch (error) {
      console.error('Error fetching enquiries:', error);
    } finally {
      setLoading(false);
    }
  };

  const handleRespond = async (enquiryId) => {
    if (!responseMessage.trim()) {
      alert('Please enter a response message');
      return;
    }

    try {
      await enquiryAPI.respond(enquiryId, { responseMessage });
      setRespondingTo(null);
      setResponseMessage('');
      fetchEnquiries(); // Refresh the list
    } catch (error) {
      console.error('Error responding to enquiry:', error);
    }
  };

  const getStatusBadge = (status) => {
    const statusConfig = {
      pending: { class: 'status-pending', label: 'Pending' },
      responded: { class: 'status-responded', label: 'Responded' },
      closed: { class: 'status-closed', label: 'Closed' }
    };
    
    const config = statusConfig[status] || statusConfig.pending;
    return <span className={`status-badge ${config.class}`}>{config.label}</span>;
  };

  if (loading) {
    return <div className="loading">Loading enquiries...</div>;
  }

  return (
    <div className="enquiries-management">
      <div className="section-header">
        <h2>Student Enquiries</h2>
        <div className="enquiry-stats">
          <span className="stat">
            Total: <strong>{enquiries.length}</strong>
          </span>
          <span className="stat">
            Pending: <strong>{enquiries.filter(e => e.status === 'pending').length}</strong>
          </span>
        </div>
      </div>

      <div className="enquiries-list">
        {enquiries.length > 0 ? (
          enquiries.map(enquiry => (
            <div key={enquiry._id} className="enquiry-card">
              <div className="enquiry-header">
                <div className="enquiry-user">
                  <h4>{enquiry.user?.name}</h4>
                  <p>{enquiry.user?.email} â€¢ {enquiry.user?.phone}</p>
                </div>
                <div className="enquiry-meta">
                  {getStatusBadge(enquiry.status)}
                  <span className="enquiry-date">
                    {new Date(enquiry.createdAt).toLocaleDateString()}
                  </span>
                </div>
              </div>

              <div className="enquiry-content">
                <p><strong>Message:</strong> {enquiry.message}</p>
                
                {enquiry.response && (
                  <div className="enquiry-response">
                    <strong>Your Response:</strong>
                    <p>{enquiry.response.message}</p>
                    <small>
                      Responded on: {new Date(enquiry.response.respondedAt).toLocaleString()}
                    </small>
                  </div>
                )}
              </div>

              {enquiry.status === 'pending' && (
                <div className="enquiry-actions">
                  {respondingTo === enquiry._id ? (
                    <div className="response-form">
                      <textarea
                        value={responseMessage}
                        onChange={(e) => setResponseMessage(e.target.value)}
                        placeholder="Type your response here..."
                        rows="4"
                      />
                      <div className="response-actions">
                        <button 
                          className="btn btn-secondary"
                          onClick={() => setRespondingTo(null)}
                        >
                          Cancel
                        </button>
                        <button 
                          className="btn btn-primary"
                          onClick={() => handleRespond(enquiry._id)}
                        >
                          Send Response
                        </button>
                      </div>
                    </div>
                  ) : (
                    <button 
                      className="btn btn-primary"
                      onClick={() => setRespondingTo(enquiry._id)}
                    >
                      Respond
                    </button>
                  )}
                </div>
              )}
            </div>
          ))
        ) : (
          <div className="empty-state">
            <div className="empty-icon">ðŸ“©</div>
            <h4>No Enquiries Yet</h4>
            <p>Student enquiries will appear here when they contact your institute.</p>
          </div>
        )}
      </div>
    </div>
  );
};

export default Enquiries;
