import React, { useState, useEffect } from 'react';
import { reviewAPI } from '../../services/api';
import { useAuth } from '../../context/AuthContext';

const Reviews = () => {
  const [reviews, setReviews] = useState([]);
  const [loading, setLoading] = useState(true);
  const { user } = useAuth();

  useEffect(() => {
    fetchReviews();
  }, []);

  const fetchReviews = async () => {
    try {
      const response = await reviewAPI.getByInstitute(user.institute?._id);
      setReviews(response.data);
    } catch (error) {
      console.error('Error fetching reviews:', error);
    } finally {
      setLoading(false);
    }
  };

  const handleFlagReview = async (reviewId) => {
    if (window.confirm('Flag this review as inappropriate?')) {
      try {
        // Implement flag review functionality
        console.log('Flag review:', reviewId);
        alert('Review flagged for admin review');
      } catch (error) {
        console.error('Error flagging review:', error);
      }
    }
  };

  const renderStars = (rating) => {
    return '‚≠ê'.repeat(rating) + '‚òÜ'.repeat(5 - rating);
  };

  if (loading) {
    return <div className="loading">Loading reviews...</div>;
  }

  return (
    <div className="reviews-management">
      <div className="section-header">
        <h2>Student Reviews</h2>
        <div className="reviews-stats">
          <span className="stat">
            Total: <strong>{reviews.length}</strong>
          </span>
          <span className="stat">
            Average: <strong>{(reviews.reduce((acc, review) => acc + review.rating, 0) / reviews.length || 0).toFixed(1)}‚≠ê</strong>
          </span>
        </div>
      </div>

      <div className="reviews-list">
        {reviews.length > 0 ? (
          reviews.map(review => (
            <div key={review._id} className="review-card">
              <div className="review-header">
                <div className="reviewer-info">
                  <div className="reviewer-avatar">
                    {review.user?.profileImage ? (
                      <img 
                        src={`http://localhost:5000/uploads/${review.user.profileImage}`} 
                        alt={review.user.name}
                      />
                    ) : (
                      <div className="avatar-placeholder">
                        {review.user?.name?.charAt(0).toUpperCase()}
                      </div>
                    )}
                  </div>
                  <div className="reviewer-details">
                    <h4>{review.user?.name}</h4>
                    <div className="review-rating">
                      {renderStars(review.rating)}
                    </div>
                  </div>
                </div>
                <span className="review-date">
                  {new Date(review.createdAt).toLocaleDateString()}
                </span>
              </div>

              <div className="review-content">
                <p>{review.reviewText}</p>
              </div>

              <div className="review-actions">
                <button 
                  className="btn btn-outline btn-sm"
                  onClick={() => handleFlagReview(review._id)}
                >
                  üö© Flag Inappropriate
                </button>
              </div>
            </div>
          ))
        ) : (
          <div className="empty-state">
            <div className="empty-icon">‚≠ê</div>
            <h4>No Reviews Yet</h4>
            <p>Student reviews will appear here when they rate your institute.</p>
          </div>
        )}
      </div>
    </div>
  );
};

export default Reviews;
