import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { instituteAPI } from '../../services/api';
import './UserPanel.css';

const InstituteList = () => {
  const [institutes, setInstitutes] = useState([]);
  const [loading, setLoading] = useState(true);
  const [filters, setFilters] = useState({
    search: '',
    category: '',
    city: '',
    minRating: '',
    maxFees: ''
  });
  const [pagination, setPagination] = useState({
    page: 1,
    totalPages: 1,
    total: 0
  });

  useEffect(() => {
    fetchInstitutes();
  }, [filters, pagination.page]);

  const fetchInstitutes = async () => {
    try {
      setLoading(true);
      const response = await instituteAPI.getAll({
        page: pagination.page,
        limit: 12,
        search: filters.search,
        category: filters.category,
        city: filters.city
      });
      
      setInstitutes(response.data.institutes);
      setPagination(prev => ({
        ...prev,
        totalPages: response.data.totalPages,
        total: response.data.total
      }));
    } catch (error) {
      console.error('Error fetching institutes:', error);
    } finally {
      setLoading(false);
    }
  };

  const handleFilterChange = (key, value) => {
    setFilters(prev => ({
      ...prev,
      [key]: value
    }));
    setPagination(prev => ({ ...prev, page: 1 }));
  };

  const clearFilters = () => {
    setFilters({
      search: '',
      category: '',
      city: '',
      minRating: '',
      maxFees: ''
    });
    setPagination(prev => ({ ...prev, page: 1 }));
  };

  if (loading && institutes.length === 0) {
    return <div className="loading">Loading institutes...</div>;
  }

  return (
    <div className="institute-list-page">
      <div className="container">
        <div className="page-header">
          <h1>Find Educational Institutes</h1>
          <p>Discover the perfect institution for your educational journey</p>
        </div>

        {/* Filters */}
        <div className="filters-section">
          <div className="filters-grid">
            <div className="filter-group">
              <label>Search</label>
              <input
                type="text"
                placeholder="Search by name..."
                value={filters.search}
                onChange={(e) => handleFilterChange('search', e.target.value)}
              />
            </div>
            
            <div className="filter-group">
              <label>Category</label>
              <select
                value={filters.category}
                onChange={(e) => handleFilterChange('category', e.target.value)}
              >
                <option value="">All Categories</option>
                <option value="school">School</option>
                <option value="college">College</option>
                <option value="coaching">Coaching Center</option>
                <option value="preschool">Preschool</option>
                <option value="university">University</option>
              </select>
            </div>
            
            <div className="filter-group">
              <label>City</label>
              <input
                type="text"
                placeholder="Enter city..."
                value={filters.city}
                onChange={(e) => handleFilterChange('city', e.target.value)}
              />
            </div>
            
            <div className="filter-group">
              <label>Min Rating</label>
              <select
                value={filters.minRating}
                onChange={(e) => handleFilterChange('minRating', e.target.value)}
              >
                <option value="">Any Rating</option>
                <option value="4">4+ Stars</option>
                <option value="3">3+ Stars</option>
                <option value="2">2+ Stars</option>
              </select>
            </div>
            
            <div className="filter-group">
              <label>Max Fees</label>
              <select
                value={filters.maxFees}
                onChange={(e) => handleFilterChange('maxFees', e.target.value)}
              >
                <option value="">Any Fees</option>
                <option value="50000">Under ‚Çπ50,000</option>
                <option value="100000">Under ‚Çπ1,00,000</option>
                <option value="200000">Under ‚Çπ2,00,000</option>
                <option value="500000">Under ‚Çπ5,00,000</option>
              </select>
            </div>
            
            <div className="filter-group">
              <button className="btn btn-outline" onClick={clearFilters}>
                Clear Filters
              </button>
            </div>
          </div>
        </div>

        {/* Results */}
        <div className="results-section">
          <div className="results-header">
            <p>Showing {institutes.length} of {pagination.total} institutes</p>
          </div>

          <div className="institutes-grid">
            {institutes.map(institute => (
              <InstituteCard key={institute._id} institute={institute} />
            ))}
          </div>

          {institutes.length === 0 && !loading && (
            <div className="empty-state">
              <h3>No institutes found</h3>
              <p>Try adjusting your filters or search terms</p>
              <button className="btn btn-primary" onClick={clearFilters}>
                Clear Filters
              </button>
            </div>
          )}

          {/* Pagination */}
          {pagination.totalPages > 1 && (
            <div className="pagination">
              <button
                disabled={pagination.page === 1}
                onClick={() => setPagination(prev => ({ ...prev, page: prev.page - 1 }))}
                className="pagination-btn"
              >
                Previous
              </button>
              
              <span className="pagination-info">
                Page {pagination.page} of {pagination.totalPages}
              </span>
              
              <button
                disabled={pagination.page === pagination.totalPages}
                onClick={() => setPagination(prev => ({ ...prev, page: prev.page + 1 }))}
                className="pagination-btn"
              >
                Next
              </button>
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

const InstituteCard = ({ institute }) => {
  return (
    <div className="institute-card">
      <Link to={`/institute/${institute._id}`} className="card-link">
        <div className="card-image">
          {institute.images && institute.images.length > 0 ? (
            <img src={institute.images[0]} alt={institute.name} />
          ) : (
            <div className="image-placeholder">üè´</div>
          )}
          <div className="card-overlay">
            <span className="view-details">View Details ‚Üí</span>
          </div>
        </div>
        
        <div className="card-content">
          <div className="card-header">
            <h3>{institute.name}</h3>
            {institute.avgRating > 0 && (
              <div className="rating">
                <span className="stars">‚≠ê</span>
                <span>{institute.avgRating}</span>
              </div>
            )}
          </div>
          
          <p className="category">{institute.category}</p>
          <p className="location">üìç {institute.address.city}, {institute.address.state}</p>
          <p className="affiliation">{institute.affiliation}</p>
          
          {institute.feeRange && (
            <p className="fees">
              Fees: ‚Çπ{institute.feeRange.min?.toLocaleString()} - ‚Çπ{institute.feeRange.max?.toLocaleString()}
