import React, { useState, useEffect } from 'react';
import { adminAPI, instituteAPI } from '../../services/api';
import './AdminPanel.css';

const ManageInstitutes = () => {
  const [institutes, setInstitutes] = useState([]);
  const [pendingInstitutes, setPendingInstitutes] = useState([]);
  const [activeTab, setActiveTab] = useState('pending');
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetchInstitutes();
  }, []);

  const fetchInstitutes = async () => {
    try {
      const [pendingResponse, approvedResponse] = await Promise.all([
        adminAPI.getPendingInstitutes(),
        instituteAPI.getAll()
      ]);
      
      setPendingInstitutes(pendingResponse.data);
      setInstitutes(approvedResponse.data);
    } catch (error) {
      console.error('Error fetching institutes:', error);
    } finally {
      setLoading(false);
    }
  };

  const handleApprove = async (instituteId) => {
    try {
      await adminAPI.approveInstitute(instituteId);
      // Move institute from pending to approved
      const institute = pendingInstitutes.find(inst => inst._id === instituteId);
      setInstitutes(prev => [institute, ...prev]);
      setPendingInstitutes(prev => prev.filter(inst => inst._id !== instituteId));
    } catch (error) {
      console.error('Error approving institute:', error);
    }
  };

  const handleReject = async (instituteId) => {
    if (window.confirm('Are you sure you want to reject this institute?')) {
      try {
        // In real implementation, you'd have a reject API endpoint
        setPendingInstitutes(prev => prev.filter(inst => inst._id !== instituteId));
        console.log('Rejected institute:', instituteId);
      } catch (error) {
        console.error('Error rejecting institute:', error);
      }
    }
  };

  const handleToggleStatus = async (instituteId, currentStatus) => {
    try {
      // In real implementation, you'd have an API to toggle institute status
      const updatedInstitutes = institutes.map(inst =>
        inst._id === instituteId 
          ? { ...inst, adminApproved: !currentStatus }
          : inst
      );
      setInstitutes(updatedInstitutes);
    } catch (error) {
      console.error('Error toggling institute status:', error);
    }
  };

  const formatDate = (dateString) => {
    return new Date(dateString).toLocaleDateString('en-IN');
  };

  if (loading) {
    return <div className="loading">Loading institutes...</div>;
  }

  return (
    <div className="manage-institutes">
      <div className="section-header">
        <h2>Manage Institutes</h2>
        <div className="tab-navigation">
          <button 
            className={`tab-btn ${activeTab === 'pending' ? 'active' : ''}`}
            onClick={() => setActiveTab('pending')}
          >
            Pending Approval ({pendingInstitutes.length})
          </button>
          <button 
            className={`tab-btn ${activeTab === 'approved' ? 'active' : ''}`}
            onClick={() => setActiveTab('approved')}
          >
            Approved Institutes ({institutes.length})
          </button>
        </div>
      </div>

      {activeTab === 'pending' && (
        <div className="institutes-list">
          {pendingInstitutes.length === 0 ? (
            <div className="empty-state">
              <p>No pending institute approvals.</p>
            </div>
          ) : (
            pendingInstitutes.map(institute => (
              <div key={institute._id} className="institute-card pending">
                <div className="institute-info">
                  <h3>{institute.name}</h3>
                  <div className="institute-details">
                    <p><strong>Category:</strong> {institute.category}</p>
                    <p><strong>Affiliation:</strong> {institute.affiliation}</p>
                    <p><strong>Location:</strong> {institute.city}, {institute.state}</p>
                    <p><strong>Contact:</strong> {institute.contactEmail} | {institute.contactPhone}</p>
                    <p><strong>Registered:</strong> {formatDate(institute.createdAt)}</p>
                  </div>
                  {institute.description && (
                    <p className="institute-description">{institute.description}</p>
                  )}
                </div>
                <div className="institute-actions">
                  <button 
                    onClick={() => handleApprove(institute._id)}
                    className="btn-approve"
                  >
                    ✓ Approve
                  </button>
                  <button 
                    onClick={() => handleReject(institute._id)}
                    className="btn-reject"
                  >
                    ✗ Reject
                  </button>
                </div>
              </div>
            ))
          )}
        </div>
      )}

      {activeTab === 'approved' && (
        <div className="institutes-list">
          {institutes.length === 0 ? (
            <div className="empty-state">
              <p>No approved institutes yet.</p>
            </div>
          ) : (
            institutes.map(institute => (
              <div key={institute._id} className="institute-card approved">
                <div className="institute-info">
                  <h3>{institute.name}</h3>
                  <div className="institute-details">
                    <p><strong>Category:</strong> {institute.category}</p>
                    <p><strong>Affiliation:</strong> {institute.affiliation}</p>
                    <p><strong>Location:</strong> {institute.city}, {institute.state}</p>
                    <p><strong>Status:</strong> 
                      <span className={`status ${institute.adminApproved ? 'active' : 'inactive'}`}>
                        {institute.adminApproved ? 'Active' : 'Inactive'}
                      </span>
                    </p>
                    <p><strong>Verified:</strong> {institute.verified ? 'Yes' : 'No'}</p>
                  </div>
                </div>
                <div className="institute-actions">
                  <button 
                    onClick={() => handleToggleStatus(institute._id, institute.adminApproved)}
                    className={institute.adminApproved ? 'btn-deactivate' : 'btn-activate'}
                  >
                    {institute.adminApproved ? 'Deactivate' : 'Activate'}
                  </button>
                  <button className="btn-view">
                    View Details
                  </button>
                </div>
              </div>
            ))
          )}
        </div>
      )}
    </div>
  );
};

export default ManageInstitutes;
