import api from './api';

// Mock data for fallback
const mockInstitutes = [
  {
    _id: '1',
    name: 'Delhi Public School',
    category: 'school',
    affiliation: 'CBSE',
    address: {
      street: 'RK Puram',
      city: 'New Delhi',
      state: 'Delhi',
      pincode: '110022'
    },
    contact: {
      phone: '011-12345678',
      email: 'info@dpsdelhi.com',
      website: 'https://dpsdelhi.com'
    },
    description: 'One of the premier educational institutions in Delhi, known for academic excellence and holistic development.',
    facilities: [
      { _id: '1', name: 'Library', description: 'Well-stocked library with 50,000+ books' },
      { _id: '2', name: 'Sports Complex', description: 'Olympic-sized swimming pool and indoor stadium' }
    ],
    reviews: [
      { rating: 5, reviewText: 'Excellent school with great facilities' },
      { rating: 4, reviewText: 'Good education quality' }
    ],
    status: 'approved'
  },
  {
    _id: '2',
    name: 'St. Xavier\'s College',
    category: 'college',
    affiliation: 'University of Delhi',
    address: {
      street: '5, Raj Niwas Marg',
      city: 'New Delhi',
      state: 'Delhi',
      pincode: '110054'
    },
    contact: {
      phone: '011-27667745',
      email: 'admissions@stxaviers.edu',
      website: 'https://stxaviers.edu'
    },
    description: 'A premier liberal arts college offering undergraduate and postgraduate programs in various disciplines.',
    facilities: [
      { _id: '1', name: 'Auditorium', description: '1000-seat auditorium for events and conferences' },
      { _id: '2', name: 'Computer Labs', description: 'State-of-the-art computer laboratories' }
    ],
    reviews: [
      { rating: 5, reviewText: 'Best college in Delhi' }
    ],
    status: 'approved'
  }
];

const mockInstituteProfile = {
  _id: '1',
  name: 'Delhi Public School',
  category: 'school',
  affiliation: 'CBSE',
  address: {
    street: 'RK Puram',
    city: 'New Delhi',
    state: 'Delhi',
    pincode: '110022'
  },
  contact: {
    phone: '011-12345678',
    email: 'info@dpsdelhi.com',
    website: 'https://dpsdelhi.com'
  },
  description: 'One of the premier educational institutions in Delhi, known for academic excellence and holistic development.',
  facilities: [
    { _id: '1', name: 'Library', description: 'Well-stocked library with 50,000+ books' },
    { _id: '2', name: 'Sports Complex', description: 'Olympic-sized swimming pool and indoor stadium' }
  ]
};

export const instituteService = {
  getAllInstitutes: async (filters = {}) => {
    try {
      const params = new URLSearchParams();
      Object.keys(filters).forEach(key => {
        if (filters[key]) {
          params.append(key, filters[key]);
        }
      });
      
      const response = await api.get(`/institutes/public?${params}`);
      return response.data;
    } catch (error) {
      console.log('Using mock data for institutes');
      // Apply basic filtering on mock data
      let filteredInstitutes = mockInstitutes;
      
      if (filters.category) {
        filteredInstitutes = filteredInstitutes.filter(inst => 
          inst.category === filters.category
        );
      }
      if (filters.city) {
        filteredInstitutes = filteredInstitutes.filter(inst => 
          inst.address.city.toLowerCase().includes(filters.city.toLowerCase())
        );
      }
      
      return filteredInstitutes;
    }
  },

  getInstituteById: async (id) => {
    try {
      const response = await api.get(`/institutes/${id}`);
      return response.data;
    } catch (error) {
      console.log('Using mock data for institute details');
      return mockInstitutes.find(inst => inst._id === id) || mockInstitutes[0];
    }
  },

  getInstituteProfile: async () => {
    try {
      const response = await api.get('/institutes/profile');
      return response.data;
    } catch (error) {
      console.log('Using mock data for institute profile');
      return mockInstituteProfile;
    }
  },

  updateInstitute: async (data) => {
    try {
      const response = await api.put('/institutes/profile', data);
      return response.data;
    } catch (error) {
      console.error('Error updating institute:', error);
      // Simulate success for demo
      return { message: 'Profile updated successfully', institute: data };
    }
  },

  addFacility: async (facility) => {
    try {
      const response = await api.post('/institutes/facilities', facility);
      return response.data;
    } catch (error) {
      console.error('Error adding facility:', error);
      // Simulate success for demo
      return { 
        message: 'Facility added successfully', 
        facilities: [...mockInstituteProfile.facilities, { ...facility, _id: Date.now().toString() }] 
      };
    }
  },

  removeFacility: async (facilityId) => {
    try {
      const response = await api.delete(`/institutes/facilities/${facilityId}`);
      return response.data;
    } catch (error) {
      console.error('Error removing facility:', error);
      // Simulate success for demo
      return { 
        message: 'Facility removed successfully', 
        facilities: mockInstituteProfile.facilities.filter(f => f._id !== facilityId) 
      };
    }
  }
};
