import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { useAuth } from '../../context/AuthContext';
import './Auth.css';

const Login = () => {
  const [formData, setFormData] = useState({
    email: '',
    password: ''
  });
  const [errors, setErrors] = useState({});
  const [isSubmitting, setIsSubmitting] = useState(false);
  
  const { login } = useAuth();
  const navigate = useNavigate();

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
    
    if (errors[name]) {
      setErrors(prev => ({
        ...prev,
        [name]: ''
      }));
    }
  };

  const validateForm = () => {
    const newErrors = {};

    if (!formData.email.trim()) {
      newErrors.email = 'Email is required';
    } else if (!/\S+@\S+\.\S+/.test(formData.email)) {
      newErrors.email = 'Email is invalid';
    }

    if (!formData.password) {
      newErrors.password = 'Password is required';
    }

    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    
    if (!validateForm()) {
      return;
    }

    setIsSubmitting(true);
    setErrors({});
    
    try {
      const result = await login(formData.email, formData.password);
      
      if (result.success) {
        console.log('üéâ Login successful, redirecting...');
        console.log('üë§ User role:', result.user.role);
        
        // Redirect based on user role
        const redirectPath = result.user.role === 'admin' ? '/admin' : 
                           result.user.role === 'institute' ? '/institute-dashboard' : '/user';
        
        console.log('üìç Redirecting to:', redirectPath);
        navigate(redirectPath);
      } else {
        // Show specific error message from backend
        setErrors({ submit: result.message || 'Login failed. Please try again.' });
      }
    } catch (error) {
      console.error('üí• Login error:', error);
      setErrors({ submit: 'Network error. Please check if the server is running.' });
    } finally {
      setIsSubmitting(false);
    }
  };

  return (
    <div className="auth-container">
      <div className="auth-card">
        <h2>Welcome Back</h2>
        
        <form onSubmit={handleSubmit} className="auth-form">
          <div className="form-group">
            <label>Email Address *</label>
            <input
              type="email"
              name="email"
              value={formData.email}
              onChange={handleChange}
              className={errors.email ? 'error' : ''}
              placeholder="Enter your email"
            />
            {errors.email && <span className="error-text">{errors.email}</span>}
          </div>

          <div className="form-group">
            <label>Password *</label>
            <input
              type="password"
              name="password"
              value={formData.password}
              onChange={handleChange}
              className={errors.password ? 'error' : ''}
              placeholder="Enter your password"
            />
            {errors.password && <span className="error-text">{errors.password}</span>}
          </div>

          {errors.submit && (
            <div className="error-message specific-error">
              {errors.submit}
            </div>
          )}

          <button 
            type="submit" 
            className="auth-button"
            disabled={isSubmitting}
          >
            {isSubmitting ? 'Signing In...' : 'Sign In'}
          </button>
        </form>

        <p className="auth-link">
          Don't have an account? <Link to="/register">Create Account</Link>
        </p>

        <div className="demo-accounts">
          <h4>Working Demo Accounts:</h4>
          <div className="demo-account">
            <strong>Admin:</strong> admin@edulist.com / admin123
          </div>
          <div className="demo-account">
            <strong>User:</strong> rahul@example.com / password123
          </div>
          <div className="demo-account">
            <strong>User:</strong> jain@gmail.com / password123
          </div>
          <div className="demo-account">
            <strong>Institute:</strong> info@dpsdelhi.com / password123
          </div>
          <div className="demo-account">
            <strong>Institute:</strong> contact@stxavier.com / password123
          </div>
        </div>
      </div>
    </div>
  );
};

export default Login;
