import React, { createContext, useState, useContext, useEffect } from 'react';
import { authAPI } from '../services/api';

const AuthContext = createContext();

export const useAuth = () => {
  const context = useContext(AuthContext);
  if (!context) {
    throw new Error('useAuth must be used within an AuthProvider');
  }
  return context;
};

export const AuthProvider = ({ children }) => {
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(true);
  const [token, setToken] = useState(localStorage.getItem('token'));

  useEffect(() => {
    if (token) {
      fetchUserProfile();
    } else {
      setLoading(false);
    }
  }, [token]);

  const fetchUserProfile = async () => {
    try {
      console.log('ðŸ”„ Fetching user profile...');
      const response = await authAPI.getProfile();
      
      if (response.data && response.data.user) {
        setUser(response.data);
        console.log('âœ… User profile loaded:', response.data.user.email);
      } else {
        throw new Error('Invalid profile response');
      }
    } catch (error) {
      console.error('âŒ Error fetching profile:', error);
      logout();
    } finally {
      setLoading(false);
    }
  };

  const login = async (email, password, role) => {
    try {
      console.log('ðŸ”‘ Frontend login attempt:', { email, role });
      
      const response = await authAPI.login(email, password, role);
      const responseData = response.data;
      
      console.log('âœ… Login response:', responseData);

      if (responseData.success && responseData.token) {
        const { token: newToken, user: userData } = responseData;
        
        localStorage.setItem('token', newToken);
        setToken(newToken);
        setUser({ user: userData }); // Store in consistent format
        
        console.log('ðŸŽ‰ Login successful for:', userData.email);
        return { success: true, data: userData };
      } else {
        return { 
          success: false, 
          message: responseData.message || 'Login failed' 
        };
      }
    } catch (error) {
      console.error('âŒ Frontend login error:', error);
      const errorMessage = error.message || 'Login failed. Please try again.';
      return { success: false, message: errorMessage };
    }
  };

  const register = async (userData) => {
    try {
      console.log('ðŸ“ Frontend registration attempt:', userData);
      
      const response = await authAPI.register(userData);
      const responseData = response.data;
      
      console.log('âœ… Registration response:', responseData);

      if (responseData.success) {
        const { token: newToken, user: registeredUser } = responseData;
        
        // Only set token and login if it's not an institute (institutes need approval)
        if (newToken) {
          localStorage.setItem('token', newToken);
          setToken(newToken);
          setUser({ user: registeredUser });
        }
        
        console.log('ðŸŽ‰ Registration successful for:', registeredUser.email);
        return { 
          success: true, 
          data: registeredUser,
          message: responseData.message
        };
      } else {
        return { 
          success: false, 
          message: responseData.message || 'Registration failed' 
        };
      }
    } catch (error) {
      console.error('âŒ Frontend registration error:', error);
      const errorMessage = error.message || 'Registration failed. Please try again.';
      return { success: false, message: errorMessage };
    }
  };

  const logout = () => {
    console.log('ðŸšª Logging out user');
    localStorage.removeItem('token');
    setToken(null);
    setUser(null);
  };

  const updateUser = (userData) => {
    setUser(prev => ({ ...prev, ...userData }));
  };

  const value = {
    user,
    token,
    login,
    register,
    logout,
    updateUser,
    loading,
    isAuthenticated: !!token && !!user,
    refetchProfile: fetchUserProfile
  };

  return (
    <AuthContext.Provider value={value}>
      {children}
    </AuthContext.Provider>
  );
};
