import React, { useState, useEffect } from 'react';
import { Routes, Route, Navigate } from 'react-router-dom';
import InstituteSidebar from './InstituteSidebar';
import ProfileManagement from './ProfileManagement';
import CourseManagement from './CourseManagement';
import FacilitiesManagement from './FacilitiesManagement';
import Enquiries from './Enquiries';
import Reviews from './Reviews';
import { useAuth } from '../../context/AuthContext';
import { instituteAPI } from '../../services/api';
import './InstituteDashboard.css';

const InstituteDashboard = () => {
  const { user } = useAuth();
  const [institute, setInstitute] = useState(null);
  const [loading, setLoading] = useState(true);
  const [stats, setStats] = useState({
    enquiries: 0,
    reviews: 0,
    courses: 0,
    facilities: 0
  });

  useEffect(() => {
    fetchInstituteData();
  }, []);

  const fetchInstituteData = async () => {
    try {
      const response = await instituteAPI.getMyInstitute();
      setInstitute(response.data);
      
      // Fetch stats
      const [coursesRes, facilitiesRes, enquiriesRes, reviewsRes] = await Promise.all([
        instituteAPI.getMyCourses(),
        instituteAPI.getMyFacilities(),
        instituteAPI.getMyEnquiries(),
        instituteAPI.getMyReviews()
      ]);

      setStats({
        courses: coursesRes.data.length,
        facilities: facilitiesRes.data.length,
        enquiries: enquiriesRes.data.length,
        reviews: reviewsRes.data.length
      });
      
      setLoading(false);
    } catch (error) {
      console.error('Error fetching institute data:', error);
      setLoading(false);
    }
  };

  if (loading) {
    return <div className="loading">Loading your institute dashboard...</div>;
  }

  if (!institute) {
    return <div className="error-message">Institute profile not found. Please contact admin.</div>;
  }

  return (
    <div className="institute-dashboard">
      <InstituteSidebar institute={institute} />
      <div className="dashboard-content">
        <Routes>
          <Route path="/" element={<DashboardHome stats={stats} institute={institute} />} />
          <Route path="/profile" element={<ProfileManagement institute={institute} onUpdate={fetchInstituteData} />} />
          <Route path="/courses" element={<CourseManagement institute={institute} />} />
          <Route path="/facilities" element={<FacilitiesManagement institute={institute} />} />
          <Route path="/enquiries" element={<Enquiries institute={institute} />} />
          <Route path="/reviews" element={<Reviews institute={institute} />} />
          <Route path="*" element={<Navigate to="/institute" replace />} />
        </Routes>
      </div>
    </div>
  );
};

const DashboardHome = ({ stats, institute }) => {
  return (
    <div className="dashboard-home">
      <div className="dashboard-header">
        <h1>Welcome, {institute.name}</h1>
        <p>Manage your institute profile and activities</p>
        <div className="status-badge approved">
          Status: {institute.status.charAt(0).toUpperCase() + institute.status.slice(1)}
        </div>
      </div>

      <div className="stats-grid">
        <div className="stat-card">
          <div className="stat-icon">üìß</div>
          <div className="stat-info">
            <h3>{stats.enquiries}</h3>
            <p>New Enquiries</p>
          </div>
        </div>

        <div className="stat-card">
          <div className="stat-icon">‚≠ê</div>
          <div className="stat-info">
            <h3>{stats.reviews}</h3>
            <p>Total Reviews</p>
          </div>
        </div>

        <div className="stat-card">
          <div className="stat-icon">üìö</div>
          <div className="stat-info">
            <h3>{stats.courses}</h3>
            <p>Courses</p>
          </div>
        </div>

        <div className="stat-card">
          <div className="stat-icon">‚öôÔ∏è</div>
          <div className="stat-info">
            <h3>{stats.facilities}</h3>
            <p>Facilities</p>
          </div>
        </div>
      </div>

      <div className="recent-activities">
        <h2>Quick Actions</h2>
        <div className="actions-grid">
          <div className="action-item" onClick={() => window.location.href = '/institute/profile'}>
            <div className="action-icon">üè´</div>
            <div className="action-content">
              <h3>Update Profile</h3>
              <p>Manage your institute information</p>
            </div>
          </div>

          <div className="action-item" onClick={() => window.location.href = '/institute/courses'}>
            <div className="action-icon">üìö</div>
            <div className="action-content">
              <h3>Manage Courses</h3>
              <p>Add and edit courses</p>
            </div>
          </div>

          <div className="action-item" onClick={() => window.location.href = '/institute/facilities'}>
            <div className="action-icon">‚öôÔ∏è</div>
            <div className="action-content">
              <h3>Manage Facilities</h3>
              <p>Update institute facilities</p>
            </div>
          </div>

          <div className="action-item" onClick={() => window.location.href = '/institute/enquiries'}>
            <div className="action-icon">üìß</div>
            <div className="action-content">
              <h3>View Enquiries</h3>
              <p>Check student enquiries</p>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default InstituteDashboard;
