import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { useAuth } from '../../context/AuthContext';
import './Auth.css';

const Register = () => {
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    phone: '',
    password: '',
    confirmPassword: '',
    role: 'user'
  });
  const [instituteData, setInstituteData] = useState({
    name: '',
    category: '',
    affiliation: '',
    address: {
      street: '',
      city: '',
      state: '',
      pincode: ''
    },
    contact: {
      phone: '',
      email: '',
      website: ''
    },
    description: '',
    facilities: []
  });
  const [errors, setErrors] = useState({});
  const [loading, setLoading] = useState(false);
  
  const { register } = useAuth();
  const navigate = useNavigate();

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleInstituteChange = (e) => {
    const { name, value } = e.target;
    
    if (name.includes('.')) {
      const [parent, child] = name.split('.');
      setInstituteData(prev => ({
        ...prev,
        [parent]: {
          ...prev[parent],
          [child]: value
        }
      }));
    } else {
      setInstituteData(prev => ({
        ...prev,
        [name]: value
      }));
    }
  };

  const validateForm = () => {
    const newErrors = {};

    if (!formData.name.trim()) newErrors.name = 'Name is required';
    if (!formData.email.trim()) newErrors.email = 'Email is required';
    if (!formData.phone.trim()) newErrors.phone = 'Phone is required';
    if (formData.password.length < 6) newErrors.password = 'Password must be at least 6 characters';
    if (formData.password !== formData.confirmPassword) newErrors.confirmPassword = 'Passwords do not match';

    if (formData.role === 'institute') {
      if (!instituteData.name.trim()) newErrors.instituteName = 'Institute name is required';
      if (!instituteData.category) newErrors.category = 'Category is required';
      if (!instituteData.affiliation.trim()) newErrors.affiliation = 'Affiliation is required';
      if (!instituteData.address.city.trim()) newErrors.city = 'City is required';
      if (!instituteData.address.state.trim()) newErrors.state = 'State is required';
      if (!instituteData.description.trim()) newErrors.description = 'Description is required';
    }

    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    
    if (!validateForm()) return;

    setLoading(true);
    
    const registrationData = {
      ...formData,
      instituteData: formData.role === 'institute' ? instituteData : undefined
    };

    const result = await register(registrationData);
    
    if (result.success) {
      navigate(formData.role === 'admin' ? '/admin/dashboard' : 
               formData.role === 'institute' ? '/institute/dashboard' : '/');
    } else {
      setErrors({ submit: result.message });
    }
    
    setLoading(false);
  };

  return (
    <div className="auth-container">
      <div className="auth-card">
        <h2>Create Account</h2>
        
        <form onSubmit={handleSubmit} className="auth-form">
          <div className="form-group">
            <label>Full Name *</label>
            <input
              type="text"
              name="name"
              value={formData.name}
              onChange={handleChange}
              className={errors.name ? 'error' : ''}
            />
            {errors.name && <span className="error-text">{errors.name}</span>}
          </div>

          <div className="form-group">
            <label>Email *</label>
            <input
              type="email"
              name="email"
              value={formData.email}
              onChange={handleChange}
              className={errors.email ? 'error' : ''}
            />
            {errors.email && <span className="error-text">{errors.email}</span>}
          </div>

          <div className="form-group">
            <label>Phone *</label>
            <input
              type="tel"
              name="phone"
              value={formData.phone}
              onChange={handleChange}
              className={errors.phone ? 'error' : ''}
            />
            {errors.phone && <span className="error-text">{errors.phone}</span>}
          </div>

          <div className="form-group">
            <label>Password *</label>
            <input
              type="password"
              name="password"
              value={formData.password}
              onChange={handleChange}
              className={errors.password ? 'error' : ''}
            />
            {errors.password && <span className="error-text">{errors.password}</span>}
          </div>

          <div className="form-group">
            <label>Confirm Password *</label>
            <input
              type="password"
              name="confirmPassword"
              value={formData.confirmPassword}
              onChange={handleChange}
              className={errors.confirmPassword ? 'error' : ''}
            />
            {errors.confirmPassword && <span className="error-text">{errors.confirmPassword}</span>}
          </div>

          <div className="form-group">
            <label>Register As *</label>
            <select
              name="role"
              value={formData.role}
              onChange={handleChange}
            >
              <option value="user">Student/Parent</option>
              <option value="institute">Institute</option>
            </select>
          </div>

          {formData.role === 'institute' && (
            <div className="institute-fields">
              <h3>Institute Information</h3>
              
              <div className="form-group">
                <label>Institute Name *</label>
                <input
                  type="text"
                  name="name"
                  value={instituteData.name}
                  onChange={handleInstituteChange}
                  className={errors.instituteName ? 'error' : ''}
                />
                {errors.instituteName && <span className="error-text">{errors.instituteName}</span>}
              </div>

              <div className="form-group">
                <label>Category *</label>
                <select
                  name="category"
                  value={instituteData.category}
                  onChange={handleInstituteChange}
                  className={errors.category ? 'error' : ''}
                >
                  <option value="">Select Category</option>
                  <option value="school">School</option>
                  <option value="college">College</option>
                  <option value="coaching">Coaching Center</option>
                  <option value="preschool">Preschool</option>
                  <option value="university">University</option>
                </select>
                {errors.category && <span className="error-text">{errors.category}</span>}
              </div>

              <div className="form-group">
                <label>Affiliation *</label>
                <input
                  type="text"
                  name="affiliation"
                  value={instituteData.affiliation}
                  onChange={handleInstituteChange}
                  className={errors.affiliation ? 'error' : ''}
                  placeholder="e.g., CBSE, State Board, UGC"
                />
                {errors.affiliation && <span className="error-text">{errors.affiliation}</span>}
              </div>

              <div className="form-row">
                <div className="form-group">
                  <label>City *</label>
                  <input
                    type="text"
                    name="address.city"
                    value={instituteData.address.city}
                    onChange={handleInstituteChange}
                    className={errors.city ? 'error' : ''}
                  />
                  {errors.city && <span className="error-text">{errors.city}</span>}
                </div>

                <div className="form-group">
                  <label>State *</label>
                  <input
                    type="text"
                    name="address.state"
                    value={instituteData.address.state}
                    onChange={handleInstituteChange}
                    className={errors.state ? 'error' : ''}
                  />
                  {errors.state && <span className="error-text">{errors.state}</span>}
                </div>
              </div>

              <div className="form-group">
                <label>Website</label>
                <input
                  type="url"
                  name="contact.website"
                  value={instituteData.contact.website}
                  onChange={handleInstituteChange}
                  placeholder="https://example.com"
                />
              </div>

              <div className="form-group">
                <label>Description *</label>
                <textarea
                  name="description"
                  value={instituteData.description}
                  onChange={handleInstituteChange}
                  className={errors.description ? 'error' : ''}
                  rows="4"
                  placeholder="Describe your institute..."
                />
                {errors.description && <span className="error-text">{errors.description}</span>}
              </div>
            </div>
          )}

          {errors.submit && <div className="error-message">{errors.submit}</div>}

          <button 
            type="submit" 
            className="auth-button" 
            disabled={loading}
          >
            {loading ? 'Creating Account...' : 'Create Account'}
          </button>
        </form>

        <p className="auth-link">
          Already have an account? <Link to="/login">Sign In</Link>
        </p>
      </div>
    </div>
  );
};

export default Register;
