// Add these to the existing api.js file

// Upload API
export const uploadAPI = {
  uploadInstituteImages: (instituteId, formData) => {
    return api.post(`/upload/institute/${instituteId}`, formData, {
      headers: {
        'Content-Type': 'multipart/form-data'
      }
    });
  },
  
  removeGalleryImage: (instituteId, imageUrl) => {
    return api.delete(`/upload/institute/${instituteId}/gallery`, {
      data: { imageUrl }
    });
  }
};
