import React, { useState, useEffect } from 'react';
import { courseAPI } from '../../services/api';
import { useAuth } from '../../context/AuthContext';

const CourseManagement = () => {
  const [courses, setCourses] = useState([]);
  const [showForm, setShowForm] = useState(false);
  const [editingCourse, setEditingCourse] = useState(null);
  const [loading, setLoading] = useState(false);
  const { user } = useAuth();

  const [formData, setFormData] = useState({
    title: '',
    description: '',
    duration: '',
    fees: '',
    category: '',
    eligibility: '',
    syllabus: ''
  });

  useEffect(() => {
    fetchCourses();
  }, []);

  const fetchCourses = async () => {
    try {
      const response = await courseAPI.getByInstitute(user.institute._id);
      setCourses(response.data);
    } catch (error) {
      console.error('Error fetching courses:', error);
    }
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);

    try {
      if (editingCourse) {
        await courseAPI.update(editingCourse._id, formData);
      } else {
        await courseAPI.create(user.institute._id, formData);
      }
      
      setShowForm(false);
      setEditingCourse(null);
      setFormData({
        title: '',
        description: '',
        duration: '',
        fees: '',
        category: '',
        eligibility: '',
        syllabus: ''
      });
      fetchCourses();
    } catch (error) {
      alert('Error saving course: ' + error.message);
    } finally {
      setLoading(false);
    }
  };

  const handleEdit = (course) => {
    setEditingCourse(course);
    setFormData({
      title: course.title,
      description: course.description,
      duration: course.duration,
      fees: course.fees,
      category: course.category,
      eligibility: course.eligibility || '',
      syllabus: course.syllabus || ''
    });
    setShowForm(true);
  };

  const handleDelete = async (courseId) => {
    if (window.confirm('Are you sure you want to delete this course?')) {
      try {
        await courseAPI.delete(courseId);
        fetchCourses();
      } catch (error) {
        alert('Error deleting course: ' + error.message);
      }
    }
  };

  return (
    <div className="course-management">
      <div className="section-header">
        <h2>Course Management</h2>
        <button 
          className="btn btn-primary"
          onClick={() => {
            setShowForm(true);
            setEditingCourse(null);
            setFormData({
              title: '',
              description: '',
              duration: '',
              fees: '',
              category: '',
              eligibility: '',
              syllabus: ''
            });
          }}
        >
          + Add New Course
        </button>
      </div>

      {showForm && (
        <div className="modal-overlay">
          <div className="modal-content">
            <div className="modal-header">
              <h3>{editingCourse ? 'Edit Course' : 'Add New Course'}</h3>
              <button 
                className="close-btn"
                onClick={() => setShowForm(false)}
              >
                ×
              </button>
            </div>
            
            <form onSubmit={handleSubmit} className="course-form">
              <div className="form-row">
                <div className="form-group">
                  <label>Course Title *</label>
                  <input
                    type="text"
                    name="title"
                    value={formData.title}
                    onChange={handleChange}
                    required
                  />
                </div>
                <div className="form-group">
                  <label>Category *</label>
                  <select
                    name="category"
                    value={formData.category}
                    onChange={handleChange}
                    required
                  >
                    <option value="">Select Category</option>
                    <option value="Engineering">Engineering</option>
                    <option value="Medical">Medical</option>
                    <option value="Arts">Arts</option>
                    <option value="Science">Science</option>
                    <option value="Commerce">Commerce</option>
                    <option value="Diploma">Diploma</option>
                    <option value="Certificate">Certificate</option>
                  </select>
                </div>
              </div>

              <div className="form-row">
                <div className="form-group">
                  <label>Duration *</label>
                  <input
                    type="text"
                    name="duration"
                    value={formData.duration}
                    onChange={handleChange}
                    required
                    placeholder="e.g., 4 Years, 6 Months"
                  />
                </div>
                <div className="form-group">
                  <label>Fees (₹) *</label>
                  <input
                    type="number"
                    name="fees"
                    value={formData.fees}
                    onChange={handleChange}
                    required
                  />
                </div>
              </div>

              <div className="form-group">
                <label>Eligibility</label>
                <input
                  type="text"
                  name="eligibility"
                  value={formData.eligibility}
                  onChange={handleChange}
                  placeholder="e.g., 12th Pass with 60%"
                />
              </div>

              <div className="form-group">
                <label>Course Description *</label>
                <textarea
                  name="description"
                  value={formData.description}
                  onChange={handleChange}
                  required
                  rows="4"
                />
              </div>

              <div className="form-group">
                <label>Syllabus (one per line)</label>
                <textarea
                  name="syllabus"
                  value={formData.syllabus}
                  onChange={handleChange}
                  rows="4"
                  placeholder="Enter syllabus topics, one per line"
                />
              </div>

              <div className="form-actions">
                <button
                  type="button"
                  className="btn btn-outline"
                  onClick={() => setShowForm(false)}
                >
                  Cancel
                </button>
                <button
                  type="submit"
                  className="btn btn-primary"
                  disabled={loading}
                >
                  {loading ? 'Saving...' : (editingCourse ? 'Update' : 'Create')}
                </button>
              </div>
            </form>
          </div>
        </div>
      )}

      <div className="courses-grid">
        {courses.length === 0 ? (
          <div className="empty-state">
            <p>No courses added yet.</p>
            <button 
              className="btn btn-primary"
              onClick={() => setShowForm(true)}
            >
              Add Your First Course
            </button>
          </div>
        ) : (
          courses.map(course => (
            <div key={course._id} className="course-card">
              <div className="course-header">
                <h3>{course.title}</h3>
                <span className="course-category">{course.category}</span>
              </div>
              
              <div className="course-details">
                <p><strong>Duration:</strong> {course.duration}</p>
                <p><strong>Fees:</strong> ₹{course.fees.toLocaleString()}</p>
                {course.eligibility && (
                  <p><strong>Eligibility:</strong> {course.eligibility}</p>
                )}
              </div>

              <p className="course-description">{course.description}</p>

              {course.syllabus && course.syllabus.length > 0 && (
                <div className="syllabus-preview">
                  <strong>Syllabus:</strong>
                  <ul>
                    {course.syllabus.split('\n').slice(0, 3).map((item, index) => (
                      <li key={index}>{item}</li>
                    ))}
                    {course.syllabus.split('\n').length > 3 && (
                      <li>... and {course.syllabus.split('\n').length - 3} more</li>
                    )}
                  </ul>
                </div>
              )}

              <div className="course-actions">
                <button
                  className="btn btn-outline btn-sm"
                  onClick={() => handleEdit(course)}
                >
                  Edit
                </button>
                <button
                  className="btn btn-danger btn-sm"
                  onClick={() => handleDelete(course._id)}
                >
                  Delete
                </button>
              </div>
            </div>
          ))
        )}
      </div>
    </div>
  );
};

export default CourseManagement;
