import React, { useState, useEffect } from 'react';
import { Routes, Route, useNavigate, useLocation } from 'react-router-dom';
import { useAuth } from '../../context/AuthContext';
import api from '../../services/api';
import InstituteSidebar from './InstituteSidebar';
import ProfileManagement from './ProfileManagement';
import CourseManagement from './CourseManagement';
import FacilitiesManagement from './FacilitiesManagement';
import Enquiries from './Enquiries';
import Reviews from './Reviews';
import './InstituteDashboard.css';

const InstituteDashboard = () => {
  const [institute, setInstitute] = useState(null);
  const [dashboardData, setDashboardData] = useState(null);
  const [loading, setLoading] = useState(true);
  const { user } = useAuth();
  const navigate = useNavigate();
  const location = useLocation();

  useEffect(() => {
    fetchInstituteData();
  }, []);

  const fetchInstituteData = async () => {
    try {
      // Get institute profile
      const profileResponse = await api.get('/users/profile');
      if (profileResponse.data.institute) {
        setInstitute(profileResponse.data.institute);
        
        // Get dashboard data
        const dashboardResponse = await api.get(`/institutes/${profileResponse.data.institute._id}/dashboard`);
        setDashboardData(dashboardResponse.data);
      }
    } catch (error) {
      console.error('Error fetching institute data:', error);
    } finally {
      setLoading(false);
    }
  };

  if (loading) {
    return (
      <div className="dashboard-loading">
        <div className="spinner"></div>
        <p>Loading your dashboard...</p>
      </div>
    );
  }

  if (!institute) {
    return (
      <div className="container">
        <div className="error-state">
          <h2>Institute not found</h2>
          <p>Please contact administrator if you believe this is an error.</p>
        </div>
      </div>
    );
  }

  return (
    <div className="dashboard">
      <InstituteSidebar institute={institute} />
      
      <div className="dashboard-content">
        <div className="dashboard-header">
          <h1>Welcome back, {institute.name}!</h1>
          <p>Manage your institute profile and track your performance</p>
        </div>

        {location.pathname === '/institute-dashboard' && (
          <DashboardOverview data={dashboardData} />
        )}

        <Routes>
          <Route path="profile" element={
            <ProfileManagement institute={institute} onUpdate={fetchInstituteData} />
          } />
          <Route path="courses" element={
            <CourseManagement institute={institute} />
          } />
          <Route path="facilities" element={
            <FacilitiesManagement institute={institute} onUpdate={fetchInstituteData} />
          } />
          <Route path="enquiries" element={
            <Enquiries institute={institute} />
          } />
          <Route path="reviews" element={
            <Reviews institute={institute} />
          } />
        </Routes>
      </div>
    </div>
  );
};

const DashboardOverview = ({ data }) => {
  if (!data) return <div>Loading dashboard data...</div>;

  const { institute, recentEnquiries, recentReviews, coursesCount, enquiriesCount, reviewsCount } = data;

  return (
    <div className="dashboard-overview">
      {/* Stats Grid */}
      <div className="stats-grid">
        <div className="stat-card">
          <div className="stat-icon">üìö</div>
          <div className="stat-info">
            <div className="stat-number">{coursesCount}</div>
            <div className="stat-label">Total Courses</div>
          </div>
        </div>
        
        <div className="stat-card">
          <div className="stat-icon">üìß</div>
          <div className="stat-info">
            <div className="stat-number">{enquiriesCount}</div>
            <div className="stat-label">Total Enquiries</div>
          </div>
        </div>
        
        <div className="stat-card">
          <div className="stat-icon">‚≠ê</div>
          <div className="stat-info">
            <div className="stat-number">{reviewsCount}</div>
            <div className="stat-label">Total Reviews</div>
          </div>
        </div>
        
        <div className="stat-card">
          <div className="stat-icon">üèÜ</div>
          <div className="stat-info">
            <div className="stat-number">{institute.rating || 'N/A'}</div>
            <div className="stat-label">Average Rating</div>
          </div>
        </div>
      </div>

      {/* Recent Activity */}
      <div className="dashboard-grid">
        <div className="recent-card">
          <h3>Recent Enquiries</h3>
          {recentEnquiries.length === 0 ? (
            <p className="no-data">No recent enquiries</p>
          ) : (
            <div className="activity-list">
              {recentEnquiries.slice(0, 5).map((enquiry) => (
                <div key={enquiry._id} className="activity-item">
                  <div className="activity-content">
                    <strong>{enquiry.user.name}</strong> sent an enquiry
                    <div className="activity-time">
                      {new Date(enquiry.createdAt).toLocaleDateString()} at{' '}
                      {new Date(enquiry.createdAt).toLocaleTimeString()}
                    </div>
                  </div>
                  <div className={`activity-status ${enquiry.status}`}>
                    {enquiry.status}
                  </div>
                </div>
              ))}
            </div>
          )}
          <button 
            onClick={() => window.location.href = '/institute-dashboard/enquiries'}
            className="btn btn-outline btn-sm"
          >
            View All Enquiries
          </button>
        </div>

        <div className="recent-card">
          <h3>Recent Reviews</h3>
          {recentReviews.length === 0 ? (
            <p className="no-data">No recent reviews</p>
          ) : (
            <div className="activity-list">
              {recentReviews.slice(0, 5).map((review) => (
                <div key={review._id} className="activity-item">
                  <div className="activity-content">
                    <strong>{review.user.name}</strong> gave {review.rating} stars
                    <div className="activity-review">
                      {review.reviewText.length > 50 
                        ? `${review.reviewText.substring(0, 50)}...`
                        : review.reviewText
                      }
                    </div>
                    <div className="activity-time">
                      {new Date(review.createdAt).toLocaleDateString()}
                    </div>
                  </div>
                </div>
              ))}
            </div>
          )}
          <button 
            onClick={() => window.location.href = '/institute-dashboard/reviews'}
            className="btn btn-outline btn-sm"
          >
            View All Reviews
          </button>
        </div>
      </div>
    </div>
  );
};

export default InstituteDashboard;
