import React from 'react';
import { Link, useLocation, useNavigate } from 'react-router-dom';
import { useAuth } from '../../context/AuthContext';
import './AdminPanel.css';

const AdminSidebar = () => {
  const location = useLocation();
  const navigate = useNavigate();
  const { logout, user } = useAuth();

  const handleLogout = () => {
    logout();
    navigate('/');
  };

  const menuItems = [
    { path: '/admin/dashboard', icon: 'fas fa-tachometer-alt', label: 'Dashboard' },
    { path: '/admin/institutes', icon: 'fas fa-school', label: 'Manage Institutes' },
    { path: '/admin/users', icon: 'fas fa-users', label: 'Manage Users' },
    { path: '/admin/analytics', icon: 'fas fa-chart-bar', label: 'Analytics' },
    { path: '/admin/reviews', icon: 'fas fa-star', label: 'Moderate Reviews' },
  ];

  return (
    <div className="dashboard-sidebar admin-sidebar">
      <div className="sidebar-header">
        <div className="admin-info">
          <div className="admin-avatar">
            {user?.name?.charAt(0) || 'A'}
          </div>
          <div className="admin-details">
            <h4>{user?.name}</h4>
            <span className="role">Administrator</span>
          </div>
        </div>
      </div>

      <nav className="sidebar-nav">
        {menuItems.map(item => (
          <Link
            key={item.path}
            to={item.path}
            className={`nav-item ${location.pathname === item.path ? 'active' : ''}`}
          >
            <i className={item.icon}></i>
            <span>{item.label}</span>
          </Link>
        ))}
      </nav>

      <div className="sidebar-footer">
        <button onClick={handleLogout} className="logout-btn">
          <i className="fas fa-sign-out-alt"></i>
          <span>Logout</span>
        </button>
      </div>
    </div>
  );
};

export default AdminSidebar;
