import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { useAuth } from '../../context/AuthContext';
import { toast } from '../../utils/toast';
import './Auth.css';

const Register = () => {
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    phone: '',
    password: '',
    confirmPassword: '',
    role: 'user',
    // Institute specific fields
    instituteName: '',
    category: '',
    affiliation: '',
    street: '',
    city: '',
    state: '',
    pincode: '',
    contactPhone: '',
    contactEmail: '',
    website: '',
    description: '',
    minFee: '',
    maxFee: ''
  });
  const [errors, setErrors] = useState({});
  const [loading, setLoading] = useState(false);
  const { register } = useAuth();
  const navigate = useNavigate();

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
    if (errors[e.target.name]) {
      setErrors({
        ...errors,
        [e.target.name]: ''
      });
    }
  };

  const validateForm = () => {
    const newErrors = {};

    if (!formData.name.trim()) newErrors.name = 'Name is required';
    if (!formData.email.trim()) newErrors.email = 'Email is required';
    else if (!/\S+@\S+\.\S+/.test(formData.email)) newErrors.email = 'Email is invalid';
    
    if (!formData.phone.trim()) newErrors.phone = 'Phone is required';
    if (!formData.password) newErrors.password = 'Password is required';
    else if (formData.password.length < 6) newErrors.password = 'Password must be at least 6 characters';
    
    if (formData.password !== formData.confirmPassword) newErrors.confirmPassword = 'Passwords do not match';

    // Institute specific validations
    if (formData.role === 'institute') {
      if (!formData.instituteName.trim()) newErrors.instituteName = 'Institute name is required';
      if (!formData.category) newErrors.category = 'Category is required';
      if (!formData.affiliation.trim()) newErrors.affiliation = 'Affiliation is required';
      if (!formData.city.trim()) newErrors.city = 'City is required';
      if (!formData.state.trim()) newErrors.state = 'State is required';
      if (!formData.description.trim()) newErrors.description = 'Description is required';
    }

    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    
    if (!validateForm()) return;

    setLoading(true);
    try {
      const userData = {
        name: formData.name,
        email: formData.email,
        phone: formData.phone,
        password: formData.password,
        role: formData.role
      };

      if (formData.role === 'institute') {
        userData.instituteData = {
          name: formData.instituteName,
          category: formData.category,
          affiliation: formData.affiliation,
          address: {
            street: formData.street,
            city: formData.city,
            state: formData.state,
            pincode: formData.pincode
          },
          contact: {
            phone: formData.contactPhone || formData.phone,
            email: formData.contactEmail || formData.email,
            website: formData.website
          },
          description: formData.description,
          feeRange: {
            min: parseInt(formData.minFee) || 0,
            max: parseInt(formData.maxFee) || 0
          }
        };
      }

      console.log('Sending registration data:', userData);
      
      const result = await register(userData);
      if (result.success) {
        if (formData.role === 'institute') {
          toast.success('Institute registered successfully! Waiting for admin approval.');
          navigate('/');
        } else {
          toast.success('Registration successful!');
          navigate('/');
        }
      }
    } catch (error) {
      console.error('Registration error:', error);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="auth-container">
      <div className="auth-card">
        <h2>Create Account</h2>
        <form onSubmit={handleSubmit}>
          <div className="form-group">
            <label className="form-label">Full Name *</label>
            <input
              type="text"
              name="name"
              value={formData.name}
              onChange={handleChange}
              className={`form-control ${errors.name ? 'error' : ''}`}
              placeholder="Enter your full name"
              required
            />
            {errors.name && <span className="error">{errors.name}</span>}
          </div>

          <div className="form-group">
            <label className="form-label">Email *</label>
            <input
              type="email"
              name="email"
              value={formData.email}
              onChange={handleChange}
              className={`form-control ${errors.email ? 'error' : ''}`}
              placeholder="Enter your email"
              required
            />
            {errors.email && <span className="error">{errors.email}</span>}
          </div>

          <div className="form-group">
            <label className="form-label">Phone *</label>
            <input
              type="tel"
              name="phone"
              value={formData.phone}
              onChange={handleChange}
              className={`form-control ${errors.phone ? 'error' : ''}`}
              placeholder="Enter your phone number"
              required
            />
            {errors.phone && <span className="error">{errors.phone}</span>}
          </div>

          <div className="form-group">
            <label className="form-label">Role *</label>
            <select
              name="role"
              value={formData.role}
              onChange={handleChange}
              className="form-control"
              required
            >
              <option value="user">Student/Parent</option>
              <option value="institute">Institute</option>
            </select>
          </div>

          {formData.role === 'institute' && (
            <>
              <div className="form-section">
                <h3>Institute Information</h3>
                
                <div className="form-group">
                  <label className="form-label">Institute Name *</label>
                  <input
                    type="text"
                    name="instituteName"
                    value={formData.instituteName}
                    onChange={handleChange}
                    className={`form-control ${errors.instituteName ? 'error' : ''}`}
                    placeholder="Enter institute name"
                    required
                  />
                  {errors.instituteName && <span className="error">{errors.instituteName}</span>}
                </div>

                <div className="form-grid">
                  <div className="form-group">
                    <label className="form-label">Category *</label>
                    <select
                      name="category"
                      value={formData.category}
                      onChange={handleChange}
                      className={`form-control ${errors.category ? 'error' : ''}`}
                      required
                    >
                      <option value="">Select Category</option>
                      <option value="school">School</option>
                      <option value="college">College</option>
                      <option value="coaching">Coaching Center</option>
                      <option value="preschool">Preschool</option>
                      <option value="university">University</option>
                    </select>
                    {errors.category && <span className="error">{errors.category}</span>}
                  </div>

                  <div className="form-group">
                    <label className="form-label">Affiliation *</label>
                    <input
                      type="text"
                      name="affiliation"
                      value={formData.affiliation}
                      onChange={handleChange}
                      className={`form-control ${errors.affiliation ? 'error' : ''}`}
                      placeholder="e.g., CBSE, State Board, UGC"
                      required
                    />
                    {errors.affiliation && <span className="error">{errors.affiliation}</span>}
                  </div>
                </div>

                <div className="form-group">
                  <label className="form-label">Description *</label>
                  <textarea
                    name="description"
                    value={formData.description}
                    onChange={handleChange}
                    className={`form-control ${errors.description ? 'error' : ''}`}
                    rows="4"
                    placeholder="Describe your institute, facilities, achievements, etc."
                    required
                  />
                  {errors.description && <span className="error">{errors.description}</span>}
                </div>

                <div className="form-grid">
                  <div className="form-group">
                    <label className="form-label">Minimum Fee (₹)</label>
                    <input
                      type="number"
                      name="minFee"
                      value={formData.minFee}
                      onChange={handleChange}
                      className="form-control"
                      placeholder="0"
                      min="0"
                    />
                  </div>

                  <div className="form-group">
                    <label className="form-label">Maximum Fee (₹)</label>
                    <input
                      type="number"
                      name="maxFee"
                      value={formData.maxFee}
                      onChange={handleChange}
                      className="form-control"
                      placeholder="0"
                      min="0"
                    />
                  </div>
                </div>
              </div>

              <div className="form-section">
                <h3>Address Information</h3>
                <div className="form-grid">
                  <div className="form-group">
                    <label className="form-label">Street Address</label>
                    <input
                      type="text"
                      name="street"
                      value={formData.street}
                      onChange={handleChange}
                      className="form-control"
                      placeholder="Street address"
                    />
                  </div>

                  <div className="form-group">
                    <label className="form-label">City *</label>
                    <input
                      type="text"
                      name="city"
                      value={formData.city}
                      onChange={handleChange}
                      className={`form-control ${errors.city ? 'error' : ''}`}
                      placeholder="City"
                      required
                    />
                    {errors.city && <span className="error">{errors.city}</span>}
                  </div>

                  <div className="form-group">
                    <label className="form-label">State *</label>
                    <input
                      type="text"
                      name="state"
                      value={formData.state}
                      onChange={handleChange}
                      className={`form-control ${errors.state ? 'error' : ''}`}
                      placeholder="State"
                      required
                    />
                    {errors.state && <span className="error">{errors.state}</span>}
                  </div>

                  <div className="form-group">
                    <label className="form-label">Pincode</label>
                    <input
                      type="text"
                      name="pincode"
                      value={formData.pincode}
                      onChange={handleChange}
                      className="form-control"
                      placeholder="Pincode"
                    />
                  </div>
                </div>
              </div>

              <div className="form-section">
                <h3>Contact Information</h3>
                <div className="form-grid">
                  <div className="form-group">
                    <label className="form-label">Contact Phone</label>
                    <input
                      type="tel"
                      name="contactPhone"
                      value={formData.contactPhone}
                      onChange={handleChange}
                      className="form-control"
                      placeholder="Contact phone number"
                    />
                  </div>

                  <div className="form-group">
                    <label className="form-label">Contact Email</label>
                    <input
                      type="email"
                      name="contactEmail"
                      value={formData.contactEmail}
                      onChange={handleChange}
                      className="form-control"
                      placeholder="Contact email address"
                    />
                  </div>

                  <div className="form-group">
                    <label className="form-label">Website</label>
                    <input
                      type="url"
                      name="website"
                      value={formData.website}
                      onChange={handleChange}
                      className="form-control"
                      placeholder="https://example.com"
                    />
                  </div>
                </div>
              </div>
            </>
          )}

          <div className="form-section">
            <h3>Account Security</h3>
            <div className="form-grid">
              <div className="form-group">
                <label className="form-label">Password *</label>
                <input
                  type="password"
                  name="password"
                  value={formData.password}
                  onChange={handleChange}
                  className={`form-control ${errors.password ? 'error' : ''}`}
                  placeholder="Enter password (min 6 characters)"
                  required
                />
                {errors.password && <span className="error">{errors.password}</span>}
              </div>

              <div className="form-group">
                <label className="form-label">Confirm Password *</label>
                <input
                  type="password"
                  name="confirmPassword"
                  value={formData.confirmPassword}
                  onChange={handleChange}
                  className={`form-control ${errors.confirmPassword ? 'error' : ''}`}
                  placeholder="Confirm password"
                  required
                />
                {errors.confirmPassword && <span className="error">{errors.confirmPassword}</span>}
              </div>
            </div>
          </div>

          <button 
            type="submit" 
            className="btn btn-primary btn-block"
            disabled={loading}
          >
            {loading ? 'Creating Account...' : 'Create Account'}
          </button>
        </form>

        <p className="auth-link">
          Already have an account? <Link to="/login">Login here</Link>
        </p>
      </div>
    </div>
  );
};

export default Register;
