import React, { createContext, useState, useContext, useEffect } from 'react';

const AuthContext = createContext();

export const useAuth = () => {
  const context = useContext(AuthContext);
  if (!context) {
    throw new Error('useAuth must be used within an AuthProvider');
  }
  return context;
};

export const AuthProvider = ({ children }) => {
  const [user, setUser] = useState(null);
  const [userType, setUserType] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const token = localStorage.getItem('token');
    const storedUserType = localStorage.getItem('userType');
    
    if (token && storedUserType) {
      // In a real app, you would verify the token with the backend
      setUserType(storedUserType);
      setUser({ name: 'User', email: 'user@example.com' }); // Temporary dummy data
    }
    setLoading(false);
  }, []);

  const login = async (email, password, type) => {
    try {
      // Temporary mock login - replace with actual API call
      const mockUser = {
        id: '1',
        name: type === 'institute' ? 'Test Institute' : 'Test User',
        email: email,
        role: type
      };
      
      const mockToken = 'mock_jwt_token';
      
      localStorage.setItem('token', mockToken);
      localStorage.setItem('userType', type);
      
      setUser(mockUser);
      setUserType(type);
      
      return { success: true };
    } catch (error) {
      return { success: false, message: 'Login failed' };
    }
  };

  const register = async (userData, type) => {
    try {
      // Temporary mock registration
      return { success: true, data: { message: 'Registration successful' } };
    } catch (error) {
      return { success: false, message: 'Registration failed' };
    }
  };

  const logout = () => {
    localStorage.removeItem('token');
    localStorage.removeItem('userType');
    setUser(null);
    setUserType(null);
  };

  const value = {
    user,
    userType,
    loading,
    login,
    register,
    logout
  };

  return (
    <AuthContext.Provider value={value}>
      {children}
    </AuthContext.Provider>
  );
};
