import React, { useState, useEffect } from 'react';
import { Routes, Route, useNavigate, useLocation } from 'react-router-dom';
import { useAuth } from '../../context/AuthContext';
import { instituteAPI } from '../../services/api';
import InstituteSidebar from './InstituteSidebar';
import ProfileManagement from './ProfileManagement';
import CourseManagement from './CourseManagement';
import FacilitiesManagement from './FacilitiesManagement';
import Enquiries from './Enquiries';
import Reviews from './Reviews';
import './InstituteDashboard.css';

const InstituteDashboard = () => {
  const { user } = useAuth();
  const navigate = useNavigate();
  const location = useLocation();
  const [institute, setInstitute] = useState(null);
  const [loading, setLoading] = useState(true);
  const [stats, setStats] = useState({
    totalCourses: 0,
    totalEnquiries: 0,
    totalReviews: 0,
    pendingEnquiries: 0
  });

  useEffect(() => {
    fetchInstituteData();
  }, []);

  const fetchInstituteData = async () => {
    try {
      // In a real app, you'd have an endpoint to get institute by user ID
      const response = await instituteAPI.getAll();
      const userInstitute = response.data.find(inst => 
        inst.userId?.email === user.email
      );
      
      if (userInstitute) {
        setInstitute(userInstitute);
        
        // Calculate stats (in real app, these would come from API)
        setStats({
          totalCourses: userInstitute.courses?.length || 0,
          totalEnquiries: 0, // Would come from API
          totalReviews: 0, // Would come from API
          pendingEnquiries: 0 // Would come from API
        });
      }
    } catch (error) {
      console.error('Error fetching institute data:', error);
    } finally {
      setLoading(false);
    }
  };

  if (loading) {
    return <div className="loading">Loading dashboard...</div>;
  }

  if (!institute) {
    return (
      <div className="dashboard-container">
        <div className="pending-approval">
          <h2>Pending Approval</h2>
          <p>Your institute registration is pending admin approval. You'll be able to access the dashboard once approved.</p>
        </div>
      </div>
    );
  }

  return (
    <div className="dashboard-container">
      <InstituteSidebar />
      
      <div className="dashboard-content">
        <div className="dashboard-header">
          <h1>Welcome, {institute.name}</h1>
          <p>Manage your institute profile, courses, and interactions</p>
        </div>
        
        <Routes>
          <Route path="/" element={
            <div className="dashboard-overview">
              <div className="stats-grid">
                <div className="stat-card">
                  <h3>Total Courses</h3>
                  <p className="stat-number">{stats.totalCourses}</p>
                </div>
                <div className="stat-card">
                  <h3>Total Enquiries</h3>
                  <p className="stat-number">{stats.totalEnquiries}</p>
                </div>
                <div className="stat-card">
                  <h3>Total Reviews</h3>
                  <p className="stat-number">{stats.totalReviews}</p>
                </div>
                <div className="stat-card">
                  <h3>Pending Enquiries</h3>
                  <p className="stat-number">{stats.pendingEnquiries}</p>
                </div>
              </div>
              
              <div className="recent-activities">
                <h3>Recent Activities</h3>
                <div className="activity-list">
                  <p>No recent activities</p>
                </div>
              </div>
            </div>
          } />
          <Route path="/profile" element={
            <ProfileManagement institute={institute} onUpdate={fetchInstituteData} />
          } />
          <Route path="/courses" element={
            <CourseManagement institute={institute} onUpdate={fetchInstituteData} />
          } />
          <Route path="/facilities" element={
            <FacilitiesManagement institute={institute} onUpdate={fetchInstituteData} />
          } />
          <Route path="/enquiries" element={<Enquiries />} />
          <Route path="/reviews" element={<Reviews />} />
        </Routes>
      </div>
    </div>
  );
};

export default InstituteDashboard;
