import React, { useState, useEffect } from 'react';
import { instituteService } from '../../services/api';
import './InstituteDashboard.css';

const FacilitiesManagement = () => {
  const [institute, setInstitute] = useState(null);
  const [facilities, setFacilities] = useState([]);
  const [newFacility, setNewFacility] = useState('');
  const [loading, setLoading] = useState(true);
  const [saving, setSaving] = useState(false);
  const [message, setMessage] = useState('');

  const commonFacilities = [
    'Library', 'Computer Lab', 'Science Lab', 'Sports Ground', 
    'Swimming Pool', 'Cafeteria', 'Auditorium', 'Hostel',
    'Transport', 'Medical Room', 'WiFi', 'Smart Classes'
  ];

  useEffect(() => {
    fetchInstituteData();
  }, []);

  const fetchInstituteData = async () => {
    try {
      const response = await instituteService.getCurrentInstitute();
      setInstitute(response.data);
      setFacilities(response.data.facilities || []);
      setLoading(false);
    } catch (error) {
      console.error('Error fetching institute data:', error);
      setLoading(false);
    }
  };

  const handleAddFacility = () => {
    if (newFacility.trim() && !facilities.includes(newFacility.trim())) {
      const updatedFacilities = [...facilities, newFacility.trim()];
      setFacilities(updatedFacilities);
      setNewFacility('');
      saveFacilities(updatedFacilities);
    }
  };

  const handleRemoveFacility = (facilityToRemove) => {
    const updatedFacilities = facilities.filter(f => f !== facilityToRemove);
    setFacilities(updatedFacilities);
    saveFacilities(updatedFacilities);
  };

  const handleAddCommonFacility = (facility) => {
    if (!facilities.includes(facility)) {
      const updatedFacilities = [...facilities, facility];
      setFacilities(updatedFacilities);
      saveFacilities(updatedFacilities);
    }
  };

  const saveFacilities = async (updatedFacilities) => {
    setSaving(true);
    setMessage('');

    try {
      await instituteService.updateInstitute(institute._id, {
        facilities: updatedFacilities
      });
      setMessage('Facilities updated successfully!');
    } catch (error) {
      setMessage('Error updating facilities. Please try again.');
    } finally {
      setSaving(false);
    }
  };

  if (loading) {
    return <div className="loading">Loading facilities...</div>;
  }

  return (
    <div className="facilities-management">
      <div className="dashboard-section">
        <h2>Manage Facilities</h2>
        <p>Add and manage the facilities available at your institute</p>

        {message && (
          <div className={`message ${message.includes('Error') ? 'error' : 'success'}`}>
            {message}
          </div>
        )}

        {/* Add New Facility */}
        <div className="add-facility-section">
          <h3>Add New Facility</h3>
          <div className="add-facility-form">
            <input
              type="text"
              value={newFacility}
              onChange={(e) => setNewFacility(e.target.value)}
              placeholder="Enter facility name..."
              className="facility-input"
            />
            <button 
              onClick={handleAddFacility}
              disabled={!newFacility.trim() || saving}
              className="btn-primary"
            >
              Add Facility
            </button>
          </div>
        </div>

        {/* Common Facilities */}
        <div className="common-facilities-section">
          <h3>Common Facilities</h3>
          <p>Quickly add commonly used facilities</p>
          <div className="common-facilities-grid">
            {commonFacilities.map(facility => (
              <button
                key={facility}
                onClick={() => handleAddCommonFacility(facility)}
                disabled={facilities.includes(facility) || saving}
                className={`common-facility-btn ${
                  facilities.includes(facility) ? 'added' : ''
                }`}
              >
                {facility}
                {facilities.includes(facility) && ' ✓'}
              </button>
            ))}
          </div>
        </div>

        {/* Current Facilities */}
        <div className="current-facilities-section">
          <h3>Current Facilities ({facilities.length})</h3>
          {facilities.length === 0 ? (
            <div className="no-facilities">
              <p>No facilities added yet. Start by adding some facilities above.</p>
            </div>
          ) : (
            <div className="facilities-list">
              {facilities.map((facility, index) => (
                <div key={index} className="facility-item">
                  <span className="facility-name">{facility}</span>
                  <button
                    onClick={() => handleRemoveFacility(facility)}
                    disabled={saving}
                    className="remove-facility-btn"
                  >
                    ×
                  </button>
                </div>
              ))}
            </div>
          )}
        </div>

        {saving && (
          <div className="saving-indicator">
            Saving changes...
          </div>
        )}
      </div>
    </div>
  );
};

export default FacilitiesManagement;
