import React, { useState } from 'react';
import { enquiryAPI } from '../../services/api';
import { useAuth } from '../../context/AuthContext';

const EnquiryForm = ({ institute, onClose, onSuccess }) => {
  const [formData, setFormData] = useState({
    message: ''
  });
  const [loading, setLoading] = useState(false);
  const { isAuthenticated, user } = useAuth();

  const handleSubmit = async (e) => {
    e.preventDefault();
    
    if (!formData.message.trim()) {
      alert('Please write your enquiry message');
      return;
    }

    if (!isAuthenticated) {
      alert('Please login to send an enquiry');
      return;
    }

    setLoading(true);

    try {
      await enquiryAPI.create({
        institute: institute._id,
        message: formData.message
      });
      
      onSuccess();
    } catch (error) {
      alert('Error sending enquiry: ' + error.message);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="modal-overlay">
      <div className="modal-content">
        <div className="modal-header">
          <h3>Send Enquiry to {institute.name}</h3>
          <button className="close-btn" onClick={onClose}>Ã—</button>
        </div>

        {isAuthenticated && (
          <div className="user-info">
            <p><strong>From:</strong> {user.name} ({user.email})</p>
          </div>
        )}

        <form onSubmit={handleSubmit} className="enquiry-form">
          <div className="form-group">
            <label>Your Message *</label>
            <textarea
              value={formData.message}
              onChange={(e) => setFormData(prev => ({ ...prev, message: e.target.value }))}
              placeholder="Tell the institute what you'd like to know... (e.g., admission process, fees, courses, etc.)"
              rows="6"
              required
            />
          </div>

          <div className="form-actions">
            <button type="button" className="btn btn-outline" onClick={onClose}>
              Cancel
            </button>
            <button 
              type="submit" 
              className="btn btn-primary" 
              disabled={loading || !isAuthenticated}
            >
              {loading ? 'Sending...' : 'Send Enquiry'}
            </button>
          </div>

          {!isAuthenticated && (
            <p className="login-prompt">
              Please <a href="/login">login</a> to send an enquiry.
            </p>
          )}
        </form>
      </div>
    </div>
  );
};

export default EnquiryForm;
