import React, { useState, useEffect } from 'react';
import { adminService } from '../../services/api';
import './AdminPanel.css';

const AnalyticsDashboard = () => {
  const [analytics, setAnalytics] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  useEffect(() => {
    fetchAnalytics();
  }, []);

  const fetchAnalytics = async () => {
    try {
      setLoading(true);
      setError('');
      const response = await adminService.getAnalytics();
      setAnalytics(response.data);
    } catch (error) {
      console.error('Error fetching analytics:', error);
      setError('Failed to load analytics data. Please try again.');
      // Set default empty data
      setAnalytics({
        counts: {
          totalUsers: 0,
          totalInstitutes: 0,
          pendingInstitutes: 0,
          totalReviews: 0,
          totalEnquiries: 0
        },
        recent: {
          institutes: [],
          reviews: []
        }
      });
    } finally {
      setLoading(false);
    }
  };

  if (loading) {
    return (
      <div className="analytics-dashboard">
        <div className="loading">Loading analytics...</div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="analytics-dashboard">
        <div className="error-message">
          <p>{error}</p>
          <button onClick={fetchAnalytics} className="btn-primary">
            Try Again
          </button>
        </div>
      </div>
    );
  }

  if (!analytics) {
    return (
      <div className="analytics-dashboard">
        <div className="no-data">No analytics data available</div>
      </div>
    );
  }

  const { counts, recent } = analytics;

  return (
    <div className="analytics-dashboard">
      <div className="admin-section">
        <h2>Platform Analytics</h2>
        
        {/* Key Metrics */}
        <div className="metrics-grid">
          <div className="metric-card">
            <div className="metric-icon">üë•</div>
            <div className="metric-info">
              <h3>{counts.totalUsers}</h3>
              <p>Total Users</p>
            </div>
          </div>
          
          <div className="metric-card">
            <div className="metric-icon">üè´</div>
            <div className="metric-info">
              <h3>{counts.totalInstitutes}</h3>
              <p>Total Institutes</p>
            </div>
          </div>
          
          <div className="metric-card">
            <div className="metric-icon">‚è≥</div>
            <div className="metric-info">
              <h3>{counts.pendingInstitutes}</h3>
              <p>Pending Approval</p>
            </div>
          </div>
          
          <div className="metric-card">
            <div className="metric-icon">‚≠ê</div>
            <div className="metric-info">
              <h3>{counts.totalReviews}</h3>
              <p>Total Reviews</p>
            </div>
          </div>
          
          <div className="metric-card">
            <div className="metric-icon">üì•</div>
            <div className="metric-info">
              <h3>{counts.totalEnquiries}</h3>
              <p>Total Enquiries</p>
            </div>
          </div>
        </div>

        {/* Recent Activity */}
        <div className="recent-activity-grid">
          <div className="recent-section">
            <h3>Recent Institute Registrations</h3>
            <div className="recent-list">
              {recent.institutes.length === 0 ? (
                <p className="no-data">No recent registrations</p>
              ) : (
                recent.institutes.map(institute => (
                  <div key={institute._id} className="recent-item">
                    <div className="item-info">
                      <strong>{institute.name}</strong>
                      <p>{institute.category} ‚Ä¢ {institute.address?.city}</p>
                      <small>
                        Registered: {new Date(institute.createdAt).toLocaleDateString()}
                      </small>
                    </div>
                    <span className={`status ${institute.verified ? 'verified' : 'pending'}`}>
                      {institute.verified ? 'Verified' : 'Pending'}
                    </span>
                  </div>
                ))
              )}
            </div>
          </div>

          <div className="recent-section">
            <h3>Recent Reviews</h3>
            <div className="recent-list">
              {recent.reviews.length === 0 ? (
                <p className="no-data">No recent reviews</p>
              ) : (
                recent.reviews.map(review => (
                  <div key={review._id} className="recent-item">
                    <div className="item-info">
                      <strong>{review.user?.name}</strong>
                      <p>Rated {review.institute?.name}</p>
                      <div className="rating">
                        {'‚≠ê'.repeat(review.rating)}
                        <span>({review.rating}/5)</span>
                      </div>
                      <small>
                        {new Date(review.createdAt).toLocaleDateString()}
                      </small>
                    </div>
                    <span className={`status ${review.isApproved ? 'approved' : 'pending'}`}>
                      {review.isApproved ? 'Approved' : 'Pending'}
                    </span>
                  </div>
                ))
              )}
            </div>
          </div>
        </div>

        {/* Quick Actions */}
        <div className="quick-actions">
          <h3>Quick Actions</h3>
          <div className="action-buttons">
            <button 
              onClick={() => window.location.href = '/admin/dashboard/institutes'}
              className="btn-primary"
            >
              Manage Institutes
            </button>
            <button 
              onClick={() => window.location.href = '/admin/dashboard/users'}
              className="btn-secondary"
            >
              Manage Users
            </button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default AnalyticsDashboard;
