import React, { useState, useEffect, useCallback } from 'react';
import { Routes, Route, Navigate } from 'react-router-dom';
import InstituteSidebar from './InstituteSidebar';
import ProfileManagement from './ProfileManagement';
import CourseManagement from './CourseManagement';
import FacilitiesManagement from './FacilitiesManagement';
import Enquiries from './Enquiries';
import Reviews from './Reviews';
import { useAuth } from '../../context/AuthContext';
import { instituteAPI } from '../../services/api';
import './InstituteDashboard.css';

const InstituteDashboard = () => {
  const { user } = useAuth();
  const [institute, setInstitute] = useState(null);
  const [stats, setStats] = useState({
    enquiries: 0,
    reviews: 0,
    courses: 0
  });
  const [loading, setLoading] = useState(true);

  const fetchInstituteData = useCallback(async () => {
    try {
      // Get institute by user ID
      const response = await instituteAPI.getByUserId(user.id);
      setInstitute(response.data);
      
      // Fetch stats for dashboard
      try {
        const [coursesRes, reviewsRes, enquiriesRes] = await Promise.all([
          instituteAPI.getByUserId(user.id).then(() => ({ data: [] })), // Mock for now
          instituteAPI.getByUserId(user.id).then(() => ({ data: [] })), // Mock for now
          instituteAPI.getByUserId(user.id).then(() => ({ data: [] }))  // Mock for now
        ]);

        setStats({
          enquiries: enquiriesRes.data.length || 0,
          reviews: reviewsRes.data.length || 0,
          courses: coursesRes.data.length || 0
        });
      } catch (statsError) {
        console.error('Error fetching stats:', statsError);
        // Use default stats if there's an error
        setStats({
          enquiries: 0,
          reviews: 0,
          courses: 0
        });
      }
      
      setLoading(false);
    } catch (error) {
      console.error('Error fetching institute data:', error);
      setLoading(false);
    }
  }, [user.id]);

  useEffect(() => {
    fetchInstituteData();
  }, [fetchInstituteData]);

  if (loading) {
    return (
      <div className="institute-dashboard">
        <div className="dashboard-content">
          <div className="loading">Loading your institute dashboard...</div>
        </div>
      </div>
    );
  }

  if (!institute) {
    return (
      <div className="institute-dashboard">
