import React, { useState, useRef } from 'react';
import './ImageUpload.css';

const ImageUpload = ({ 
  onImagesChange, 
  existingLogo = '', 
  existingBanner = '', 
  existingGallery = [] 
}) => {
  const [logo, setLogo] = useState(existingLogo);
  const [banner, setBanner] = useState(existingBanner);
  const [gallery, setGallery] = useState(existingGallery);
  
  const logoInputRef = useRef(null);
  const bannerInputRef = useRef(null);
  const galleryInputRef = useRef(null);

  const handleImageUpload = (e, type) => {
    const file = e.target.files[0];
    if (!file) return;

    // Validate file type
    if (!file.type.startsWith('image/')) {
      alert('Please select an image file');
      return;
    }

    // Validate file size (5MB)
    if (file.size > 5 * 1024 * 1024) {
      alert('Image size should be less than 5MB');
      return;
    }

    const reader = new FileReader();
    reader.onload = (event) => {
      const imageUrl = event.target.result;
      
      switch (type) {
        case 'logo':
          setLogo(imageUrl);
          break;
        case 'banner':
          setBanner(imageUrl);
          break;
        case 'gallery':
          setGallery(prev => [...prev, imageUrl]);
          break;
        default:
          break;
      }

      // Notify parent component
      onImagesChange({
        logo: type === 'logo' ? imageUrl : logo,
        banner: type === 'banner' ? imageUrl : banner,
        gallery: type === 'gallery' ? [...gallery, imageUrl] : gallery
      });
    };
    reader.readAsDataURL(file);
  };

  const removeImage = (type, index = null) => {
    switch (type) {
      case 'logo':
        setLogo('');
        onImagesChange({ logo: '', banner, gallery });
        break;
      case 'banner':
        setBanner('');
        onImagesChange({ logo, banner: '', gallery });
        break;
      case 'gallery':
        const newGallery = gallery.filter((_, i) => i !== index);
        setGallery(newGallery);
        onImagesChange({ logo, banner, gallery: newGallery });
        break;
      default:
        break;
    }
  };

  const triggerFileInput = (ref) => {
    ref.current?.click();
  };

  return (
    <div className="image-upload-section">
      <h3>Institute Images</h3>
      
      <div className="image-upload-grid">
        {/* Logo Upload */}
        <div className="image-upload-group">
          <label>Institute Logo</label>
          <div 
            className="image-upload-container logo-upload"
            onClick={() => triggerFileInput(logoInputRef)}
          >
            {logo ? (
              <div className="image-preview">
                <img src={logo} alt="Institute logo" />
                <button 
                  type="button" 
                  className="remove-image"
                  onClick={(e) => {
                    e.stopPropagation();
                    removeImage('logo');
                  }}
                >
                  √ó
                </button>
              </div>
            ) : (
              <div className="upload-placeholder">
                <span>üè´</span>
                <p>Click to upload logo</p>
                <p className="upload-hint">Recommended: 200x200px</p>
              </div>
            )}
            <input
              ref={logoInputRef}
              type="file"
              className="file-input"
              accept="image/*"
              onChange={(e) => handleImageUpload(e, 'logo')}
            />
          </div>
        </div>

        {/* Banner Upload */}
        <div className="image-upload-group">
          <label>Institute Banner</label>
          <div 
            className="image-upload-container banner-upload"
            onClick={() => triggerFileInput(bannerInputRef)}
          >
            {banner ? (
              <div className="banner-preview">
                <img src={banner} alt="Institute banner" />
                <button 
                  type="button" 
                  className="remove-image"
                  onClick={(e) => {
                    e.stopPropagation();
                    removeImage('banner');
                  }}
                >
                  √ó
                </button>
              </div>
            ) : (
              <div className="upload-placeholder">
                <span>üèõÔ∏è</span>
                <p>Click to upload banner</p>
                <p className="upload-hint">Recommended: 1200x300px</p>
              </div>
            )}
            <input
              ref={bannerInputRef}
              type="file"
              className="file-input"
              accept="image/*"
              onChange={(e) => handleImageUpload(e, 'banner')}
            />
          </div>
        </div>
      </div>

      {/* Gallery Upload */}
      <div className="image-upload-group">
        <label>Gallery Images</label>
        <div className="gallery-upload">
          <div className="gallery-previews">
            {gallery.map((image, index) => (
              <div key={index} className="gallery-preview">
                <img src={image} alt={`Gallery ${index + 1}`} />
                <button 
                  type="button" 
                  className="remove-image"
                  onClick={() => removeImage('gallery', index)}
                >
                  √ó
                </button>
              </div>
            ))}
            
            <div 
              className="upload-gallery-item"
              onClick={() => triggerFileInput(galleryInputRef)}
            >
              <span>‚ûï</span>
              <p>Add Image</p>
              <p className="upload-hint">Max 10 images</p>
            </div>
          </div>
          
          <input
            ref={galleryInputRef}
            type="file"
            className="file-input"
            accept="image/*"
            onChange={(e) => handleImageUpload(e, 'gallery')}
            style={{ display: 'none' }}
          />
        </div>
      </div>

      <div className="upload-guidelines">
        <h4>Upload Guidelines:</h4>
        <ul>
          <li>Supported formats: JPG, PNG, GIF, WebP</li>
          <li>Maximum file size: 5MB per image</li>
          <li>Logo: Square image recommended (200x200px)</li>
          <li>Banner: Wide image recommended (1200x300px)</li>
          <li>Gallery: Up to 10 images allowed</li>
        </ul>
      </div>
    </div>
  );
};

export default ImageUpload;
