import React from 'react';
import { Link, useLocation, useNavigate } from 'react-router-dom';
import { useAuth } from '../../context/AuthContext';
import './InstituteDashboard.css';

const InstituteSidebar = () => {
  const location = useLocation();
  const navigate = useNavigate();
  const { logout, user } = useAuth();

  const handleLogout = () => {
    logout();
    navigate('/');
  };

  const menuItems = [
    { path: '/institute/dashboard', icon: 'fas fa-tachometer-alt', label: 'Dashboard' },
    { path: '/institute/profile', icon: 'fas fa-user', label: 'Profile' },
    { path: '/institute/courses', icon: 'fas fa-book', label: 'Courses' },
    { path: '/institute/facilities', icon: 'fas fa-dumbbell', label: 'Facilities' },
    { path: '/institute/enquiries', icon: 'fas fa-envelope', label: 'Enquiries' },
    { path: '/institute/reviews', icon: 'fas fa-star', label: 'Reviews' },
  ];

  return (
    <div className="dashboard-sidebar">
      <div className="sidebar-header">
        <div className="institute-info">
          <div className="institute-avatar">
            {user?.name?.charAt(0) || 'I'}
          </div>
          <div className="institute-details">
            <h4>{user?.name}</h4>
            <span className="status verified">Verified</span>
          </div>
        </div>
      </div>

      <nav className="sidebar-nav">
        {menuItems.map(item => (
          <Link
            key={item.path}
            to={item.path}
            className={`nav-item ${location.pathname === item.path ? 'active' : ''}`}
          >
            <i className={item.icon}></i>
            <span>{item.label}</span>
          </Link>
        ))}
      </nav>

      <div className="sidebar-footer">
        <button onClick={handleLogout} className="logout-btn">
          <i className="fas fa-sign-out-alt"></i>
          <span>Logout</span>
        </button>
      </div>
    </div>
  );
};

export default InstituteSidebar;
