import React, { useState, useEffect } from 'react';
import { Link, useSearchParams } from 'react-router-dom';
import api from '../../services/api';
import './UserPanel.css';

const InstituteList = () => {
  const [institutes, setInstitutes] = useState([]);
  const [loading, setLoading] = useState(true);
  const [searchParams, setSearchParams] = useSearchParams();
  const [filters, setFilters] = useState({
    search: searchParams.get('search') || '',
    category: searchParams.get('category') || '',
    city: searchParams.get('city') || '',
    minFee: searchParams.get('minFee') || '',
    maxFee: searchParams.get('maxFee') || '',
    rating: searchParams.get('rating') || '',
    facilities: searchParams.get('facilities') || ''
  });

  useEffect(() => {
    fetchInstitutes();
  }, [searchParams]);

  const fetchInstitutes = async () => {
    try {
      setLoading(true);
      const response = await api.get('/institutes', { params: Object.fromEntries(searchParams) });
      setInstitutes(response.data.institutes);
    } catch (error) {
      console.error('Error fetching institutes:', error);
    } finally {
      setLoading(false);
    }
  };

  const handleFilterChange = (key, value) => {
    const newFilters = { ...filters, [key]: value };
    setFilters(newFilters);
    
    // Update URL params
    const params = new URLSearchParams();
    Object.entries(newFilters).forEach(([k, v]) => {
      if (v) params.set(k, v);
    });
    setSearchParams(params);
  };

  const clearFilters = () => {
    setFilters({
      search: '',
      category: '',
      city: '',
      minFee: '',
      maxFee: '',
      rating: '',
      facilities: ''
    });
    setSearchParams({});
  };

  if (loading) {
    return (
      <div className="container">
        <div className="loading">
          <div className="spinner"></div>
        </div>
      </div>
    );
  }

  return (
    <div className="container">
      <div className="page-header">
        <h1>Find Educational Institutes</h1>
        <p>Discover the best schools, colleges, and coaching centers</p>
      </div>

      {/* Filters Section */}
      <div className="filter-section">
        <div className="filter-header">
          <h3>Filter Institutes</h3>
          <button onClick={clearFilters} className="btn btn-outline">
            Clear All
          </button>
        </div>
        
        <div className="filter-grid">
          <div className="form-group">
            <input
              type="text"
              placeholder="Search by name..."
              value={filters.search}
              onChange={(e) => handleFilterChange('search', e.target.value)}
              className="form-control"
            />
          </div>
          
          <div className="form-group">
            <select
              value={filters.category}
              onChange={(e) => handleFilterChange('category', e.target.value)}
              className="form-control"
            >
              <option value="">All Categories</option>
              <option value="school">School</option>
              <option value="college">College</option>
              <option value="coaching">Coaching Center</option>
              <option value="preschool">Preschool</option>
              <option value="university">University</option>
            </select>
          </div>
          
          <div className="form-group">
            <input
              type="text"
              placeholder="City..."
              value={filters.city}
              onChange={(e) => handleFilterChange('city', e.target.value)}
              className="form-control"
            />
          </div>
          
          <div className="form-group">
            <select
              value={filters.rating}
              onChange={(e) => handleFilterChange('rating', e.target.value)}
              className="form-control"
            >
              <option value="">All Ratings</option>
              <option value="4">4+ Stars</option>
              <option value="3">3+ Stars</option>
              <option value="2">2+ Stars</option>
            </select>
          </div>
          
          <div className="form-group">
            <input
              type="number"
              placeholder="Min Fee"
              value={filters.minFee}
              onChange={(e) => handleFilterChange('minFee', e.target.value)}
              className="form-control"
            />
          </div>
          
          <div className="form-group">
            <input
              type="number"
              placeholder="Max Fee"
              value={filters.maxFee}
              onChange={(e) => handleFilterChange('maxFee', e.target.value)}
              className="form-control"
            />
          </div>
        </div>
      </div>

      {/* Institutes Grid */}
      <div className="institutes-grid">
        {institutes.length === 0 ? (
          <div className="no-results">
            <h3>No institutes found</h3>
            <p>Try adjusting your search filters</p>
          </div>
        ) : (
          <div className="grid grid-3">
            {institutes.map((institute) => (
              <InstituteCard key={institute._id} institute={institute} />
            ))}
          </div>
        )}
      </div>
    </div>
  );
};

const InstituteCard = ({ institute }) => {
  // Safely handle rating data
  const getRatingDisplay = () => {
    if (!institute.rating) return 'No ratings';
    
    // If rating is an object, extract the average
    if (typeof institute.rating === 'object' && institute.rating !== null) {
      return institute.rating.average ? `‚≠ê ${institute.rating.average}` : 'No ratings';
    }
    
    // If rating is a number, display it directly
    return `‚≠ê ${institute.rating}`;
  };

  // Safely handle review count
  const getReviewCount = () => {
    if (!institute.reviewCount) return 0;
    
    // If reviewCount is an object, extract the count
    if (typeof institute.reviewCount === 'object' && institute.reviewCount !== null) {
      return institute.reviewCount.count || 0;
    }
    
    // If reviewCount is a number, use it directly
    return institute.reviewCount;
  };

  return (
    <Link to={`/institute/${institute._id}`} className="institute-card">
      <div className="institute-image-container">
        {institute.images && institute.images.length > 0 ? (
          <img 
            src={institute.images[0]} 
            alt={institute.name}
            className="institute-image"
          />
        ) : (
          <div className="institute-image-placeholder">
            üè´
          </div>
        )}
        <div className="institute-category-tag">
          {institute.category}
        </div>
      </div>
      
      <div className="institute-content">
        <h3 className="institute-name">{institute.name}</h3>
        
        <div className="institute-meta">
          <span className="institute-rating">
            {getRatingDisplay()}
          </span>
          <span className="institute-reviews">
            ({getReviewCount()} reviews)
          </span>
        </div>
        
        <div className="institute-location">
          üìç {institute.address.city}, {institute.address.state}
        </div>
        
        <div className="institute-affiliation">
          üéì {institute.affiliation}
        </div>
        
        {institute.feeRange && (
          <div className="institute-fees">
            üí∞ ‚Çπ{institute.feeRange.min} - ‚Çπ{institute.feeRange.max}
          </div>
        )}
        
        <div className="institute-description">
          {institute.description.length > 100 
            ? `${institute.description.substring(0, 100)}...` 
            : institute.description
          }
        </div>
      </div>
    </Link>
  );
};

export default InstituteList;
