import React from 'react';
import { NavLink, useNavigate } from 'react-router-dom';
import { useAuth } from '../../context/AuthContext';

const InstituteSidebar = () => {
  const { logout } = useAuth();
  const navigate = useNavigate();

  const handleLogout = () => {
    logout();
    navigate('/');
  };

  const navItems = [
    { path: '/institute/dashboard', icon: 'üìä', label: 'Dashboard' },
    { path: '/institute/dashboard/profile', icon: 'üè´', label: 'Profile' },
    { path: '/institute/dashboard/courses', icon: 'üìö', label: 'Courses' },
    { path: '/institute/dashboard/facilities', icon: '‚öôÔ∏è', label: 'Facilities' },
    { path: '/institute/dashboard/enquiries', icon: 'üì•', label: 'Enquiries' },
    { path: '/institute/dashboard/reviews', icon: '‚≠ê', label: 'Reviews' }
  ];

  return (
    <div className="institute-sidebar">
      <div className="sidebar-header">
        <h3>Institute Panel</h3>
      </div>
      
      <nav className="sidebar-nav">
        {navItems.map(item => (
          <NavLink
            key={item.path}
            to={item.path}
            className={({ isActive }) => 
              `nav-item ${isActive ? 'active' : ''}`
            }
          >
            <span className="nav-icon">{item.icon}</span>
            <span className="nav-label">{item.label}</span>
          </NavLink>
        ))}
      </nav>

      <div className="sidebar-footer">
        <button onClick={handleLogout} className="logout-btn">
          <span className="nav-icon">üö™</span>
          <span className="nav-label">Logout</span>
        </button>
      </div>
    </div>
  );
};

export default InstituteSidebar;
