import React from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import { AuthProvider, useAuth } from './context/AuthContext'; // Import useAuth here
import { AppProvider } from './context/AppContext';
import './App.css';

// Components
import Header from './components/Common/Header';
import Footer from './components/Common/Footer';
import Loading from './components/Common/Loading';

// Pages
import HomePage from './components/Home/HomePage';
import Login from './components/Auth/Login';
import Register from './components/Auth/Register';

// User Panel
import UserDashboard from './components/UserPanel/UserDashboard';
import InstituteList from './components/UserPanel/InstituteList';
import InstituteDetail from './components/UserPanel/InstituteDetail';

// Institute Dashboard
import InstituteDashboard from './components/InstituteDashboard/InstituteDashboard';
import ProfileManagement from './components/InstituteDashboard/ProfileManagement';
import CourseManagement from './components/InstituteDashboard/CourseManagement';
import FacilitiesManagement from './components/InstituteDashboard/FacilitiesManagement';
import Enquiries from './components/InstituteDashboard/Enquiries';
import Reviews from './components/InstituteDashboard/Reviews';

// Admin Panel
import AdminDashboard from './components/AdminPanel/AdminDashboard';
import ManageInstitutes from './components/AdminPanel/ManageInstitutes';
import ManageUsers from './components/AdminPanel/ManageUsers';
import AnalyticsDashboard from './components/AdminPanel/AnalyticsDashboard';

// Protected Route Component
const ProtectedRoute = ({ children, requiredRole }) => {
  const { user, userType, loading } = useAuth(); // Now this will work properly

  if (loading) {
    return <Loading />;
  }

  if (!user) {
    return <Navigate to="/login" />;
  }

  if (requiredRole && userType !== requiredRole) {
    return <Navigate to="/" />;
  }

  return children;
};

// Main App Content Component
function AppContent() {
  const { user, userType, loading } = useAuth();

  if (loading) {
    return <Loading />;
  }

  return (
    <Router>
      <div className="App">
        <Header />
        <main className="main-content">
          <Routes>
            {/* Public Routes */}
            <Route path="/" element={<HomePage />} />
            <Route 
              path="/login" 
              element={!user ? <Login /> : <Navigate to={`/${userType}/dashboard`} />} 
            />
            <Route 
              path="/register" 
              element={!user ? <Register /> : <Navigate to={`/${userType}/dashboard`} />} 
            />
            <Route path="/institutes" element={<InstituteList />} />
            <Route path="/institute/:id" element={<InstituteDetail />} />

            {/* User Routes */}
            <Route 
              path="/user/dashboard" 
              element={
                <ProtectedRoute requiredRole="user">
                  <UserDashboard />
                </ProtectedRoute>
              } 
            />

            {/* Institute Routes */}
            <Route 
              path="/institute/dashboard" 
              element={
                <ProtectedRoute requiredRole="institute">
                  <InstituteDashboard />
                </ProtectedRoute>
              } 
            />
            <Route 
              path="/institute/profile" 
              element={
                <ProtectedRoute requiredRole="institute">
                  <ProfileManagement />
                </ProtectedRoute>
              } 
            />
            <Route 
              path="/institute/courses" 
              element={
                <ProtectedRoute requiredRole="institute">
                  <CourseManagement />
                </ProtectedRoute>
              } 
            />
            <Route 
              path="/institute/facilities" 
              element={
                <ProtectedRoute requiredRole="institute">
                  <FacilitiesManagement />
                </ProtectedRoute>
              } 
            />
            <Route 
              path="/institute/enquiries" 
              element={
                <ProtectedRoute requiredRole="institute">
                  <Enquiries />
                </ProtectedRoute>
              } 
            />
            <Route 
              path="/institute/reviews" 
              element={
                <ProtectedRoute requiredRole="institute">
                  <Reviews />
                </ProtectedRoute>
              } 
            />

            {/* Admin Routes */}
            <Route 
              path="/admin/dashboard" 
              element={
                <ProtectedRoute requiredRole="admin">
                  <AdminDashboard />
                </ProtectedRoute>
              } 
            />
            <Route 
              path="/admin/institutes" 
              element={
                <ProtectedRoute requiredRole="admin">
                  <ManageInstitutes />
                </ProtectedRoute>
              } 
            />
            <Route 
              path="/admin/users" 
              element={
                <ProtectedRoute requiredRole="admin">
                  <ManageUsers />
                </ProtectedRoute>
              } 
            />
            <Route 
              path="/admin/analytics" 
              element={
                <ProtectedRoute requiredRole="admin">
                  <AnalyticsDashboard />
                </ProtectedRoute>
              } 
            />

            {/* Fallback Route */}
            <Route path="*" element={<Navigate to="/" />} />
          </Routes>
        </main>
        <Footer />
      </div>
    </Router>
  );
}

// Main App Component
function App() {
  return (
    <AuthProvider>
      <AppProvider>
        <AppContent />
      </AppProvider>
    </AuthProvider>
  );
}

export default App;
