import axios from 'axios';

const API_BASE_URL = 'http://localhost:5000/api';

// Create axios instance with better configuration
const api = axios.create({
  baseURL: API_BASE_URL,
  timeout: 10000,
  headers: {
    'Content-Type': 'application/json',
  },
});

// Request interceptor to add auth token
api.interceptors.request.use(
  (config) => {
    const token = localStorage.getItem('token');
    if (token) {
      config.headers.Authorization = `Bearer ${token}`;
    }
    return config;
  },
  (error) => {
    return Promise.reject(error);
  }
);

// Response interceptor to handle errors
api.interceptors.response.use(
  (response) => {
    return response;
  },
  (error) => {
    console.error('API Error:', error.response?.data || error.message);
    
    if (error.response?.status === 401) {
      // Token expired or invalid
      localStorage.removeItem('token');
      localStorage.removeItem('user');
      window.location.href = '/login';
    }
    
    return Promise.reject(error.response?.data || { message: 'Network error' });
  }
);

export const authAPI = {
  login: (credentials) => api.post('/login', credentials),
  register: (userData) => api.post('/register', userData),
};

export const instituteAPI = {
  getAll: (filters = {}) => {
    const params = new URLSearchParams();
    Object.keys(filters).forEach(key => {
      if (filters[key]) {
        params.append(key, filters[key]);
      }
    });
    return api.get(`/institutes?${params.toString()}`);
  },
  getById: (id) => api.get(`/institutes/${id}`),
  update: (id, data) => api.put(`/institutes/${id}`, data),
};

export const reviewAPI = {
  create: (data) => api.post('/reviews', data),
};

export const enquiryAPI = {
  create: (data) => api.post('/enquiries', data),
};

export const adminAPI = {
  getPendingInstitutes: () => api.get('/admin/pending-institutes'),
  approveInstitute: (id) => api.put(`/admin/approve-institute/${id}`),
  getAnalytics: () => api.get('/admin/analytics'),
};

// Health check
export const healthCheck = () => api.get('/health');

export default api;
