import React, { createContext, useState, useContext, useEffect } from 'react';
import { authAPI } from '../services/api';

const AuthContext = createContext();

export const useAuth = () => {
  const context = useContext(AuthContext);
  if (!context) {
    throw new Error('useAuth must be used within an AuthProvider');
  }
  return context;
};

export const AuthProvider = ({ children }) => {
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(true);
  const [token, setToken] = useState(localStorage.getItem('token'));

  useEffect(() => {
    if (token) {
      fetchUserProfile();
    } else {
      setLoading(false);
    }
  }, [token]);

  const fetchUserProfile = async () => {
    try {
      const response = await authAPI.getProfile();
      if (response.data && response.data.success) {
        setUser(response.data);
      } else {
        throw new Error(response.data?.message || 'Failed to fetch profile');
      }
    } catch (error) {
      console.error('âŒ Error fetching profile:', error);
      logout();
    } finally {
      setLoading(false);
    }
  };

  const login = async (email, password, role) => {
    try {
      console.log('ðŸ”‘ Frontend login attempt:', { email, role });
      
      const response = await authAPI.login(email, password, role);
      
      if (response.data && response.data.success) {
        const { token: newToken, user: userData } = response.data;
        
        localStorage.setItem('token', newToken);
        setToken(newToken);
        setUser(userData);
        
        console.log('âœ… Frontend login successful:', userData.email);
        return { success: true, data: userData };
      } else {
        const errorMessage = response.data?.message || 'Login failed';
        console.log('âŒ Frontend login failed:', errorMessage);
        return { 
          success: false, 
          message: errorMessage 
        };
      }
    } catch (error) {
      console.error('âŒ Frontend login error:', error);
      const errorMessage = error.message || error.response?.data?.message || 'Login failed. Please try again.';
      return { success: false, message: errorMessage };
    }
  };

  const register = async (userData) => {
    try {
      console.log('ðŸ“ Frontend registration attempt:', userData);
      
      const response = await authAPI.register(userData);
      
      if (response.data && response.data.success) {
        const { token: newToken, user: registeredUser } = response.data;
        
        // Only set token and user if token is provided (for regular users)
        if (newToken) {
          localStorage.setItem('token', newToken);
          setToken(newToken);
          setUser(registeredUser);
        }
        
        console.log('âœ… Frontend registration successful:', registeredUser.email);
        return { success: true, data: registeredUser };
      } else {
        const errorMessage = response.data?.message || 'Registration failed';
        console.log('âŒ Frontend registration failed:', errorMessage);
        return { 
          success: false, 
          message: errorMessage 
        };
      }
    } catch (error) {
      console.error('âŒ Frontend registration error:', error);
      const errorMessage = error.message || error.data?.message || 'Registration failed. Please try again.';
      return { success: false, message: errorMessage };
    }
  };

  const logout = () => {
    console.log('ðŸšª Logging out user');
    localStorage.removeItem('token');
    setToken(null);
    setUser(null);
  };

  const updateUser = (userData) => {
    setUser(prev => ({ ...prev, ...userData }));
  };

  const value = {
    user,
    token,
    login,
    register,
    logout,
    updateUser,
    loading,
    isAuthenticated: !!token && !!user,
    refetchProfile: fetchUserProfile
  };

  return (
    <AuthContext.Provider value={value}>
      {children}
    </AuthContext.Provider>
  );
};
