import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { instituteService, enquiryService, reviewService } from '../../services/api';
import './InstituteDashboard.css';

const DashboardHome = () => {
  const [stats, setStats] = useState({
    enquiries: 0,
    reviews: 0,
    courses: 0,
    rating: 0
  });
  const [recentEnquiries, setRecentEnquiries] = useState([]);
  const [recentReviews, setRecentReviews] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetchDashboardData();
  }, []);

  const fetchDashboardData = async () => {
    try {
      const [instituteRes, enquiriesRes, reviewsRes] = await Promise.all([
        instituteService.getCurrentInstitute(),
        enquiryService.getByInstitute(),
        reviewService.getByInstitute()
      ]);

      const institute = instituteRes.data;
      
      setStats({
        enquiries: enquiriesRes.data.length,
        reviews: reviewsRes.data.length,
        courses: institute.courses?.length || 0,
        rating: institute.rating?.average || 0
      });

      setRecentEnquiries(enquiriesRes.data.slice(0, 5));
      setRecentReviews(reviewsRes.data.slice(0, 5));
      setLoading(false);
    } catch (error) {
      console.error('Error fetching dashboard data:', error);
      setLoading(false);
    }
  };

  if (loading) {
    return <div className="loading">Loading dashboard...</div>;
  }

  return (
    <div className="dashboard-home">
      <div className="welcome-section">
        <h1>Welcome to Your Dashboard</h1>
        <p>Manage your institute profile, courses, and interact with students</p>
      </div>

      {/* Stats Grid */}
      <div className="stats-grid">
        <div className="stat-card">
          <div className="stat-icon">üì•</div>
          <div className="stat-info">
            <h3>{stats.enquiries}</h3>
            <p>Total Enquiries</p>
          </div>
          <Link to="/institute/dashboard/enquiries" className="stat-link">View All</Link>
        </div>

        <div className="stat-card">
          <div className="stat-icon">‚≠ê</div>
          <div className="stat-info">
            <h3>{stats.reviews}</h3>
            <p>Total Reviews</p>
          </div>
          <Link to="/institute/dashboard/reviews" className="stat-link">View All</Link>
        </div>

        <div className="stat-card">
          <div className="stat-icon">üìö</div>
          <div className="stat-info">
            <h3>{stats.courses}</h3>
            <p>Courses</p>
          </div>
          <Link to="/institute/dashboard/courses" className="stat-link">Manage</Link>
        </div>

        <div className="stat-card">
          <div className="stat-icon">üèÜ</div>
          <div className="stat-info">
            <h3>{stats.rating}/5</h3>
            <p>Average Rating</p>
          </div>
        </div>
      </div>

      {/* Quick Actions */}
      <div className="quick-actions">
        <h2>Quick Actions</h2>
        <div className="actions-grid">
          <Link to="/institute/dashboard/profile" className="action-card">
            <span className="action-icon">üë§</span>
            <h3>Update Profile</h3>
            <p>Edit your institute information</p>
          </Link>
          
          <Link to="/institute/dashboard/courses" className="action-card">
            <span className="action-icon">üìö</span>
            <h3>Manage Courses</h3>
            <p>Add or edit courses</p>
          </Link>
          
          <Link to="/institute/dashboard/facilities" className="action-card">
            <span className="action-icon">üè¢</span>
            <h3>Update Facilities</h3>
            <p>Edit available facilities</p>
          </Link>
          
          <Link to="/institute/dashboard/enquiries" className="action-card">
            <span className="action-icon">üí¨</span>
            <h3>Respond to Enquiries</h3>
            <p>Check and respond to student queries</p>
          </Link>
        </div>
      </div>

      {/* Recent Activity */}
      <div className="recent-activity">
        <div className="activity-column">
          <h3>Recent Enquiries</h3>
          {recentEnquiries.length === 0 ? (
            <p className="no-data">No recent enquiries</p>
          ) : (
            recentEnquiries.map(enquiry => (
              <div key={enquiry._id} className="activity-item">
                <div className="activity-content">
                  <strong>{enquiry.user?.name}</strong>
                  <p>{enquiry.message}</p>
                  <small>{new Date(enquiry.createdAt).toLocaleDateString()}</small>
                </div>
                <span className={`status ${enquiry.status}`}>
                  {enquiry.status}
                </span>
              </div>
            ))
          )}
          <Link to="/institute/dashboard/enquiries" className="view-all-link">
            View All Enquiries ‚Üí
          </Link>
        </div>

        <div className="activity-column">
          <h3>Recent Reviews</h3>
          {recentReviews.length === 0 ? (
            <p className="no-data">No recent reviews</p>
          ) : (
            recentReviews.map(review => (
              <div key={review._id} className="activity-item">
                <div className="activity-content">
                  <strong>{review.user?.name}</strong>
                  <div className="rating">
                    {'‚≠ê'.repeat(review.rating)}
                    <span>({review.rating}/5)</span>
                  </div>
                  <p>{review.reviewText}</p>
                  <small>{new Date(review.createdAt).toLocaleDateString()}</small>
                </div>
                <span className={`status ${review.isApproved ? 'approved' : 'pending'}`}>
                  {review.isApproved ? 'Approved' : 'Pending'}
                </span>
              </div>
            ))
          )}
          <Link to="/institute/dashboard/reviews" className="view-all-link">
            View All Reviews ‚Üí
          </Link>
        </div>
      </div>
    </div>
  );
};

export default DashboardHome;
