import React, { useState, useEffect } from 'react';
import { reviewService, instituteService } from '../../services/api';
import './InstituteDashboard.css';

const Reviews = () => {
  const [reviews, setReviews] = useState([]);
  const [loading, setLoading] = useState(true);
  const [message, setMessage] = useState('');
  const [instituteId, setInstituteId] = useState(null);

  useEffect(() => {
    fetchInstituteAndReviews();
  }, []);

  const fetchInstituteAndReviews = async () => {
    try {
      setLoading(true);
      
      // First get the institute to get the ID
      const instituteRes = await instituteService.getCurrentInstitute();
      const instituteData = instituteRes.data;
      
      if (instituteData && instituteData._id) {
        setInstituteId(instituteData._id);
        const reviewsRes = await reviewService.getByInstituteDashboard(instituteData._id);
        setReviews(reviewsRes.data || []);
      } else {
        throw new Error('Institute not found');
      }
    } catch (error) {
      console.error('Error fetching reviews:', error);
      setReviews([]);
    } finally {
      setLoading(false);
    }
  };

  const handleApproveReview = async (reviewId) => {
    try {
      await reviewService.update(reviewId, { isApproved: true });
      setMessage('Review approved successfully!');
      fetchInstituteAndReviews();
    } catch (error) {
      setMessage('Error approving review.');
    }
  };

  const handleRejectReview = async (reviewId) => {
    try {
      await reviewService.update(reviewId, { isApproved: false });
      setMessage('Review rejected successfully!');
      fetchInstituteAndReviews();
    } catch (error) {
      setMessage('Error rejecting review.');
    }
  };

  const getRatingStars = (rating) => {
    return '⭐'.repeat(rating) + '☆'.repeat(5 - rating);
  };

  if (loading) {
    return <div className="loading">Loading reviews...</div>;
  }

  return (
    <div className="reviews-management">
      <div className="dashboard-section">
        <div className="section-header">
          <div>
            <h2>Student Reviews</h2>
            <p>Manage and moderate student reviews</p>
          </div>
          <div className="reviews-stats">
            <span className="stat">
              Total: <strong>{reviews.length}</strong>
            </span>
            <span className="stat">
              Approved: <strong>{reviews.filter(r => r.isApproved).length}</strong>
            </span>
            <span className="stat">
              Pending: <strong>{reviews.filter(r => !r.isApproved).length}</strong>
            </span>
          </div>
        </div>

        {message && (
          <div className={`message ${message.includes('Error') ? 'error' : 'success'}`}>
            {message}
          </div>
        )}

        {reviews.length === 0 ? (
          <div className="no-data">
            <h3>No Reviews Yet</h3>
            <p>Your institute hasn't received any reviews from students yet.</p>
          </div>
        ) : (
          <div className="reviews-list">
            {reviews.map(review => (
              <div key={review._id} className="review-card">
                <div className="review-header">
                  <div className="reviewer-info">
                    <div className="reviewer-avatar">
                      {review.user?.name?.charAt(0).toUpperCase()}
                    </div>
                    <div>
                      <h4>{review.user?.name}</h4>
                      <div className="review-rating">
                        {getRatingStars(review.rating)}
                        <span>({review.rating}/5)</span>
                      </div>
                    </div>
                  </div>
                  <div className="review-meta">
                    <span className={`status ${review.isApproved ? 'approved' : 'pending'}`}>
                      {review.isApproved ? 'Approved' : 'Pending Approval'}
                    </span>
                    <span className="review-date">
                      {new Date(review.createdAt).toLocaleDateString()}
                    </span>
                  </div>
                </div>

                <div className="review-content">
                  <p>{review.reviewText}</p>
                </div>

                <div className="review-actions">
                  {!review.isApproved && (
                    <>
                      <button
                        onClick={() => handleApproveReview(review._id)}
                        className="btn-approve"
                      >
                        Approve Review
                      </button>
                      <button
                        onClick={() => handleRejectReview(review._id)}
                        className="btn-reject"
                      >
                        Reject Review
                      </button>
                    </>
                  )}
                  
                  {review.isApproved && (
                    <button
                      onClick={() => handleRejectReview(review._id)}
                      className="btn-reject"
                    >
                      Unpublish Review
                    </button>
                  )}
                </div>
              </div>
            ))}
          </div>
        )}
      </div>
    </div>
  );
};

export default Reviews;
