const InstituteList = () => {
  const { addAlert } = useApp();
  const [institutes, setInstitutes] = useState([]);
  const [filteredInstitutes, setFilteredInstitutes] = useState([]);
  const [loading, setLoading] = useState(true);
  const [filters, setFilters] = useState({
    search: '',
    city: '',
    category: '',
    minFees: '',
    maxFees: '',
    minRating: '',
    facilities: []
  });

  const [cities, setCities] = useState([]);
  const [categories, setCategories] = useState([]);

  useEffect(() => {
    fetchInstitutes();
  }, []);

  useEffect(() => {
    filterInstitutes();
  }, [institutes, filters]);

  const fetchInstitutes = async () => {
    try {
      const response = await instituteAPI.getAll();
      const institutesData = response.data || [];
      
      setInstitutes(institutesData);
      
      // Extract unique cities and categories from real data
      const uniqueCities = [...new Set(institutesData
        .filter(inst => inst.city)
        .map(inst => inst.city)
      )].sort();

      const uniqueCategories = [...new Set(institutesData
        .filter(inst => inst.affiliation)
        .map(inst => inst.affiliation)
      )].sort();

      setCities(uniqueCities);
      setCategories(uniqueCategories);

    } catch (error) {
      addAlert({
        type: 'error',
        message: 'Failed to load institutes'
      });
    } finally {
      setLoading(false);
    }
  };

  const filterInstitutes = () => {
    let filtered = institutes.filter(institute => 
      institute.status === 'approved' && institute.isActive !== false
    );

    // Apply search filter
    if (filters.search) {
      filtered = filtered.filter(institute =>
        institute.name.toLowerCase().includes(filters.search.toLowerCase()) ||
        institute.affiliation?.toLowerCase().includes(filters.search.toLowerCase()) ||
        institute.city?.toLowerCase().includes(filters.search.toLowerCase())
      );
    }

    // Apply city filter
    if (filters.city) {
      filtered = filtered.filter(institute => 
        institute.city === filters.city
      );
    }

    // Apply category filter
    if (filters.category) {
      filtered = filtered.filter(institute => 
        institute.affiliation === filters.category
      );
    }

    // Apply fees filter
    if (filters.minFees) {
      filtered = filtered.filter(institute => {
        const minCourseFee = Math.min(...(institute.courses?.map(c => c.fees) || [0]));
        return minCourseFee >= parseInt(filters.minFees);
      });
    }

    if (filters.maxFees) {
      filtered = filtered.filter(institute => {
        const maxCourseFee = Math.max(...(institute.courses?.map(c => c.fees) || [0]));
        return maxCourseFee <= parseInt(filters.maxFees);
      });
    }

    // Apply rating filter
    if (filters.minRating) {
      filtered = filtered.filter(institute => {
        const avgRating = calculateAverageRating(institute.reviews);
        return avgRating >= parseInt(filters.minRating);
      });
    }

    setFilteredInstitutes(filtered);
  };

  const calculateAverageRating = (reviews) => {
    if (!reviews || reviews.length === 0) return 0;
    const sum = reviews.reduce((total, review) => total + review.rating, 0);
    return (sum / reviews.length).toFixed(1);
  };

  const handleFilterChange = (key, value) => {
    setFilters(prev => ({
      ...prev,
      [key]: value
    }));
  };

  const clearFilters = () => {
    setFilters({
      search: '',
      city: '',
      category: '',
      minFees: '',
      maxFees: '',
      minRating: '',
      facilities: []
    });
  };

  if (loading) {
    return (
      <div className="user-container">
        <div className="loading">Loading institutes...</div>
      </div>
    );
  }

  return (
    <div className="user-container">
      <div className="page-header">
        <h1>Find the Perfect Institute</h1>
        <p>Discover and compare educational institutions that match your needs</p>
      </div>

      {/* Filters Section */}
      <div className="filters-section">
        <div className="filters-grid">
          <div className="filter-group">
            <label>Search</label>
            <input
              type="text"
              placeholder="Search by name, location, or category..."
              value={filters.search}
              onChange={(e) => handleFilterChange('search', e.target.value)}
              className="filter-input"
            />
          </div>

          <div className="filter-group">
            <label>City</label>
            <select
              value={filters.city}
              onChange={(e) => handleFilterChange('city', e.target.value)}
              className="filter-select"
            >
              <option value="">All Cities</option>
              {cities.map(city => (
                <option key={city} value={city}>{city}</option>
              ))}
            </select>
          </div>

          <div className="filter-group">
            <label>Category</label>
            <select
              value={filters.category}
              onChange={(e) => handleFilterChange('category', e.target.value)}
              className="filter-select"
            >
              <option value="">All Categories</option>
              {categories.map(category => (
                <option key={category} value={category}>{category}</option>
              ))}
            </select>
          </div>

          <div className="filter-group">
            <label>Min Rating</label>
            <select
              value={filters.minRating}
              onChange={(e) => handleFilterChange('minRating', e.target.value)}
              className="filter-select"
            >
              <option value="">Any Rating</option>
              <option value="4">4+ Stars</option>
              <option value="3">3+ Stars</option>
              <option value="2">2+ Stars</option>
              <option value="1">1+ Star</option>
            </select>
          </div>

          <div className="filter-group">
            <label>Fees Range</label>
            <div className="fees-range">
              <input
                type="number"
                placeholder="Min"
                value={filters.minFees}
                onChange={(e) => handleFilterChange('minFees', e.target.value)}
                className="fees-input"
              />
              <span>to</span>
              <input
                type="number"
                placeholder="Max"
                value={filters.maxFees}
                onChange={(e) => handleFilterChange('maxFees', e.target.value)}
                className="fees-input"
              />
            </div>
          </div>

          <div className="filter-group">
            <label>&nbsp;</label>
            <button onClick={clearFilters} className="btn btn-outline">
              Clear Filters
            </button>
          </div>
        </div>
      </div>

      {/* Results Summary */}
      <div className="results-summary">
        <p>
          Found <strong>{filteredInstitutes.length}</strong> institutes
          {filters.search && ` matching "${filters.search}"`}
          {filters.city && ` in ${filters.city}`}
          {filters.category && ` under ${filters.category}`}
        </p>
      </div>

      {/* Institutes Grid */}
      <div className="institutes-grid">
        {filteredInstitutes.length > 0 ? (
          filteredInstitutes.map(institute => (
            <InstituteCard 
              key={institute._id} 
              institute={institute} 
            />
          ))
        ) : (
          <div className="no-results">
            <i className="fas fa-search"></i>
            <h3>No institutes found</h3>
            <p>Try adjusting your search criteria or clear filters</p>
            <button onClick={clearFilters} className="btn btn-primary">
              Clear All Filters
            </button>
          </div>
        )}
      </div>
    </div>
  );
};

// Institute Card Component
const InstituteCard = ({ institute }) => {
  const calculateAverageRating = (reviews) => {
    if (!reviews || reviews.length === 0) return 0;
    const sum = reviews.reduce((total, review) => total + review.rating, 0);
    return (sum / reviews.length).toFixed(1);
  };

  const getMinFees = (courses) => {
    if (!courses || courses.length === 0) return 'N/A';
    const minFee = Math.min(...courses.map(c => c.fees));
    return `₹${minFee.toLocaleString()}/year`;
  };

  const avgRating = calculateAverageRating(institute.reviews);
  const totalReviews = institute.reviews?.length || 0;
  const minFees = getMinFees(institute.courses);

  return (
    <div className="institute-card">
      <Link to={`/institute/${institute._id}`} className="card-link">
        <div className="card-image">
          {institute.bannerImage ? (
            <img src={institute.bannerImage} alt={institute.name} />
          ) : (
            <div className="image-placeholder">
              <i className="fas fa-school"></i>
            </div>
          )}
          <div className="card-badge">
            {institute.affiliation}
          </div>
        </div>

        <div className="card-content">
          <div className="institute-header">
            <div className="institute-logo">
              {institute.logo ? (
                <img src={institute.logo} alt={`${institute.name} logo`} />
              ) : (
                <div className="logo-placeholder">
                  {institute.name?.charAt(0)}
                </div>
              )}
            </div>
            <div className="institute-info">
              <h3>{institute.name}</h3>
              <p className="location">
                <i className="fas fa-map-marker-alt"></i>
                {institute.city}, {institute.state}
              </p>
            </div>
          </div>

          <div className="institute-stats">
            <div className="stat">
              <div className="rating">
                <span className="stars">
                  {'★'.repeat(Math.floor(avgRating))}
                  {'☆'.repeat(5 - Math.floor(avgRating))}
                </span>
                <span className="rating-value">{avgRating}</span>
                <span className="review-count">({totalReviews})</span>
              </div>
            </div>
            <div className="stat">
              <span className="fees">{minFees}</span>
            </div>
          </div>

          <p className="institute-description">
            {institute.description?.substring(0, 120)}...
          </p>

          <div className="institute-features">
            {institute.facilities?.slice(0, 3).map(facility => (
              <span key={facility._id} className="feature-tag">
                {facility.type}
              </span>
            ))}
            {institute.facilities?.length > 3 && (
              <span className="feature-tag">
                +{institute.facilities.length - 3} more
              </span>
            )}
          </div>
        </div>

        <div className="card-footer">
          <span className="view-details">View Details →</span>
        </div>
      </Link>
    </div>
  );
};

export default InstituteList;
