import axios from 'axios';

const API_BASE_URL = 'http://localhost:5000/api';

const api = axios.create({
  baseURL: API_BASE_URL,
  timeout: 10000,
  headers: {
    'Content-Type': 'application/json',
  },
});

// Request interceptor
api.interceptors.request.use(
  (config) => {
    const token = localStorage.getItem('token');
    if (token) {
      config.headers.Authorization = `Bearer ${token}`;
    }
    return config;
  },
  (error) => {
    return Promise.reject(error);
  }
);

// Response interceptor
api.interceptors.response.use(
  (response) => response,
  (error) => {
    const errorMessage = error.response?.data?.message || error.message || 'Network error';
    console.error('API Error:', errorMessage);
    
    if (error.response?.status === 401) {
      localStorage.removeItem('token');
      localStorage.removeItem('user');
      window.location.href = '/login';
    }
    
    return Promise.reject({ message: errorMessage });
  }
);

// API endpoints
export const authAPI = {
  login: (credentials) => api.post('/auth/login', credentials),
  register: (userData) => api.post('/auth/register', userData),
  seed: () => api.post('/auth/seed'),
};

export const instituteAPI = {
  getAll: () => api.get('/institutes'),
  getById: (id) => api.get(`/institutes/${id}`),
  update: (id, data) => api.put(`/institutes/${id}`, data),
};

export const reviewAPI = {
  create: (data) => api.post('/reviews', data),
};

export const enquiryAPI = {
  create: (data) => api.post('/enquiries', data),
};

export const adminAPI = {
  getPendingInstitutes: () => api.get('/admin/pending-institutes'),
  approveInstitute: (id) => api.put(`/admin/approve-institute/${id}`),
  getAnalytics: () => api.get('/admin/analytics'),
};

export const userAPI = {
  getDashboard: () => api.get('/user/dashboard'),
};

// Health check
export const healthCheck = () => api.get('/health');

export default api;
