import React, { useState, useEffect } from 'react';
import { enquiryAPI } from '../../services/api';
import { useAuth } from '../../context/AuthContext';

const Enquiries = () => {
  const [enquiries, setEnquiries] = useState([]);
  const [loading, setLoading] = useState(true);
  const [respondingTo, setRespondingTo] = useState(null);
  const [responseText, setResponseText] = useState('');
  const { user } = useAuth();

  useEffect(() => {
    fetchEnquiries();
  }, []);

  const fetchEnquiries = async () => {
    try {
      const response = await enquiryAPI.getByInstitute(user.institute._id);
      setEnquiries(response.data);
    } catch (error) {
      console.error('Error fetching enquiries:', error);
    } finally {
      setLoading(false);
    }
  };

  const handleRespond = async (enquiryId) => {
    if (!responseText.trim()) {
      alert('Please enter a response');
      return;
    }

    try {
      await enquiryAPI.respond(enquiryId, responseText);
      setRespondingTo(null);
      setResponseText('');
      fetchEnquiries();
      alert('Response sent successfully!');
    } catch (error) {
      alert('Error sending response: ' + error.message);
    }
  };

  const getStatusBadge = (status) => {
    const statusConfig = {
      pending: { class: 'pending', text: 'Pending' },
      responded: { class: 'responded', text: 'Responded' },
      closed: { class: 'closed', text: 'Closed' }
    };
    
    const config = statusConfig[status] || statusConfig.pending;
    return <span className={`status-badge ${config.class}`}>{config.text}</span>;
  };

  const formatDate = (dateString) => {
    return new Date(dateString).toLocaleDateString('en-IN', {
      day: 'numeric',
      month: 'short',
      year: 'numeric',
      hour: '2-digit',
      minute: '2-digit'
    });
  };

  if (loading) {
    return <div className="loading">Loading enquiries...</div>;
  }

  return (
    <div className="enquiries-management">
      <div className="section-header">
        <h2>Student Enquiries</h2>
        <p>Manage and respond to student queries</p>
      </div>

      <div className="enquiries-stats">
        <div className="stat-card">
          <h3>Total Enquiries</h3>
          <div className="stat-number">{enquiries.length}</div>
        </div>
        <div className="stat-card">
          <h3>Pending</h3>
          <div className="stat-number">
            {enquiries.filter(e => e.status === 'pending').length}
          </div>
        </div>
        <div className="stat-card">
          <h3>Responded</h3>
          <div className="stat-number">
            {enquiries.filter(e => e.status === 'responded').length}
          </div>
        </div>
      </div>

      <div className="enquiries-list">
        {enquiries.length === 0 ? (
          <div className="empty-state">
            <h3>No Enquiries Yet</h3>
            <p>Student enquiries will appear here when they contact your institute.</p>
          </div>
        ) : (
          enquiries.map(enquiry => (
            <div key={enquiry._id} className="enquiry-card">
              <div className="enquiry-header">
                <div className="user-info">
                  <h4>{enquiry.user.name}</h4>
                  <p className="user-email">{enquiry.user.email}</p>
                  {enquiry.user.phone && (
                    <p className="user-phone">ðŸ“ž {enquiry.user.phone}</p>
                  )}
                </div>
                <div className="enquiry-meta">
                  {getStatusBadge(enquiry.status)}
                  <span className="enquiry-date">
                    {formatDate(enquiry.createdAt)}
                  </span>
                </div>
              </div>

              <div className="enquiry-content">
                <p><strong>Message:</strong> {enquiry.message}</p>
              </div>

              {enquiry.response && (
                <div className="response-section">
                  <p><strong>Your Response:</strong> {enquiry.response}</p>
                  <span className="response-date">
                    Responded on {formatDate(enquiry.respondedAt)}
                  </span>
                </div>
              )}

              {enquiry.status === 'pending' && (
                <div className="enquiry-actions">
                  {respondingTo === enquiry._id ? (
                    <div className="response-form">
                      <textarea
                        value={responseText}
                        onChange={(e) => setResponseText(e.target.value)}
                        placeholder="Type your response here..."
                        rows="4"
                      />
                      <div className="response-actions">
                        <button
                          className="btn btn-outline"
                          onClick={() => setRespondingTo(null)}
                        >
                          Cancel
                        </button>
                        <button
                          className="btn btn-primary"
                          onClick={() => handleRespond(enquiry._id)}
                        >
                          Send Response
                        </button>
                      </div>
                    </div>
                  ) : (
                    <button
                      className="btn btn-primary"
                      onClick={() => setRespondingTo(enquiry._id)}
                    >
                      Respond
                    </button>
                  )}
                </div>
              )}
            </div>
          ))
        )}
      </div>
    </div>
  );
};

export default Enquiries;
