import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { useAuth } from '../../context/AuthContext';
import { instituteAPI, reviewAPI, enquiryAPI } from '../../services/api';
import ReviewForm from './ReviewForm';
import EnquiryForm from './EnquiryForm';
import './UserPanel.css';

const InstituteDetail = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const { user } = useAuth();
  const [institute, setInstitute] = useState(null);
  const [reviews, setReviews] = useState([]);
  const [activeTab, setActiveTab] = useState('overview');
  const [showReviewForm, setShowReviewForm] = useState(false);
  const [showEnquiryForm, setShowEnquiryForm] = useState(false);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetchInstituteDetail();
  }, [id]);

  const fetchInstituteDetail = async () => {
    try {
      const response = await instituteAPI.getById(id);
      setInstitute(response.data.institute);
      setReviews(response.data.reviews);
    } catch (error) {
      console.error('Error fetching institute details:', error);
    } finally {
      setLoading(false);
    }
  };

  const handleReviewSubmit = async (reviewData) => {
    try {
      await reviewAPI.create({
        instituteId: id,
        ...reviewData
      });
      setShowReviewForm(false);
      fetchInstituteDetail(); // Refresh reviews
    } catch (error) {
      console.error('Error submitting review:', error);
    }
  };

  const handleEnquirySubmit = async (enquiryData) => {
    try {
      await enquiryAPI.create({
        instituteId: id,
        ...enquiryData
      });
      setShowEnquiryForm(false);
      alert('Your enquiry has been sent successfully!');
    } catch (error) {
      console.error('Error submitting enquiry:', error);
    }
  };

  const calculateAverageRating = () => {
    if (reviews.length === 0) return 0;
    const total = reviews.reduce((sum, review) => sum + review.rating, 0);
    return (total / reviews.length).toFixed(1);
  };

  const getRatingDistribution = () => {
    const distribution = { 5: 0, 4: 0, 3: 0, 2: 0, 1: 0 };
    reviews.forEach(review => {
      distribution[review.rating]++;
    });
    return distribution;
  };

  const renderStars = (rating) => {
    return (
      <div className="star-rating">
        {[1, 2, 3, 4, 5].map(star => (
          <span
            key={star}
            className={star <= rating ? 'star filled' : 'star'}
          >
            â˜…
          </span>
        ))}
      </div>
    );
  };

  const formatDate = (dateString) => {
    return new Date(dateString).toLocaleDateString('en-IN', {
      day: '2-digit',
      month: 'short',
      year: 'numeric'
    });
  };

  if (loading) {
    return <div className="loading">Loading institute details...</div>;
  }

  if (!institute) {
    return <div className="error">Institute not found</div>;
  }

  const ratingDistribution = getRatingDistribution();
  const averageRating = calculateAverageRating();

  return (
    <div className="institute-detail">
      {/* Hero Section */}
      <div className="institute-hero">
        <div className="hero-images">
          {institute.images && institute.images.length > 0 ? (
            <img src={institute.images[0]} alt={institute.name} />
          ) : (
            <div className="no-hero-image">ğŸ«</div>
          )}
        </div>
        
        <div className="hero-content">
          <div className="institute-badge">
            {institute.category.charAt(0).toUpperCase() + institute.category.slice(1)}
          </div>
          <h1>{institute.name}</h1>
          <div className="hero-meta">
            <span className="location">ğŸ“ {institute.city}, {institute.state}</span>
            <span className="affiliation">ğŸ›ï¸ {institute.affiliation}</span>
          </div>
          
          <div className="hero-rating">
            <div className="rating-score">
              <span className="score">{averageRating}</span>
              <span className="out-of">/5</span>
            </div>
            <div className="rating-details">
              {renderStars(parseFloat(averageRating))}
              <span className="review-count">({reviews.length} reviews)</span>
            </div>
          </div>

          <div className="hero-actions">
            <button 
              onClick={() => setShowEnquiryForm(true)}
              className="btn-primary"
            >
              ğŸ“§ Send Enquiry
            </button>
            <button 
              onClick={() => setShowReviewForm(true)}
              className="btn-secondary"
            >
              â­ Write Review
            </button>
          </div>
        </div>
      </div>

      {/* Navigation Tabs */}
      <div className="detail-tabs">
        <button 
          className={activeTab === 'overview' ? 'tab active' : 'tab'}
          onClick={() => setActiveTab('overview')}
        >
          Overview
        </button>
        <button 
          className={activeTab === 'courses' ? 'tab active' : 'tab'}
          onClick={() => setActiveTab('courses')}
        >
          Courses
        </button>
        <button 
          className={activeTab === 'facilities' ? 'tab active' : 'tab'}
          onClick={() => setActiveTab('facilities')}
        >
          Facilities
        </button>
        <button 
          className={activeTab === 'reviews' ? 'tab active' : 'tab'}
          onClick={() => setActiveTab('reviews')}
        >
          Reviews ({reviews.length})
        </button>
        <button 
          className={activeTab === 'contact' ? 'tab active' : 'tab'}
          onClick={() => setActiveTab('contact')}
        >
          Contact
        </button>
      </div>

      {/* Tab Content */}
      <div className="tab-content">
        {activeTab === 'overview' && (
          <div className="overview-tab">
            <div className="overview-grid">
              <div className="overview-main">
                <h2>About {institute.name}</h2>
                <p className="institute-description">
                  {institute.description || 'No description available.'}
                </p>

                <div className="key-info">
                  <h3>Key Information</h3>
                  <div className="info-grid">
                    <div className="info-item">
                      <span className="info-label">Established</span>
                      <span className="info-value">2005</span>
                    </div>
                    <div className="info-item">
                      <span className="info-label">Institute Type</span>
                      <span className="info-value">{institute.category}</span>
                    </div>
                    <div className="info-item">
                      <span className="info-label">Affiliation</span>
                      <span className="info-value">{institute.affiliation}</span>
                    </div>
                    <div className="info-item">
                      <span className="info-label">Location</span>
                      <span className="info-value">{institute.city}, {institute.state}</span>
                    </div>
                  </div>
                </div>
              </div>

              <div className="overview-sidebar">
                <div className="contact-card">
                  <h3>Contact Information</h3>
                  <div className="contact-info">
                    <p><strong>Email:</strong> {institute.contactEmail}</p>
                    <p><strong>Phone:</strong> {institute.contactPhone}</p>
                    <p><strong>Address:</strong> {institute.address}</p>
                    {institute.website && (
                      <p><strong>Website:</strong> 
                        <a href={institute.website} target="_blank" rel="noopener noreferrer">
                          {institute.website}
                        </a>
                      </p>
                    )}
                  </div>
                </div>
              </div>
            </div>
          </div>
        )}

        {activeTab === 'courses' && (
          <div className="courses-tab">
            <h2>Available Courses</h2>
            {institute.courses && institute.courses.length > 0 ? (
              <div className="courses-grid">
                {institute.courses.map((course, index) => (
                  <div key={index} className="course-card">
                    <div className="course-image">
                      {course.images && course.images.length > 0 ? (
                        <img src={course.images[0]} alt={course.title} />
                      ) : (
                        <div className="no-course-image">ğŸ“š</div>
                      )}
                    </div>
                    <div className="course-info">
                      <h3>{course.title}</h3>
                      <p className="course-description">{course.description}</p>
                      <div className="course-details">
                        <span className="duration">â±ï¸ {course.duration}</span>
                        <span className="fees">ğŸ’° â‚¹{course.fees?.toLocaleString()}</span>
                      </div>
                    </div>
                  </div>
                ))}
              </div>
            ) : (
              <div className="empty-state">
                <p>No courses available at the moment.</p>
              </div>
            )}
          </div>
        )}

        {activeTab === 'facilities' && (
          <div className="facilities-tab">
            <h2>Institute Facilities</h2>
            {institute.facilities && institute.facilities.length > 0 ? (
              <div className="facilities-grid">
                {institute.facilities.map((facility, index) => (
                  <div key={index} className="facility-item">
                    <span className="facility-icon">âœ…</span>
                    <span className="facility-name">{facility}</span>
                  </div>
                ))}
              </div>
            ) : (
              <div className="empty-state">
                <p>No facilities information available.</p>
              </div>
            )}
          </div>
        )}

        {activeTab === 'reviews' && (
          <div className="reviews-tab">
            <div className="reviews-header">
              <div className="rating-summary">
                <div className="average-rating">
                  <span className="rating-number">{averageRating}</span>
                  <span className="rating-out-of">out of 5</span>
                  {renderStars(parseFloat(averageRating))}
                  <span className="total-reviews">{reviews.length} reviews</span>
                </div>
                
                <div className="rating-breakdown">
                  {[5, 4, 3, 2, 1].map(rating => (
                    <div key={rating} className="rating-bar">
                      <span className="rating-label">{rating} star</span>
                      <div className="bar-container">
                        <div 
                          className="bar-fill"
                          style={{ 
                            width: `${(ratingDistribution[rating] / reviews.length) * 100}%` 
                          }}
                        ></div>
                      </div>
                      <span className="rating-count">{ratingDistribution[rating]}</span>
                    </div>
                  ))}
                </div>
              </div>

              <button 
                onClick={() => setShowReviewForm(true)}
                className="btn-primary"
                disabled={!user}
              >
                Write a Review
              </button>
            </div>

            <div className="reviews-list">
              {reviews.length === 0 ? (
                <div className="empty-state">
                  <p>No reviews yet. Be the first to review this institute!</p>
                  {!user && (
                    <p>Please login to write a review.</p>
                  )}
                </div>
              ) : (
                reviews.map(review => (
                  <div key={review._id} className="review-card">
                    <div className="review-header">
                      <div className="reviewer-info">
                        <h4>{review.userId?.name || 'Anonymous'}</h4>
                        {renderStars(review.rating)}
                      </div>
                      <span className="review-date">
                        {formatDate(review.createdAt)}
                      </span>
                    </div>
                    <p className="review-text">{review.reviewText}</p>
                  </div>
                ))
              )}
            </div>
          </div>
        )}

        {activeTab === 'contact' && (
          <div className="contact-tab">
            <h2>Contact Information</h2>
            <div className="contact-details">
              <div className="contact-info">
                <h3>Get in Touch</h3>
                <div className="contact-item">
                  <strong>ğŸ“§ Email:</strong>
                  <a href={`mailto:${institute.contactEmail}`}>
                    {institute.contactEmail}
                  </a>
                </div>
                <div className="contact-item">
                  <strong>ğŸ“ Phone:</strong>
                  <a href={`tel:${institute.contactPhone}`}>
                    {institute.contactPhone}
                  </a>
                </div>
                <div className="contact-item">
                  <strong>ğŸ“ Address:</strong>
                  <span>{institute.address}</span>
                </div>
                {institute.website && (
                  <div className="contact-item">
                    <strong>ğŸŒ Website:</strong>
                    <a href={institute.website} target="_blank" rel="noopener noreferrer">
                      {institute.website}
                    </a>
                  </div>
                )}
              </div>

              <div className="enquiry-section">
                <h3>Send Quick Enquiry</h3>
                <p>Have questions? Send us a message and we'll get back to you soon.</p>
                <button 
                  onClick={() => setShowEnquiryForm(true)}
                  className="btn-primary"
                >
                  Send Enquiry
                </button>
              </div>
            </div>
          </div>
        )}
      </div>

      {/* Review Form Modal */}
      {showReviewForm && (
        <ReviewForm
          institute={institute}
          onSubmit={handleReviewSubmit}
          onClose={() => setShowReviewForm(false)}
        />
      )}

      {/* Enquiry Form Modal */}
      {showEnquiryForm && (
        <EnquiryForm
          institute={institute}
          onSubmit={handleEnquirySubmit}
          onClose={() => setShowEnquiryForm(false)}
        />
      )}
    </div>
  );
};

export default InstituteDetail;
