import React, { useState, useEffect } from 'react';
import { instituteService } from '../../services/api';
import './InstituteDashboard.css';

const CourseManagement = () => {
  const [courses, setCourses] = useState([]);
  const [showForm, setShowForm] = useState(false);
  const [editingCourse, setEditingCourse] = useState(null);
  const [formData, setFormData] = useState({
    title: '',
    description: '',
    duration: '',
    fees: '',
    images: []
  });
  const [loading, setLoading] = useState(false);
  const [message, setMessage] = useState('');

  useEffect(() => {
    fetchCourses();
  }, []);

  const fetchCourses = async () => {
    try {
      const response = await instituteService.getCurrentInstitute();
      setCourses(response.data.courses || []);
    } catch (error) {
      console.error('Error fetching courses:', error);
    }
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleImageUpload = (e) => {
    const files = Array.from(e.target.files);
    setFormData(prev => ({
      ...prev,
      images: files
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setMessage('');

    try {
      const submitData = new FormData();
      Object.keys(formData).forEach(key => {
        if (key === 'images') {
          formData.images.forEach(file => {
            submitData.append('courseImages', file);
          });
        } else {
          submitData.append(key, formData[key]);
        }
      });

      if (editingCourse) {
        await instituteService.updateCourse(editingCourse._id, submitData);
        setMessage('Course updated successfully!');
      } else {
        await instituteService.addCourse(submitData);
        setMessage('Course added successfully!');
      }

      resetForm();
      fetchCourses();
    } catch (error) {
      setMessage('Error saving course. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  const resetForm = () => {
    setFormData({
      title: '',
      description: '',
      duration: '',
      fees: '',
      images: []
    });
    setEditingCourse(null);
    setShowForm(false);
  };

  const editCourse = (course) => {
    setFormData({
      title: course.title,
      description: course.description,
      duration: course.duration,
      fees: course.fees,
      images: []
    });
    setEditingCourse(course);
    setShowForm(true);
  };

  const deleteCourse = async (courseId) => {
    if (window.confirm('Are you sure you want to delete this course?')) {
      try {
        await instituteService.deleteCourse(courseId);
        setMessage('Course deleted successfully!');
        fetchCourses();
      } catch (error) {
        setMessage('Error deleting course. Please try again.');
      }
    }
  };

  return (
    <div className="course-management">
      <div className="dashboard-section">
        <div className="section-header">
          <h2>Course Management</h2>
          <button 
            className="btn-primary"
            onClick={() => setShowForm(true)}
          >
            Add New Course
          </button>
        </div>

        {message && (
          <div className={`message ${message.includes('Error') ? 'error' : 'success'}`}>
            {message}
          </div>
        )}

        {showForm && (
          <div className="form-modal">
            <div className="modal-content">
              <div className="modal-header">
                <h3>{editingCourse ? 'Edit Course' : 'Add New Course'}</h3>
                <button onClick={resetForm} className="close-btn">√ó</button>
              </div>
              
              <form onSubmit={handleSubmit} className="course-form">
                <div className="form-group">
                  <label>Course Title *</label>
                  <input
                    type="text"
                    name="title"
                    value={formData.title}
                    onChange={handleChange}
                    required
                  />
                </div>

                <div className="form-group">
                  <label>Description *</label>
                  <textarea
                    name="description"
                    value={formData.description}
                    onChange={handleChange}
                    rows="4"
                    required
                  />
                </div>

                <div className="form-row">
                  <div className="form-group">
                    <label>Duration *</label>
                    <input
                      type="text"
                      name="duration"
                      value={formData.duration}
                      onChange={handleChange}
                      placeholder="e.g., 2 years, 6 months"
                      required
                    />
                  </div>

                  <div className="form-group">
                    <label>Fees (‚Çπ) *</label>
                    <input
                      type="number"
                      name="fees"
                      value={formData.fees}
                      onChange={handleChange}
                      required
                    />
                  </div>
                </div>

                <div className="form-group">
                  <label>Course Images</label>
                  <input
                    type="file"
                    multiple
                    accept="image/*"
                    onChange={handleImageUpload}
                  />
                  <small>You can upload multiple images</small>
                </div>

                <div className="form-actions">
                  <button 
                    type="button" 
                    onClick={resetForm}
                    className="btn-secondary"
                  >
                    Cancel
                  </button>
                  <button 
                    type="submit" 
                    disabled={loading}
                    className="btn-primary"
                  >
                    {loading ? 'Saving...' : editingCourse ? 'Update Course' : 'Add Course'}
                  </button>
                </div>
              </form>
            </div>
          </div>
        )}

        <div className="courses-grid">
          {courses.length === 0 ? (
            <div className="no-data">
              <p>No courses added yet.</p>
              <button 
                className="btn-primary"
                onClick={() => setShowForm(true)}
              >
                Add Your First Course
              </button>
            </div>
          ) : (
            courses.map(course => (
              <div key={course._id} className="course-card">
                <div className="course-images">
                  {course.images && course.images.length > 0 ? (
                    <img 
                      src={`http://localhost:5000/${course.images[0]}`} 
                      alt={course.title}
                    />
                  ) : (
                    <div className="no-image">No Image</div>
                  )}
                </div>
                
                <div className="course-info">
                  <h3>{course.title}</h3>
                  <p className="course-description">{course.description}</p>
                  
                  <div className="course-details">
                    <span className="duration">‚è±Ô∏è {course.duration}</span>
                    <span className="fees">üí∞ ‚Çπ{course.fees?.toLocaleString()}</span>
                  </div>
                  
                  <div className="course-actions">
                    <button 
                      onClick={() => editCourse(course)}
                      className="btn-edit"
                    >
                      Edit
                    </button>
                    <button 
                      onClick={() => deleteCourse(course._id)}
                      className="btn-delete"
                    >
                      Delete
                    </button>
                  </div>
                </div>
              </div>
            ))
          )}
        </div>
      </div>
    </div>
  );
};

export default CourseManagement;
