import React, { useState } from 'react';
import { useAuth } from '../../context/AuthContext';
import './UserPanel.css';

const ReviewForm = ({ institute, onSubmit, onClose }) => {
  const { user } = useAuth();
  const [formData, setFormData] = useState({
    rating: 0,
    reviewText: '',
    anonymous: false
  });
  const [hoverRating, setHoverRating] = useState(0);
  const [submitting, setSubmitting] = useState(false);

  const handleSubmit = async (e) => {
    e.preventDefault();
    
    if (formData.rating === 0) {
      alert('Please select a rating');
      return;
    }

    if (!formData.reviewText.trim()) {
      alert('Please write your review');
      return;
    }

    setSubmitting(true);
    await onSubmit(formData);
    setSubmitting(false);
  };

  const handleRatingClick = (rating) => {
    setFormData(prev => ({ ...prev, rating }));
  };

  const handleRatingHover = (rating) => {
    setHoverRating(rating);
  };

  const handleRatingLeave = () => {
    setHoverRating(0);
  };

  return (
    <div className="modal-overlay">
      <div className="modal-content review-modal">
        <div className="modal-header">
          <h3>Write a Review</h3>
          <button onClick={onClose} className="btn btn-close">
            <i className="fas fa-times"></i>
          </button>
        </div>

        <div className="modal-body">
          <div className="review-institute">
            <div className="institute-logo-sm">
              {institute.logo ? (
                <img src={institute.logo} alt={institute.name} />
              ) : (
                <div className="logo-placeholder-sm">
                  {institute.name?.charAt(0)}
                </div>
              )}
            </div>
            <div>
              <h4>{institute.name}</h4>
              <p>{institute.affiliation}</p>
            </div>
          </div>

          <form onSubmit={handleSubmit} className="review-form">
            <div className="form-group">
              <label className="form-label">Your Rating</label>
              <div className="rating-selector">
                {[1, 2, 3, 4, 5].map(star => (
                  <button
                    key={star}
                    type="button"
                    className={`star-btn ${star <= (hoverRating || formData.rating) ? 'active' : ''}`}
                    onClick={() => handleRatingClick(star)}
                    onMouseEnter={() => handleRatingHover(star)}
                    onMouseLeave={handleRatingLeave}
                  >
                    â˜…
                  </button>
                ))}
              </div>
              <div className="rating-labels">
                <span>Poor</span>
                <span>Excellent</span>
              </div>
            </div>

            <div className="form-group">
              <label className="form-label">Your Review</label>
              <textarea
                value={formData.reviewText}
                onChange={(e) => setFormData(prev => ({ ...prev, reviewText: e.target.value }))}
                className="form-control"
                rows="6"
                placeholder="Share your experience with this institute. What did you like? What could be improved?"
                required
              />
            </div>

            <div className="form-group">
              <label className="checkbox-label">
                <input
                  type="checkbox"
                  checked={formData.anonymous}
                  onChange={(e) => setFormData(prev => ({ ...prev, anonymous: e.target.checked }))}
                />
                <span className="checkmark"></span>
                Post anonymously
              </label>
            </div>

            <div className="form-actions">
              <button 
                type="button" 
                onClick={onClose}
                className="btn btn-outline"
              >
                Cancel
              </button>
              <button 
                type="submit" 
                disabled={submitting}
                className="btn btn-primary"
              >
                {submitting ? 'Submitting...' : 'Submit Review'}
              </button>
            </div>
          </form>
        </div>
      </div>
    </div>
  );
};

export default ReviewForm;
